			OrpheusMiscMeeting01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "OrpheusFirstMeeting", "OrpheusFirstMeeting_Alt" },
				RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain02", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				{ Cue = "/VO/Orpheus_0066",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Oh, hello Zagreus. I was just wondering if you had ever heard of any other court musicians who had lost the will to sing? Your father's asked me to explain myself and honestly it has been rather difficult." },
				{  Cue = "/VO/ZagreusHome_0414", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "If it's any consolation, mate, I'm certain every single undead court musician serving in the throne room of the god of the dead has been through everything you're going through." },
