				{  Cue = "/VO/ZagreusHome_3547", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Look, if you decide to start strumming on that lyre again, I'm sure nobody here is going to mind. Though you take care, OK?" },
