						{
							ChoiceText = "OrpheusJukebox_00",
							{  Cue = "/VO/ZagreusHome_3548", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
								PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
								PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
								Text = "How about {#DialogueItalicFormat}The Lament{#PreviousFormat}? You used to play that one a lot!" },
							{ Cue = "/VO/Orpheus_0069",
								Text = "Oh, fancy that one do you? Alas, I've not the heart to sing it anymore. I'm glad you like it, though." },
						},
						{
							ChoiceText = "OrpheusJukebox_01",
							{  Cue = "/VO/ZagreusHome_3549", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
								PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
								PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
								Text = "How about {#DialogueItalicFormat}In the Blood{#PreviousFormat}? That's one of your old hits, right?" },
							{ Cue = "/VO/Orpheus_0069",
								Text = "Oh, fancy that one do you? Alas, I've not the heart to sing it anymore. I'm glad you like it, though." },
						},
						{
							ChoiceText = "OrpheusJukebox_02",
							{  Cue = "/VO/ZagreusHome_3550", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
								PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
								PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
								Text = "How about {#DialogueItalicFormat}Argonauts{#PreviousFormat}, didn't you sail with them?" },
