				},
			},
			OrpheusMiscMeeting04 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusMiscMeeting02", "OrpheusMiscMeeting03" },
				RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain02", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B", "OrpheusGift05", "OrpheusAboutEurydice04" },
				{  Cue = "/VO/ZagreusHome_0203", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Orpheus, forgive my bluntness here, but I have to ask. Why won't you sing? You were a legend, mate! Our court musician!" },
				{ Cue = "/VO/Orpheus_0070",
					Text = "Why, it's that I have lost my muse, my friend. It isn't harder to explain than that. Have you not had such moments in your life, where you had lost the will to chase your passions?" },
				{  Cue = "/VO/ZagreusHome_0204", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I, I suppose I have, yeah. But recently I found my drive again, and that's why I have to get out of here." },
				{ Cue = "/VO/Orpheus_0071",
					PreLineAnim = "OrpheusFidget",
					Text = "And I am truly happy for you, friend. Alas, I've had no luck in my own search, not that I'm searching any longer, so, I'll just be over here, OK?" },
			},
			OrpheusMiscMeeting05 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusMiscMeeting04" },
				RequiredFalseTextLines = { "EurydiceMentionsOrpheus01", "EurydiceFirstMeeting01_C", "EurydiceAboutOrpheus01" },
				{  Cue = "/VO/ZagreusHome_0205", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Anything I can do to help you find your muse, mate?" },
				{ Cue = "/VO/Orpheus_0072",
					Text = "I fear my muse is long since lost to me, my friend, though truly thanks for asking. I suppose she must be out there somewhere still, since I once traveled 'cross this land in search of her, and found her then. But that was quite a while back." },
				{  Cue = "/VO/ZagreusHome_0206", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenial_Full", PreLineAnimTarget = "Hero",
					Text = "Wait, you found her? What happened?" },
				{ Cue = "/VO/Orpheus_0073",
					Text = "Well, this was back when I was still alive, you understand. Your father, why, he doesn't care for anyone to leave this realm, and, basically now I'm stuck here as well! Not seen her since, and, I'm not much for travel anymore." },
				{  Cue = "/VO/ZagreusHome_1859", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "That's... I'm sorry to hear that. Thank you for letting me know. I hope you get to see her again someday." },
				{ Cue = "/VO/Orpheus_0074",
					PreLineAnim = "OrpheusFidget",
					Text = "I appreciate the sentiment, my friend, though please don't worry about her or me. I mean, we're dead! We're fine." },
			},
			-- not learned of Eurydice's connection to him from her
			OrpheusMiscMeeting06 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "OrpheusMiscMeeting05", "OrpheusAboutEurydice01", "EurydiceFirstMeeting01_C" },
				RequiredFalseTextLines = { "OrpheusMiscMeeting06_B", "EurydiceFirstMeeting01_A", "EurydiceFirstMeeting01_B" },
				{  Cue = "/VO/ZagreusExtra_0011", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Mate, if you don't mind, I'd love it if you told me more about your muse." },
				{ Cue = "/VO/Orpheus_0233",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "My muse... never has somebody been as splendorous as my Eurydice...! She was so gentle, yet so stout of heart that it would make yours ache, just thinking on it. She inspired innumerable songs of mine." },
				{  Cue = "/VO/ZagreusHome_0209", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					Text = "That's quite a few. She sounds like she was a real catch all right. I'm sorry for your loss. Thank you for sharing your memory of her with me." },
				{ Cue = "/VO/Orpheus_0076",
					PreLineAnim = "OrpheusFidget",
					Text = "Thank you for listening, my friend. Eurydice, she likewise used to listen to me all the time...." },
			},
			-- learned of Eurydice's connection to him from her
			OrpheusMiscMeeting06_B =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusMiscMeeting05" },
				RequiredAnyTextLines = { "EurydiceFirstMeeting01_A", "EurydiceFirstMeeting01_B", },
				RequiredFalseTextLines = { "EurydiceFirstMeeting01_C", "OrpheusMiscMeeting06", "EurydiceMentionsOrpheus01" },
				{  Cue = "/VO/ZagreusExtra_0011", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Mate, if you don't mind, I'd love it if you told me more about your muse." },
				{ Cue = "/VO/Orpheus_0233",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "My muse... never has somebody been as splendorous as my Eurydice...! She was so gentle, yet so stout of heart that it would make yours ache, just thinking on it. She inspired innumerable songs of mine." },
				{  Cue = "/VO/ZagreusHome_1511", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteSurprise",
					Text = "Eurydice...? I know a... anyway, she must have been a real catch! I'm sorry for your loss. I have to go look into something here." },
				{ Cue = "/VO/Orpheus_0076",
					PreLineAnim = "OrpheusFidget",
					Text = "Thank you for listening, my friend. Eurydice, she likewise used to listen to me all the time...." },
			},
			OrpheusMiscMeeting07 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusMiscMeeting05" },
				RequiredAnyRoomsThisRun = { "B_Wrapping01", "B_MiniBoss01", "B_Boss01", "B_Boss02" },
				RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain02", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B", "OrpheusGift05", "OrpheusAboutEurydice04" },
				-- Of course!
				EndCue = "/VO/ZagreusHome_3458",
				EndWait = 0.5,
				{ Cue = "/VO/Orpheus_0077",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "I understand you've journeyed well beyond the pits of Tartarus, my friend? Another most heroic feat of yours, and truth be told I find it quite inspiring." },
				{  Cue = "/VO/ZagreusHome_0210", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Inspiring enough to... make you want to sing, again?" },
				{ Cue = "/VO/Orpheus_0078",
					Text = "Oh, that's a rather bold insinuation, Zagreus. I don't know that I've the will for that, I... just, I need some time to think, is that all right?" },
			},

			OrpheusAboutHades01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusWithHades02" },
				RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain02", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B", "OrpheusGift05", "OrpheusAboutEurydice04" },
				-- I understand. I'm sorry.
				EndCue = "/VO/ZagreusHome_1240",
				EndWait = 0.4,
				{  Cue = "/VO/ZagreusHome_1238", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It sounds like Father's very cross with you, Orpheus. Won't you consider singing once again? You know his threat of further punishment is not a bluff." },
				{ Cue = "/VO/Orpheus_0184",
					PreLineAnim = "OrpheusFidget",
					Text = "Your father can be.... most persuasive, yes, my friend. However, this time, I am... putting my foot down! I have no plans to sing for him again." },
				{  Cue = "/VO/ZagreusHome_1239", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Well then how about for me? Or for your other friends among the court?" },
				{ Cue = "/VO/Orpheus_0185",
					Emote = "PortraitEmoteSurprise",
					Text = "I... no. Please. My muse is gone to me. I can do no such thing." },
			},

			OrpheusAboutPressure01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusGift03" },
				RequiredAnyTextLines = { "OrpheusAboutEurydice01", "OrpheusAboutEurydice01_B", "OrpheusMiscMeeting06", "OrpheusMiscMeeting06_B" },
				RequiresRunNotCleared = true,
				ConsecutiveDeathsInRoom =
				{
				  Name = "D_Boss01",
				  Count = 2,
				},
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- Appreciate it, mate.
					{ Cue = "/VO/ZagreusHome_3121" },
				},
				{ Cue = "/VO/Orpheus_0266",
					Text = "You seem quite agitated, as of late, my friend. Is anything amiss with you, perhaps?" },
				{  Cue = "/VO/ZagreusHome_3120", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}! No, I'm all right, Orpheus. Just a lot on my mind is all, sorting out what's next. Stuff I need to work through on my own." },
				{ Cue = "/VO/Orpheus_0267",
					Text = "Ah, well, I truly understand. I was reluctant to discuss my long-lost muse, really for quite some time. Well, if you do require some emotional support, know that I likely shall be standing over here..." },
			},

			OrpheusAboutHermes01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "HermesAboutOrpheus01", "OrpheusGift02" },
				-- I'm glad you took it so well...
				EndCue = "/VO/ZagreusHome_2617",
				EndWait = 0.4,
				{  Cue = "/VO/ZagreusHome_2616", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Oh, Orpheus? I heard from Hermes, messenger and god of travel, and all that? It's odd {#DialogueItalicFormat}I'm {#PreviousFormat}giving you his message in this case, but he says hey! Admires your musical ability, as well." },
				{ Cue = "/VO/Orpheus_0229",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, that was kind of him, my friend! When I first ventured deep into this realm, whilst searching for the soul of my lost muse... after I failed utterly, it was Lord Hermes who whisked her away. It happened so fast. He did an {#DialogueItalicFormat}excellent {#PreviousFormat}job!" },
			},

			OrpheusAboutSinging01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				-- It's certainly paid off.
				EndCue = "/VO/ZagreusHome_1040",
				EndWait = 0.4,
				{  Cue = "/VO/ZagreusHome_1039", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I always meant to ask you, Orpheus. Where did you learn to sing like that? Was it really Apollo himself who taught you?" },
				{ Cue = "/VO/Orpheus_0212",
					PreLineAnim = "OrpheusFidget",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, why, I learned from many different sources, Zagreus. That is before all of my inspiration started coming from a single source. I credit my accomplishments to her, and to Apollo, and the gods. And to considerable time spent practicing!" },
			},

			OrpheusAboutNyx01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusAboutSinging01", "NyxGift01", "OrpheusGift01" },
				-- That she is.
				EndCue = "/VO/ZagreusHome_2626",
				EndWait = 0.4,
				{  Cue = "/VO/ZagreusHome_2625", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Orpheus? I know Father appreciates your songs, to the extent he's capable of appreciating anything. Though, has Nyx ever responded to them? She's always right nearby yet you two don't seem to talk..." },
				{ Cue = "/VO/Orpheus_0257",
					Text = "Almighty Nyx appears to tolerate my presence, Zagreus. For she could cast me into everlasting darkness if my song failed to be pleasing to her. She is a most-discerning listener!" },
			},

			OrpheusAboutMusicStand01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredCosmetics = { "Cosmetic_MusicPlayer" },
				-- Amazing. And a bit unsettling.
				EndCue = "/VO/ZagreusHome_1029",
				EndWait = 0.5,
				{ Cue = "/VO/Orpheus_0152",
					Text = "Are you the one whom I should thank for this most wondrous music stand, my friend? Why, it is positively splendid, isn't it?" },
				{  Cue = "/VO/ZagreusHome_1028", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Our renowned court musician ought to have the finest means available, I think. I'm pleased you like it, Orpheus. Though, tell me something, how can it play your music when you're not even here?" },
				{ Cue = "/VO/Orpheus_0153",
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteCheerful", WaitTime = 10.0 },
					Text = "Who can explain the secret art of Daedalus, my friend? Know only that, whenever it produces music for your listening enjoyment, that the sound you hear is coming from my soul!" },	
			},

			OrpheusPostEpilogue01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusGift01", "OlympianReunionQuestComplete" },
				RequiredFalseFlags = { "PersephoneAway" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					RequiredMinElapsedTime = 2.5,
					UsePlayerSource = true,
					-- Same goes for me, mate. Same goes for me.
					{ Cue = "/VO/ZagreusHome_3266" },
				},
				{ Cue = "/VO/Orpheus_0269",
					Text = "So, I am quite confused, I must admit, my friend. The Queen Persephone, she is your mother, then? How is it that she came to reside, here?" },
				{  Cue = "/VO/ZagreusHome_3263", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Well, {#DialogueItalicFormat}erm{#PreviousFormat}... it all started when my father saw her on Olympus, and... they fell madly in love. He swept her off her feet, and brought her here. The Olympians, they got pretty worried when they saw she was gone. But now it's OK! Everything's out in the open." },
				{ Cue = "/VO/Orpheus_0270",
					Text = "But, surely the Queen's mother on Olympus, the great goddess Demeter... surely she would quite like it if she saw her daughter still, from time to time?" },
				{  Cue = "/VO/ZagreusHome_3264", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Oh, that! Thing is, Mother ate some... pomegranate seeds when she was here. And we have rules about that stuff, you know! It means she cannot leave this place for very long at all. I'm sure Demeter will understand. And unfreeze everything when her daughter's around." },
				{ Cue = "/VO/Orpheus_0271",
					Text = "Ah, yes, pomegranate seeds, of course, of course! Though, there is one more subject I don't understand, which is... how {#DialogueItalicFormat}you {#PreviousFormat}fit into all of this. Were you not trying to escape from here?" },
				{  Cue = "/VO/ZagreusHome_3265", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Forget about me, Orpheus! This isn't about me, it's about Mother. What's important is she's here, where she belongs." },
				{ Cue = "/VO/Orpheus_0272",
					Text = "I see! I understand. Well, thank you, then, for clearing all that up. It is a splendid tale, I must say, and every part of it makes sense to me, at last." },
			},

			--[[
			singing prereqs: OrpheusMiscMeeting07, OrpheusGift05
			]]--
			OrpheusSingsAgain01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				InitialGiftableOffSource = true,
				RequiredTextLines = { "OrpheusMiscMeeting07", "OrpheusGift05" },
				RequiredFalseTextLines = { "EurydiceFirstMeeting01_A", "EurydiceFirstMeeting01_B", "EurydiceFirstMeeting01_C", "OrpheusSingsAgain01_B",  "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D" },
				RequiredFalseQueuedTextLines = { "OlympianReunionQuestComplete" },
				{ Cue = "/VO/Orpheus_0088",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "I've decided I should give this singing thing another shot for now, my friend. I hope you like it." },
				{ Cue = "/VO/ZagreusHome_0219", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "This is amazing, mate. So does this mean you found your muse?" },
				{ Cue = "/VO/Orpheus_0089",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Oh no, I never found my muse, my friend. It's just... seeing you carrying on like this time after time, I was reminded I ought to get on with my life, too, such as it is right now." },
				OnQueuedFunctionName = "MusicianMusic",
				OnQueuedFunctionArgs = PresetEventArgs.Singing_SongFromStart,
				RequiredAmbientTrackNameMatch = true,
			},
			-- alt if you met eurydice
			OrpheusSingsAgain01_B =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				InitialGiftableOffSource = true,
				RequiredTextLines = { "OrpheusMiscMeeting07", "OrpheusGift05" },
				RequiredAnyTextLines = { "EurydiceFirstMeeting01_A", "EurydiceFirstMeeting01_B", "EurydiceFirstMeeting01_C", },
				RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D" },
				RequiredFalseQueuedTextLines = { "OlympianReunionQuestComplete" },
				{ Cue = "/VO/Orpheus_0088",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "I've decided I should give this singing thing another shot for now, my friend. I hope you like it." },
				{ Cue = "/VO/ZagreusHome_1027", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "This is amazing, mate. You sing quite beautifully, with or without your muse." },
				{ Cue = "/VO/Orpheus_0230",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "How kind of you. It's just... seeing you carry on like this, time after time, I was reminded that I might as well get on with my existence, too. Such as it is right now." },
				OnQueuedFunctionName = "MusicianMusic",
				OnQueuedFunctionArgs = PresetEventArgs.Singing_SongFromStart,
				RequiredAmbientTrackNameMatch = true,
			},
			-- via eurydice; no 'good riddance' yet
			OrpheusSingsAgain01_C =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				InitialGiftableOffSource = true,
				RequiredTextLines = { "OrpheusAboutEurydice03" },
				RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_D", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				RequiredFalseQueuedTextLines = { "OlympianReunionQuestComplete" },
				{ Cue = "/VO/Orpheus_0088",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "I've decided I should give this singing thing another shot for now, my friend. I hope you like it." },
				{ Cue = "/VO/ZagreusHome_3472", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "It's beautiful, mate. If only Eurydice could hear you right now." },
				{ Cue = "/VO/Orpheus_0151",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "How kind of you, my friend. It's just... seeing you carry on like this, time after time, I was reminded I ought to get on with my existence, too. Such as it is right now." },
				OnQueuedFunctionName = "MusicianMusic",
				OnQueuedFunctionArgs = PresetEventArgs.Singing_SongFromStart,
				RequiredAmbientTrackNameMatch = true,
			},
			-- via eurydice; 'good riddance' played first
			OrpheusSingsAgain01_D =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				InitialGiftableOffSource = true,
				RequiredTextLines = { "OrpheusAboutEurydice03" },
				RequiredAnyTextLines = { "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C" },
				RequiredFalseQueuedTextLines = { "OlympianReunionQuestComplete" },
				{ Cue = "/VO/Orpheus_0231",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "I became so enthralled with song once more, my friend, that I have been inspired to compose another one. For old time's sake, perhaps." },
				{ Cue = "/VO/ZagreusHome_1027", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "This is amazing, mate. You sing quite beautifully, with or without your muse." },
				{ Cue = "/VO/Orpheus_0232",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "How kind of you, although... my voice is nothing but the crunch of gravel underfoot compared to hers, which soars as though on wings." },
				OnQueuedFunctionName = "MusicianMusic",
				OnQueuedFunctionArgs = PresetEventArgs.Singing_SongFromStart,
				RequiredAmbientTrackNameMatch = true,
			},

			OrpheusTallTale01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusGift01" },
				RequiredAnyTextLines = { "OrpheusMiscMeeting07", "OrpheusAboutEurydice01", "OrpheusAboutEurydice01_B" },
				RequiredAnyRoomsLastRun = { "B_Boss01", "B_Boss02" },
				-- Poor Orpheus.
				-- EndCue = "/VO/ZagreusHome_0554",
				-- EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_0211", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Say, Orpheus? I ever tell you I once single-handedly slew a multi-headed bone hydra within the magma fields of Asphodel without so much as breaking a sweat?" },
				{ Cue = "/VO/Orpheus_0079",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "OrpheusFidget",
					Text = "My, that is wonderful to hear, my friend! Another in a litany of such heroic deeds, more noble than the heroes of our verse, and even great Achilles over there!" },
				{  Cue = "/VO/ZagreusHome_1619", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusExpressiveEmpathy", PreLineAnimTarget = "Hero",
					Text = "I'm glad you think so, mate! These tales of my valorous accomplishments do seem to cheer you up, so, I will keep reporting back once I rack up some more." },
			},
			OrpheusTallTale02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusTallTale01" },
				-- Would that there were, mate.
				EndCue = "/VO/ZagreusHome_0723",
				EndWait = 0.45,
				{  Cue = "/VO/ZagreusHome_0213", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey Orpheus? You know about the time I vanquished seven legions of Bloodless warriors with both hands tied behind my back while hopping on one foot? Lord Poseidon dared me to do it, you know." },
				{ Cue = "/VO/Orpheus_0080",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "OrpheusFidget",
					Text = "Why, that is a spectacularly vivid tale, Zagreus, and most deserving to be chronicled in song! Would that there were a poet here still willing to commit such deeds to verse." },
			},
			OrpheusTallTale03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusTallTale02" },
				RequiredAnyTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D" },
				RequiredAnyOtherTextLines = { "HadesPostFlashback01", "Ending01" },
				-- Great!
				EndCue = "/VO/ZagreusHome_0724",
				EndWait = 0.5,
				{  Cue = "/VO/ZagreusHome_0214", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey, Orpheus? I take it you know all about the time I burned my father's hands to a crisp the moment I was born? He grasped me by the feet, and they caught fire! And that's why he still wears those fancy rings: to cover up the hideous scar tissue." },
				{ Cue = "/VO/Orpheus_0081",
					Text = "But that is such a splendid tale, Zagreus, deserving to be sung both far and wide, until it turns into the stuff of myth, as deathless as the gods themselves! I'll gladly tell that one about!" },
			},
			OrpheusTallTale04 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusTallTale03", "DionysusAboutOrpheus01" },
				-- Dionysus and I appreciate it, mate.
				EndCue = "/VO/ZagreusHome_0725",
				EndWait = 0.5,
				{  Cue = "/VO/ZagreusHome_0215", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey Orpheus, you've noticed the resemblance between me and Dionysus, haven't you, mate? I'll tell you a little secret but you have to promise not to tell... We're really the same god. I love a good feast, don't I?" },
				{ Cue = "/VO/Orpheus_0082",
					Emote = "PortraitEmoteSurprise",
					Text = "But, Zagreus, that is a striking revelation, the connection you describe. I see it! I can see it now! To think the god of wine and you are intricately linked, it isn't obvious, is it?" },
				{  Cue = "/VO/ZagreusHome_0216", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Oh, there's more to it! A bit grim, really, as I'm a little older than I look. You see, the Titans, they once tore me limb from limb, but then my heart was saved, and that's where Dionysus comes from! That is the connection between us, you understand me, mate?" },
				{ Cue = "/VO/Orpheus_0083",
					PreLineAnim = "OrpheusFidget",
					Text = "Your tale stirs my soul, it truly does, my friend. It is a marvel to me that such heroism isn't widely known, and I'm committed to the spread of all such truths." },
			},
			OrpheusTallTale05 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusTallTale04" },
				-- I was kidding. Seriously.
				EndCue = "/VO/ZagreusHome_0726",
				EndWait = 0.5,
				{  Cue = "/VO/ZagreusHome_0217", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Hey Orpheus, you know I've just been messing with you, with those Dionysus tales, and all that? Just trying to liven things up for us, mate." },
				{ Cue = "/VO/Orpheus_0084",
					PreLineAnim = "ZagreusTalkEmpathy_Return", PreLineAnimTarget = "Hero",
					Text = "Oh, Zagreus! There isn't need for such humility, not when one's deeds of heroism are as great and numerous as yours! It is an honor to have heard such deeds first-hand!" },
			},
			OrpheusTallTale06 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusTallTale03" },
				-- What have I done.
				EndCue = "/VO/ZagreusHome_0727",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_0218", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "You know, mate, sometimes I worry that you're taking my tall tales rather seriously. You're not really going about and telling everyone this stuff, are you?" },
				{ Cue = "/VO/Orpheus_0085",
					PreLineAnim = "ZagreusTalkEmpathy_Return", PreLineAnimTarget = "Hero",
					Text = "Come now, my friend! I know this is a test of faith! Of course I am committed to the spread of such heroic deeds as yours. And I do so enjoy getting to hear them first!" },
			},
			OrpheusTallTale07 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusTallTale03" },
				RequiredRoomThisRun = "D_Intro",
				-- ...Did he make fun of me just now?
				EndCue = "/VO/ZagreusHome_1051",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_1050", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Orpheus? I figure that you've heard by now about how I bested the Minotaur Asterius, alongside Theseus, the Champion of Elysium, himself? I don't think I even broke a sweat." },
				{ Cue = "/VO/Orpheus_0223",
					PreLineAnim = "OrpheusFidget",
					Text = "I scarce can possibly imagine such a spectacle, my friend! But certainly I shall attempt to try!" },
			},
			OrpheusTallTale08 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusTallTale03" },
				RequiresRunCleared = true,
				RequiredMinRunsCleared = 2,
				-- ...Fine, all right.
				EndCue = "/VO/ZagreusHome_1053",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_1052", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Orpheus, you have to hear this straight from me. I vanquished my own father, on the outskirts of the Temple of Styx. At the surface! I know how this looks, since I'm back here, and so is he. You believe me, don't you?" },
				{ Cue = "/VO/Orpheus_0224",
					Text = "Of {#DialogueItalicFormat}course {#PreviousFormat}I do, my friend! Though, you must know, your father has requested his relationship to you be kept quite clandestine. You'll understand if I not sing your praises in this case, won't you?" },
			},
			OrpheusSingsAgain02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "OrpheusTallTale05", "OrpheusTallTale06" },
				RequiredFalseQueuedTextLines = { "OlympianReunionQuestComplete" },
				-- Um. Cheers.
				EndCue = "/VO/ZagreusHome_0732",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_0730", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "Orpheus, sorry to interrupt, it's just... I couldn't help but notice that new ballad that you're playing, there." },
				{ Cue = "/VO/Orpheus_0099",
					Text = "Technically, one ought consider it a hymn, my friend! In honor of your numerous travails, prior to your current set of circumstances, which must pale in comparison to all the rigors you've endured to date!" },
				{  Cue = "/VO/ZagreusHome_0731", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Erm{#PreviousFormat}, the thing is... none of the information in your hymn is accurate. It's nonsense. I was just kidding with you, mate! I tried to make that clear. You get so lively when I tell you my tall tales, you know? You're not angry with me, are you...?" },
				{ Cue = "/VO/Orpheus_0100",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "OrpheusFidget",
					Text = "Heavens, no, my friend! Your humility is matched only by your perseverance in the face of adversity! May your great deeds live on forever, much like you!" },
				OnQueuedFunctionName = "MusicianMusic",
				OnQueuedFunctionArgs = PresetEventArgs.SingingHymn_SongFromStart,
				RequiredAmbientTrackNameMatch = true,
			},

			-- if you haven't learned of Eurydice from Orpheus
			OrpheusAboutEurydice01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "EurydiceMentionsOrpheus01" },
				RequiredAnyTextLines = { "OrpheusFirstMeeting", "OrpheusFirstMeeting_Alt" },
				RequiredFalseTextLines = { "OrpheusAboutEurydice01_B", "OrpheusMiscMeeting06", "OrpheusMiscMeeting06_B" },
				-- You can't be serious.
				EndCue = "/VO/ZagreusHome_1001",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_0998", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Say, Orpheus. You wouldn't happen to know the nymph Eurydice, would you? Tall, woodsy type? I get the impression maybe you know each other." },
				{ Cue = "/VO/Orpheus_0124",
					Emote = "PortraitEmoteSurprise",
					Text = "{#DialogueItalicFormat}What{#PreviousFormat}? Why... yes. I know Eurydice. Or, I once knew her, I should say. Whyever would you ask me such a thing, my friend?" },
				{  Cue = "/VO/ZagreusHome_0999", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I saw her, mate. In Asphodel. She's doing fine up there! All on her own. The two of you, you used to know each other, then?" },
				{ Cue = "/VO/Orpheus_0125",
					Text = "We more than knew each other, Zagreus. We were husband and wife. She was my muse. To think that we are doomed to be apart during our afterlives. Ah, well!" },
				{  Cue = "/VO/ZagreusHome_1000", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Ah, well{#PreviousFormat}? Don't you have a message I could bring to her, if we should meet again? Something like that?" },
				{ Cue = "/VO/Orpheus_0126",
					PreLineAnim = "OrpheusFidget",
					Text = "I do not wish to trouble her again, my friend. Speak not to her of me, all right? I have my memory of her; it is enough." },
			},
			-- if you have learned of Eurydice from Orpheus
			OrpheusAboutEurydice01_B =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = {  "EurydiceAboutOrpheus01", "EurydiceAboutOrpheus01_B", "EurydiceFirstMeeting01_C" },
				RequiredAnyOtherTextLines = { "OrpheusFirstMeeting", "OrpheusFirstMeeting_Alt" },
				RequiredFalseTextLines = { "OrpheusAboutEurydice01", },
				-- You can't be serious.
				EndCue = "/VO/ZagreusHome_1001",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_1002", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Say, Orpheus. You're not going to believe this. I happened on a certain nymph during my journeys recently. It was Eurydice. Tall, woodsy type?" },
				{ Cue = "/VO/Orpheus_0127",
					Text = "{#DialogueItalicFormat}What{#PreviousFormat}? Why... truly. I knew Eurydice resided in your father's realm, but... I had not anticipated you would cross her path. You're certain of this, then?" },
				{  Cue = "/VO/ZagreusHome_1003", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I saw her, mate. In Asphodel. She's doing fine up there! All on her own. You said the two of you were very close, isn't that right?" },
				{ Cue = "/VO/Orpheus_0128",
					Text = "We were much more than that, Zagreus. We were husband and wife. She was my muse. To think that we are doomed to be apart during our afterlives. Ah, well!" },
				{  Cue = "/VO/ZagreusHome_1000", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Ah, well{#PreviousFormat}? Don't you have a message I could bring to her, if we should meet again? Something like that?" },
				{ Cue = "/VO/Orpheus_0126",
					PreLineAnim = "OrpheusFidget",
					Text = "I do not wish to trouble her again, my friend. Speak not to her of me, all right? I have my memory of her; it is enough." },
			},

			-- if you don't know their backstory
			OrpheusAboutEurydice02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "EurydiceAboutOrpheus02" },
				RequiredFalseTextLines = { "OrpheusAboutEurydice02_B", "OrpheusGift05" },
				-- I'll have to get to the bottom of this later.
				EndCue = "/VO/ZagreusHome_1006",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_1004", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Orpheus, I saw Eurydice again. I... couldn't bring myself to do exactly as you asked, I... sort of mentioned you." },
				{ Cue = "/VO/Orpheus_0129",
					Emote = "PortraitEmoteSurprise",
					Text = "You {#DialogueItalicFormat}what{#PreviousFormat}?! Or, what I meant to say was: Oh. Did you perchance get the impression she yet harbors toward me feelings of resentment, and the like?" },
				{  Cue = "/VO/ZagreusHome_1005", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You seemed to be a bit of a sore subject, yes. What happened to the two of you, if I may ask?" },
				{ Cue = "/VO/Orpheus_0130",
					Text = "Oh, it's just... I almost saved her soul from this domain, back when I lived and breathed. But in the final moments of my rescue, my faith faltered. She is lost to me forever, now. Though, I'm pleased to hear she isn't lost to you!" },
			},
			-- if you do know their backstory
			OrpheusAboutEurydice02_B =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "EurydiceAboutOrpheus02", "OrpheusGift05" },
				RequiredFalseTextLines = { "OrpheusAboutEurydice02" },
				-- Take it easy, mate.
				EndCue = "/VO/ZagreusHome_1008",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_1004", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Orpheus, I saw Eurydice again. I... couldn't bring myself to do exactly as you asked, I... sort of mentioned you." },
				{ Cue = "/VO/Orpheus_0129",
					Emote = "PortraitEmoteSurprise",
					Text = "You {#DialogueItalicFormat}what{#PreviousFormat}?! Or, what I meant to say was: Oh. Did you perchance get the impression she yet harbors toward me feelings of resentment, and the like?" },
				{  Cue = "/VO/ZagreusHome_1007", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You seemed to be a bit of a sore subject, yes. You really almost freed her from the Underworld, huh?" },
				{ Cue = "/VO/Orpheus_0131",
					Text = "Almost, my friend, almost. But in the final moments of my rescue, my faith faltered. She is lost to me forever, now. Though, I'm pleased to hear she isn't lost to you!" },
			},

			OrpheusAboutEurydice03 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "EurydiceAboutOrpheus03", "OrpheusAboutEurydice06" },
				-- You're not making this easy, Orpheus.
				EndCue = "/VO/ZagreusHome_1011",
				EndWait = 0.6,
				{ Cue = "/VO/Orpheus_0132",
					Text = "Pray tell me something, Zagreus. How is she lately, hm? Eurydice, I mean. Her hair, fall colors, or full bloom? Her poise, as proud and supple as the oak?" },
				{  Cue = "/VO/ZagreusHome_1009", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "She's all right, Orpheus. Seems comfortable within her little place in Asphodel. The only thing that seems to bother her at all is... when I mention you. Is there nothing you can do to patch things up?" },
				{ Cue = "/VO/Orpheus_0133",
					Text = "Alas, I cannot change the past, my friend. So, no. As you can plainly see by now, I'm dead to her. Rightfully so, at that." },
				{  Cue = "/VO/ZagreusHome_1010", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "But that's just a technicality! I mean, you're here, she's there! So what if you're not breathing anymore, you have all eternity ahead of you. Don't you want to see her again?" },
				{ Cue = "/VO/Orpheus_0134",
					Text = "Don't I want to see my Eurydice again...? Why, yes. That, more than anything, my friend. Provided she wanted to see me. I tried once to disturb her everlasting rest, as you well know. And that did not pan out as I had hoped...." },
			},

			OrpheusAboutEurydice04 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "EurydiceAboutOrpheus04" },
				RequiredFalseTextLines = { "OrpheusAboutSingersReunionQuest01" },
				-- I'm working on it, mate.
				EndCue = "/VO/ZagreusHome_1013",
				EndWait = 0.45,
				{ Cue = "/VO/Orpheus_0135",
					Text = "What news of late about Eurydice, my friend? She is as delicate as I described, no doubt? {#DialogueItalicFormat}Ah{#PreviousFormat}, but... I am dwelling on her once again, it seems." },
				{  Cue = "/VO/ZagreusHome_1012", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I knew she was a talented musician, mate, but you never let on that she is an amazing chef, as well. She's been hospitable to me each time we've met. Though you still seem to be a bit of a sore subject." },
				{ Cue = "/VO/Orpheus_0136",
					Text = "How very understandable, indeed. Were I to have been failed to be rescued from the depths of hell, mere moments from escape, I too would be quite cross with my attempted rescuer. And for quite some time." },
			},

			OrpheusAboutEurydice05 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "OrpheusAboutEurydice02", "OrpheusAboutEurydice02_B" },
				RequiredRoomThisRun = "B_Story01",
				-- The Fates can't be that cruel...
				EndCue = "/VO/ZagreusHome_1015",
				EndWait = 0.45,
				{  Cue = "/VO/ZagreusHome_1014", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Orpheus, I don't suppose you know how come I keep running into Eurydice in particular out there? For all the shades resting eternally in Asphodel, it seems beyond coincidence I always find the one that's dear to you." },
				{ Cue = "/VO/Orpheus_0137",
					Text = "I have no knowledge of the weavings of the Fates, my friend. Perhaps they trample on what used to be my heart for leisure or for sport, do you suppose?" },
			},

			OrpheusAboutEurydice06 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "EurydiceAboutOrpheus03" },
				EndVoiceLines =
				{
					PreLineWait = 0.55,
					-- I know no other way, my friend.
					{ Cue = "/VO/Orpheus_0211" },
				},
				{  Cue = "/VO/ZagreusHome_1036", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Say, Orpheus, Eurydice told me your songs, some of them came from her? She seems quite brilliant. You choose your muses well!" },
				{ Cue = "/VO/Orpheus_0209",
					Text = "I owe my everything to my Eurydice. She authored many of my songs, indeed; and she inspired many, many more. Whilst living, we collaborated frequently, you see." },
				{  Cue = "/VO/ZagreusHome_1037", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "But weren't you the famous one? I don't understand why she doesn't share your renown." },
				{ Cue = "/VO/Orpheus_0210",
					Text = "It is but one of life's injustices, I fear. I always sang her praises every chance I had. And she asserted herself splendidly. Yet even still, our listeners believed our songs, they came from me... what else could I have done?" },
				{  Cue = "/VO/ZagreusHome_1038", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "I don't exactly know the ways of mortals, mate. But I'm pleased to hear you always giving credit where it's due." },
			},
			OrpheusAboutEurydice06Extra =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusAboutEurydice06" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- Then, word will spread. I'm sure of it.
					{ Cue = "/VO/ZagreusHome_2624" },
				},
				{  Cue = "/VO/ZagreusHome_2622", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Orpheus, you said that it was one of life's injustices that Eurydice doesn't get her fair share of the credit for the songs she wrote. Isn't there something we can do?" },
				{ Cue = "/VO/Orpheus_0247",
					Text = "Why, I don't know, my friend. Once people set their minds to certain things... it can be difficult to show them other possibilities exist. What's to be done?" },
				{  Cue = "/VO/ZagreusHome_2623", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I don't know... tell them they're wrong? Politely, perhaps not? Or... tell them what's right. Get the word out about what's right? Can't we do that?" },
				{ Cue = "/VO/Orpheus_0248",
					Text = "Get the word out...? But I am court musician of this House. My songs no longer reach too many ears. Although... I do suppose all of these shades about... they are my audience, as well. I shall make certain that they know whose songs I sing." },
			},

			-- song request subplot begins
			OrpheusProgressWithEurydice01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "EurydiceAboutOrpheus04" },
				-- I know...
				EndCue = "/VO/ZagreusHome_1017",
				EndWait = 0.5,
				{ Cue = "/VO/Orpheus_0138",
					Text = "I must confess something to you, my friend. My mind still wanders to Eurydice quite often, and my memories of her. Despite my best attempts to move along with my existence here. I would call it a fatal flaw, except, I am already dead." },
				{  Cue = "/VO/ZagreusHome_1016", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Don't torture yourself, mate. You've been through enough. As has she! You still care for one another just don't have a way to get the point across. But I have an idea." },
				{ Cue = "/VO/Orpheus_0139",
					Text = "I, too, once had ideas, Zagreus. Ideas, and inspirations. It was so very wonderful, to have a muse..." },
			},
			OrpheusProgressWithEurydice02 =
			{
				SuperPriority = true,
				GiftableOffSource = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "EurydiceProgressWithOrpheus02" },
				-- Take all the time you need, mate.
				EndCue = "/VO/ZagreusHome_1019",
				EndWait = 0.5,
				{  Cue = "/VO/ZagreusHome_1018", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PostLineAnim = "ZagreusInteractEquip", PostLineAnimTarget = "Hero",
					Text = "I got you something, Orpheus. It's a new song. From someone who still cares for you." },
				{ Cue = "/VO/Orpheus_0140",
					PreLineWait = 0.5,
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteSurprise", WaitTime = 7.0 },
					Text = "My friend, you wrote a song, for me...? Oh, but this is not from you, it's from... {#DialogueItalicFormat}Eurydice{#PreviousFormat}... I, no, I don't know what to say, I... I..." },
			},
			-- add alt in case it's the first time Orpheus is singing; check for other contexts
			OrpheusSingsAgain03 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				InitialGiftableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "OrpheusProgressWithEurydice02" },
				RequiredFalseTextLines = { "OrpheusSingsAgain03_B" },
				RequiredFalseQueuedTextLines = { "OlympianReunionQuestComplete" },
				-- Perhaps she can.
				EndCue = "/VO/ZagreusHome_1021",
				EndWait = 0.55,
				{  Cue = "/VO/ZagreusHome_1020", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You sing beautifully, mate. If only Eurydice could hear you singing this right now." },
				{ Cue = "/VO/Orpheus_0141",
					Text = "Oh. Thanks for the sentiment, my friend, it's just... when I sing this song of hers, why, I've this feeling where my heart once was that, well... perhaps she {#DialogueItalicFormat}can{#PreviousFormat}." },
				OnQueuedFunctionName = "MusicianMusic",
				OnQueuedFunctionArgs = PresetEventArgs.SingingEurydiceSong01_Orpheus_SongFromStart,
				RequiredAmbientTrackNameMatch = true,
			},
			-- alt if this is first song he sings
			OrpheusSingsAgain03_B =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				InitialGiftableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "OrpheusProgressWithEurydice02" },
				RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain03" },
				RequiredFalseQueuedTextLines = { "OlympianReunionQuestComplete" },
				-- Perhaps she can.
				EndCue = "/VO/ZagreusHome_1021",
				EndWait = 0.55,
				{ Cue = "/VO/Orpheus_0088",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "I've decided I should give this singing thing another shot for now, my friend. I hope you like it." },
				{  Cue = "/VO/ZagreusHome_1020", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You sing beautifully, mate. If only Eurydice could hear you singing this right now." },
				{ Cue = "/VO/Orpheus_0141",
					Text = "Oh. Thanks for the sentiment, my friend, it's just... when I sing this song of hers, why, I've this feeling where my heart once was that, well... perhaps she {#DialogueItalicFormat}can{#PreviousFormat}." },
				OnQueuedFunctionName = "MusicianMusic",
				OnQueuedFunctionArgs = PresetEventArgs.SingingEurydiceSong01_Orpheus_SongFromStart,
				RequiredAmbientTrackNameMatch = true,
			},

			OrpheusAboutSingersReunionQuest01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				StatusAnimation = "StatusIconWantsToTalkImportant",
				RequiredCosmetics = { "OrpheusEurydiceQuestItem", },
				RequiresNullAmbientMusicId = true,
				EndVoiceLines =
				{
					PreLineWait = 0.55,
					-- Thank you. I shall. I shall!
					{ Cue = "/VO/Orpheus_0144" },
				},
				{ Cue = "/VO/Orpheus_0142",
					Emote = "PortraitEmoteSurprise",
					Text = "Zagreus! The most amazing circumstances have occurred. The terms of my employment, they've been altered such that... I may visit Asphodel sometimes. May visit {#DialogueItalicFormat}her{#PreviousFormat}." },
				{  Cue = "/VO/ZagreusHome_1022", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "That's great! Orpheus, what are you waiting for, mate? Go and find your muse out there! You two have lots to catch up on, I think." },
				{ Cue = "/VO/Orpheus_0143",
					Text = "I think you're right. I am so very anxious, I must say. But I must also thank you, for this opportunity, and... for speaking with me all this time. So, how might I repay you?" },
				{  Cue = "/VO/ZagreusHome_1023", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					PostLineThreadedFunctionName = "OrpheusExit", PostLineFunctionArgs = { AnimationState = "NPCOrpheusExited", WaitTime = 3.5 },
					SetFlagTrue = "OrpheusReunionInProgress",
					Text = "Why is it always talk of payment around here? You're my mate. And your songs have lifted many spirits here, including mine. Now go, and make the most of this, will you?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.OrpheusAlerting,
			},

			OrpheusAboutSingersReunionQuestComplete01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusWithEurydice01" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- Great to hear it. I can only imagine.
					{ Cue = "/VO/ZagreusHome_1026" },
				},
				{  Cue = "/VO/ZagreusHome_1024", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey Orpheus, you're back! How was your trip? I hope it was all right." },
				{ Cue = "/VO/Orpheus_0147",
					Text = "It was more than all right, my friend. Why, it was positively inspirational. But I've returned as promised to this House. My love is there, whilst my responsibilities are here." },
				{  Cue = "/VO/ZagreusHome_1025", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You sound despondent again, mate. You must wish you were there right now. I'm sorry you had to come all the way back." },
				{ Cue = "/VO/Orpheus_0148",
					Emote = "PortraitEmoteCheerful",
					Text = "Oh, on the contrary, Zagreus. It dawned upon me that having some distance between my responsibilities and my domestic life, why... it is not so bad. Besides, Charon makes travel to and fro quite simple for me, really." },
			},
			OrpheusAboutSingersReunionQuestComplete02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusWithEurydice03" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- You're doing wonderfully at it, mate.
					{ Cue = "/VO/ZagreusHome_1049" },
				},
				{  Cue = "/VO/ZagreusHome_1048", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I have to ask you something, Orpheus. It seems like everything has been all right for you, except... you don't look very happy to me, mate. Is everything all right? Between you and Eurydice, I mean?" },
				{ Cue = "/VO/Orpheus_0222",
					Emote = "PortraitEmoteSparkly",
					PreLineAnim = "OrpheusFidget",
					Text = "Oh, {#DialogueItalicFormat}haha{#PreviousFormat}, but your concern is much appreciated, friend. I feel I am in everlasting bliss. It simply does not show upon my countenance. Perhaps through song is how I best express myself." },
			},
			OrpheusAboutSingersReunionQuestComplete03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "ThanatosAboutSingersReunionQuestComplete01" },
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					-- Well, thank you also, mate.
					{ Cue = "/VO/ZagreusHome_2621" },
				},
				{  Cue = "/VO/ZagreusHome_2619", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Orpheus. You know I'm very happy that things turned out well between you and Eurydice, but... looking back, I think I might have overstepped my bounds with you a bit, and wanted to say I'm sorry." },
				{ Cue = "/VO/Orpheus_0245",
					Emote = "PortraitEmoteSurprise",
					Text = "You're sorry, Zagreus? Whatever for? If not for you, I fear I never would have seen my muse again." },
				{  Cue = "/VO/ZagreusHome_2620", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well, it's just... when I met her, and I found out the two of you were having problems, I... well, I just wanted to see you patch things up. But, it wasn't any of my business, and you never asked my help. Neither did she. I could have made things worse." },
				{ Cue = "/VO/Orpheus_0246",
					Text = "But, you didn't make things worse, my friend. And... truth be told, I do not think I ever would have asked for help, at any point, because... I don't entirely know how. So I am grateful to you, and your generous instinct." },
			},

			OrpheusPostEnding01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01" },
				EndVoiceLines =
				{
					{
						PreLineWait = 0.4,
						ObjectType = "NPC_Orpheus_01",
						RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
						-- I highly doubt it, no.
						{ Cue = "/VO/Orpheus_0259" },
					},
					{
						PreLineWait = 0.4,
						ObjectType = "NPC_Orpheus_01",
						RequiredAnyTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
						-- I shall give it some thought, I think.
						{ Cue = "/VO/Orpheus_0260" },
					},
				},				
				{  Cue = "/VO/ZagreusHome_3259", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Say, Orpheus! I heard what must have been a song of yours, it went {#DialogueItalicFormat}something, something, in the blood{#PreviousFormat}, you know that one? It was amazing, mate." },
				{ Cue = "/VO/Orpheus_0258",
					Text = "Oh, that one, yes, I know! I mean, it was a favorite, I was told, by those who listened to the music I created with my muse. It was a most productive time, back when we were alive!" },
				{  Cue = "/VO/ZagreusHome_3260", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Would you ever sing it again?" },
			},

			OrpheusAboutDistantMemory01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredKeepsake = "DistanceDamageTrait",
				-- RequiresMaxKeepsake = true,
				-- ...Err, didn't think so.
				EndCue = "/VO/ZagreusHome_1031",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_1030", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Orpheus. The keepsake that you gave me... it's come in useful, thanks. I can't quite see what's in the container, though. I shouldn't open it, should I?" },
				{ Cue = "/VO/Orpheus_0154",
					PreLineAnim = "OrpheusFidget",
					Text = "No, Zagreus. Unless of course you wanted to set loose a cherished living memory of mine, and take from me that fleeting bit of joy?" },
			},
			OrpheusAboutDistantMemory02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OrpheusAboutDistantMemory01", "OrpheusAboutEurydice03" },
				RequiredAnyTextLines = { "OrpheusGift05", "OrpheusAboutEurydice02", "OrpheusAboutEurydice02_B" },
				RequiredKeepsake = "DistanceDamageTrait",
				RequiresMaxKeepsake = true,
				-- I guess I can see that. Cheers.
				EndCue = "/VO/ZagreusHome_1033",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_1032", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Orpheus, the Distant Memory you gave me... it's a bit of your past with Eurydice in there, isn't it. I'm grateful, but... why give me such a precious gift as that?" },
				{ Cue = "/VO/Orpheus_0155",
					Text = "I'm glad you like it, Zagreus. Some memories are cherished, yes, indeed. But there can come a point where they provide more benefit to others than ourselves. Don't you agree?" },
			},

			-- music quest / music lessons
			OrpheusAboutMusicPlaying01 =
