				},
				{ Cue = "/VO/Orpheus_0213",
					PreLineAnim = "OrpheusFidget",
					Text = "Have you considered doing music-making as a recreational pursuit, my friend? You have such slender hands, why, I believe you'd do quite well at it." },
				{  Cue = "/VO/ZagreusHome_1041", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Not really been at the top of my list of late, mate, but who knows. It's something I've been meaning to try at some point, I suppose. Thanks for the words of encouragement!" },
			},
			OrpheusMusicProgress01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredCosmetics = { "HouseLyre01", },
				RequiredMinItemInteractions = { HouseLyre01 = 10 },
				-- Thanks for the impromptu lesson, mate.
				EndCue = "/VO/ZagreusHome_1043",
				EndWait = 0.6,
				{  Cue = "/VO/ZagreusHome_1042", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey Orpheus! I got myself a lyre much like yours, except I'm absolutely miserable when I try to play! Got any advice?" },
				{ Cue = "/VO/Orpheus_0215",
					PostLineThreadedFunctionName = "MusicPracticePresentation",
					Text = "Oh, why, that's splendid, Zagreus. And I can certainly show you a thing or two. First, the proper posture is important, here..." },
				{ Cue = "/VO/Orpheus_0216",
					FadeOutTime = 0.5, FullFadeTime = 7.0, FadeInTime = 1.0, PreLineWait = 0.1,
					FadeInSound = "/Leftovers/Menu Sounds/EmoteExcitement",
					InterSceneWaitTime = 0.3,
					Text = "...now remember to practice the plucking technique which I taught you, and mind your off-hand constantly. And, above all, continue practicing! The joyfulness is in the practicing itself." },
			},
			OrpheusMusicProgress02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredCosmetics = { "HouseLyre01" },
				RequiredTextLines = { "OrpheusMusicProgress01", },
				RequiredFalseTextLinesLastRun = { "OrpheusMusicProgress01" },
				RequiredMinItemInteractions = { HouseLyre01 = 60 },
				-- I hope you're right.
				EndCue = "/VO/ZagreusHome_1045",
				EndWait = 0.4,
				{ Cue = "/VO/Orpheus_0217",
					Text = "Zagreus, I heard your recent lyre-playing from your bedchambers. Let no one lead you to believe it's not a reassuring start." },
				{  Cue = "/VO/ZagreusHome_1044", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Oh, come now, mate, I'm terrible and you know it. You pulling one over on me as payback?" },
				{ Cue = "/VO/Orpheus_0218",
					PreLineAnim = "OrpheusFidget",
					Text = "I'm being serious, my friend. It is a challenging pursuit requiring a dedicated state of mind. The progress shall come. You shall discover what I mean." },
			},
			OrpheusMusicProgress03 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredCosmetics = { "HouseLyre01" },
				RequiredTextLines = { "OrpheusMusicProgress02" },
				RequiredFalseTextLinesLastRun = { "OrpheusMusicProgress02" },
				RequiredMinItemInteractions = { HouseLyre01 = 110 },
				EndVoiceLines =
				{
					{
						PreLineWait = 0.4,
						ObjectType = "NPC_Orpheus_01",
						-- It's almost everything to me, my friend.
						{ Cue = "/VO/Orpheus_0220" },
					},
				},
				{ Cue = "/VO/Orpheus_0219",
					Text = "Your music-playing has improved substantially. Perhaps in time you shall surpass my own abilities!" },
				{  Cue = "/VO/ZagreusHome_1046", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I highly doubt that, mate, though thanks for noticing. Creating music's quite rewarding once you get the hang of it!" },
			},
			OrpheusMusicProgress04 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredCosmetics = { "HouseLyre01" },
				RequiredTextLines = { "OrpheusMusicProgress03" },
				RequiredFalseTextLinesLastRun = { "OrpheusMusicProgress03" },
				RequiredMinItemInteractions = { HouseLyre01 = 210 },

				{ Cue = "/VO/Orpheus_0221",
					Text = "I must commend you on the most splendid lyre-playing emanating from your bedchambers, my friend. It seems we have a second court musician, now." },
				{  Cue = "/VO/ZagreusHome_1047", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Thank you for encouraging me to give it a shot, mate. And for the kind words." },
			},

			-- partner conversations
			OrpheusWithHades01 =
			{
				Priority = true,
				PlayOnce = true,
				Partner = "NPC_Hades_01",
				RequiredAnyTextLines = { "OrpheusFirstMeeting", "OrpheusFirstMeeting_Alt" },
				RequiredFalseTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain02", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },			
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				IsIdAlive = 370006,
				TeleportToId = 393481,
				TeleportOffsetX = 80,
				TeleportOffsetY = -80,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				-- He's in trouble.
				EndCue = "/VO/ZagreusHome_0553",
				EndWait = 0.6,
				{ Cue = "/VO/Orpheus_0096", PreLineWait = 0.3,
					Text = "...You called upon your humble court musician, as I understand, Lord Hades sir? Referring to myself of course?" },
				{ Cue = "/VO/Hades_0281", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					Text = "Well, Orpheus... if you're quite finished with your little stint within the depths of Tartarus, I'd like to ask again: Play us a song, already. Or else next time I'll not be quite so charitable with your punishment." },
				{ Cue = "/VO/Orpheus_0097",
					Text = "Oh, that. I must apologize, again, Lord Hades sir. For I'm afraid that I've no wish to sing as yet. In spite of your persuasiveness even. And, that, um, is my final word upon the subject. At this time." },
				{ Cue = "/VO/Hades_0282", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "Is that so...? Why, then, I'll go ahead and wait. I've all the patience in the world." },
			},

			OrpheusWithHades02 =
			{
				Priority = true,
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "OrpheusWithHades01" },
				RequiredAnyTextLines = { "OrpheusFirstMeeting", "OrpheusFirstMeeting_Alt" },
				MinRunsSinceAnyTextLines = { TextLines = GameData.HadesMiscPartnerTextLines, Count = 4 },
				RequiredFalseTextLines = { "OrpheusGift05", "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain02", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -380,
				TeleportOffsetY = 260,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				-- Poor Orpheus.
				EndCue = "/VO/ZagreusHome_0554",
				EndWait = 0.6,
				{ Cue = "/VO/Hades_0283", Portrait = "Portrait_Hades_Default_01", PreLineWait = 0.35, Speaker = "NPC_Hades_01",
					Text = "Help me to understand something here, Orpheus. Why do I bother keeping such a worthless shadow of a court musician in my House? You prattle ceaselessly, yet I can't get even a single note out of you?" },
				{ Cue = "/VO/Orpheus_0098",
					PreLineAnim = "OrpheusFidget",
					Text = "If I may be so bold, my lord? Perhaps the merest sight of me still stirs in you a memory of my performances of yore. It gladdens me you used to like my songs." },
				{ Cue = "/VO/Hades_0284",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "I used to tolerate your insolence. We're finished here. You find your voice. Immediately! You have one hundred years, and not a moment more." },
			},
			OrpheusWithHades03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "HadesAboutOrpheus01", "OrpheusAboutEurydice03" },
				RequiredAnyTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				RequiredFalseTextLines = { "OrpheusWithEurydice01" },
				RequiredFalseCosmetics = { "OrpheusEurydiceQuestItem" },
				Partner = "NPC_Hades_01",
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -380,
				TeleportOffsetY = 260,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				-- That's why he's stuck here, huh.
				EndCue = "/VO/ZagreusHome_0991",
				EndWait = 0.6,
				{ Cue = "/VO/Hades_0484", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "Finally returned to your senses, haven't you, Orpheus. A court musician who refused to sing! Such nonsense. I trust there shall be no more insubordination from you, moving forward?" },
				{ Cue = "/VO/Orpheus_0194",
					PreLineAnim = "OrpheusFidget",
					Text = "I'm gladdened that my songs yet stir your soul, my lord. But, I have to confess my choice to sing again was motivated largely by the Prince. And by my muse, of course." },
				{ Cue = "/VO/Hades_0485", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "Still going on about your so-called muse, I see. You had your chance with Eurydice. You squandered it. And now you're here; a deal is a deal. I expect you to comply with your end of our agreement." },
			},
			OrpheusWithHades04 =
			{
				PlayOnce = true,
				RequiredTextLines = { "OrpheusAboutSingersReunionQuestComplete01" },
				MinRunsSinceAnyTextLines = { TextLines = GameData.HadesMiscPartnerTextLines, Count = 4 },
				Partner = "NPC_Hades_01",
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -380,
				TeleportOffsetY = 260,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.55,
						ObjectType = "NPC_Hades_01",
						-- Nrgh...
						{ Cue = "/VO/Hades_0881" },
					},
					{
						PreLineWait = 0.15,
						UsePlayerSource = true,
						-- Good thing that's settled, then.
						{ Cue = "/VO/ZagreusHome_2618" },
					}
				},
				{ Cue = "/VO/Orpheus_0243", PreLineWait = 0.3,
					Text = "...I'm pleased to know my new, more flexible arrangement remains to your satisfaction, still, my lord. I must admit I feared that my departure would result once more in a protracted stay within the pits of Tartarus." },
				{ Cue = "/VO/Hades_0880", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					Text = "Don't give me such ideas, Orpheus! Instead, you go about your business, there, and play your blasted songs when circumstances call for it." },
				{ Cue = "/VO/Orpheus_0244",
					PreLineAnim = "OrpheusFidget",
					Text = "I shall be happy to oblige, my lord. Eurydice and I are grateful for your magnanimity." },
			},

			OrpheusWithAchilles01 =
			{
				Partner = "NPC_Achilles_01",
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				UseText = "UseListenNPC",
				RequiredTextLines = { "AchillesGrantsCodex" },
				RequiredAnyTextLines = { "OrpheusFirstMeeting", "OrpheusFirstMeeting_Alt" },
				RequiredAnyOtherTextLines = { "AchillesFirstMeeting", "AchillesFirstMeeting_Alt" },
				TeleportToId = 370009,
				TeleportOffsetX = 40,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370009,
				-- What can I say, I try.
				EndCue = "/VO/ZagreusHome_0258",
				EndWait = 0.4,

				{ Cue = "/VO/Orpheus_0090", PreLineWait = 0.3,
					Text = "...You're much too modest, Lord Achilles, for someone with such a number of heroic deeds and glorious decapitations to his name! You ought to be more like ever-boastful Zagreus, and talk it up some more!" },
				{ Cue = "/VO/Achilles_0080", Portrait = "Portrait_MaleGhost_Default_01", Speaker = "NPC_Achilles_01",
					Text = "Oh, fear not, Orpheus, I was quite boastful in my day. And would you look at where it got me? Besides, I've not the imagination to come up with tales as tall as those of our Prince Zagreus." },
			},
			OrpheusWithAchilles02 =
			{
				Partner = "NPC_Achilles_01",
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				UseText = "UseListenNPC",
				RequiredAnyTextLines = { "AchillesFirstMeeting", "AchillesFirstMeeting_Alt" },
				RequiredAnyOtherTextLines = { "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				TeleportToId = 370009,
				TeleportOffsetX = 40,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370009,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.55,
						ObjectType = "NPC_Achilles_01",
						-- Now that you mention it... I can't.
						{ Cue = "/VO/Achilles_0129" },
					},
					{
						PreLineWait = 0.15,
						UsePlayerSource = true,
						-- I can!
						{ Cue = "/VO/ZagreusHome_0990" },
					}
				},
				{ Cue = "/VO/Orpheus_0225", PreLineWait = 0.3,
					Text = "...Do you miss it, yourself, Lord Achilles, sir? The freshness of the air. The need to have meals several times a day, and such." },
				{ Cue = "/VO/Achilles_0128", Portrait = "Portrait_MaleGhost_Default_01", Speaker = "NPC_Achilles_01",
					Text = "I scarce remember it, my friend. Although your songs do stir up memories, sometimes. I'm used to things the way they are, I think. But you, not so much?" },
				{ Cue = "/VO/Orpheus_0226", PreLineWait = 0.3,
					PreLineAnim = "OrpheusFidget",
					Text = "Oh, on the contrary, sir, I am profoundly satisfied. We are most fortunate to have employment even after death. Imagine, merely resting for eternity, when you could work!" },
			},

			OrpheusWithPersephone01 =
			{
				Partner = "NPC_Persephone_Home_01",
				RequiredTextLines = { "Ending01", "PersephoneHomeMeeting01" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 3 },
				MaxRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 25 },
				RequiredAnyTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain02", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				StatusAnimation = false,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 555714,
				TeleportOffsetX = -40,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 555714,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Persephone_Home_01",
					-- Oh, you're too kind!
					{ Cue = "/VO/Persephone_0198", PreLineAnim = "PersephoneUnderworld_Greeting" },
				},
				{ Cue = "/VO/Persephone_0197", Portrait = "Portrait_Persephone_Queen_01", Speaker = "NPC_Persephone_Home_01",
					PreLineAnim = "PersephoneUnderworld_Vulnerable", PreLineAnimTarget = 555714,
					PreLineWait = 0.35,
					Text = "...{#DialogueItalicFormat}Ah{#PreviousFormat}, but to think that our esteemed musician of the court is none other than {#DialogueItalicFormat}you{#PreviousFormat}, Orpheus! In matters of music, at least, Hades and I have similar tastes. Anyway, I don't mean to keep you!" },
				{ Cue = "/VO/Orpheus_0268", PreLineWait = 0.35,
					PreLineAnim = "OrpheusFidget",
					Text = "I shall be honored to perform my songs, Your Grace. And, might I add, Your Grace bears many of the striking features of your son? I understand now where he gets his charm." },
			},

