		GiftTextLineSets =
		{
			-- grants a gift
			OrpheusGift01 =
			{
				PlayOnce = true,
				{ Cue = "/VO/ZagreusHome_0115", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey, mate, here. I got this for my favorite court musician." },
				{ Cue = "/VO/Orpheus_0057",
					Text = "Oh, my, but you can't simply be this kind to me without retaliation on my part, my friend! And so, in turn, I have a gift for you!" },
			},
			OrpheusGift02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "OrpheusGift01", },
				{ Cue = "/VO/Orpheus_0051",
					Text = "Why, this is absolutely splendid, Zagreus. Is this really for me? It's so very kind of you to think of me like this." },
				{ Cue = "/VO/ZagreusHome_0239", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "I want you to have it, Orpheus. You've brightened up our lives down here more than you know. I only wish that you could see that." },
			},
			OrpheusGift03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "OrpheusGift02", },
				{ Cue = "/VO/Orpheus_0055",
					Emote = "PortraitEmoteSurprise",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, why! I'm very much obliged that you have thought of me again, for such a wonderfully thoughtful gift!" },
				{ Cue = "/VO/ZagreusHome_0240", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "It's nothing, Orpheus. Thank you for all the inspiration through your song. I love your work, mate." },
			},
			-- alt below in case he's already started singing
			OrpheusGift04 =
			{
				PlayOnce = true,
				RequiredTextLines = { "OrpheusGift03", },
				RequiredFalseTextLines = { "OrpheusGift04_B", "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				{ Cue = "/VO/Orpheus_0056",
					Text = "You're always much too kind, you know, my friend? Now typically I would express my gratitude through song, except, you understand, don't you?" },
				{ Cue = "/VO/ZagreusHome_0522", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "I understand completely, Orpheus. I think we'll both get through this rough patch in due time." },
			},
			OrpheusGift04_B =
			{
				PlayOnce = true,
				RequiredTextLines = { "OrpheusGift03" },
				RequiredAnyTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain01_C", "OrpheusSingsAgain01_D", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				RequiredFalseTextLines = { "OrpheusGift04" },
				{ Cue = "/VO/Orpheus_0195",
					Text = "You always offer means to quench my thirst, my friend. A wonder that I have such feelings, even now." },
				{ Cue = "/VO/ZagreusHome_0082", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Now that you've started singing once again, I figured it was thirst-inducing work! Your music stirs so many souls around here, mate." },
			},
			OrpheusGift05 =
			{
				-- cutaway scene
				PlayOnce = true,
				RequiredAnyTextLines = { "OrpheusGift04", "OrpheusGift04_B" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Take care, mate.
					{ Cue = "/VO/ZagreusHome_3058" },
				},
				{ Cue = "/VO/ZagreusHome_0202", Portrait = "Portrait_Zag_Default_01", PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero", PreLineWait = 0.35, Speaker = "CharProtag", PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey, mate! What do you say we share this over in the lounge?" },
				{ Cue = "/VO/Orpheus_0052", PostLineAnim = "ZagreusTalkEmpathyStart", PostLineAnimTarget = "Hero",
					PreLineAnim = "OrpheusFidget",
					Text = "Oh, I, certainly, why, I don't see why not. I mean, it's not as though I've got something to do that isn't that." },
				{ Cue = "/VO/Orpheus_0053", FadeOutTime = 0.5, FullFadeTime = 0.5, TeleportToId = 556834, AngleTowardTargetId = 556835, TeleportOffsetY = 30, TeleportOffsetX = -20, TeleportHeroToId = 556835, AngleHeroTowardTargetId = 556834, FadeInTime = 0.5, PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero", AttachedDim = "Lounge",
					FadeInSound = "/Leftovers/World Sounds/MapZoomInShortHigh",
					Text = "...And, anyways, I oughtn't have looked back, I mean, the moral of the story is to not look back when you are told not to look back, you understand, don't you, my friend?" },
				{ Cue = "/VO/ZagreusHome_3117", Portrait = "Portrait_Zag_Serious_01", PortraitExitAnimation = "Portrait_Zag_Serious_01_Exit", Speaker = "CharProtag", PreLineWait = 0.35,
					PostLineThreadedFunctionName = "LoungeRevelryPresentation",
					PostLineFunctionArgs = { Sound2 = "/EmptyCue", Sound3 = "/EmptyCue" },
					Text = "Don't look back, huh. I'll try not to. Though, you can't be blamed for doing it yourself, just the one time. What happened to you... it wasn't fair. If you ask me." },
				{ Cue = "/VO/Orpheus_0054", FadeOutTime = 0.5, FullFadeTime = 7.8, FadeInTime = 2.0, InterSceneWaitTime = 0.5, TeleportToId = 391546, TeleportHeroToId = 391546, TeleportHeroOffsetX = -30, TeleportHeroOffsetY = 90, AngleHeroTowardTargetId = 391546, FadeInTime = 0.5, PreLineWait = 0.35,
					FadeInSound = "/Leftovers/World Sounds/MapZoomInShortHigh",
					Text = "...I quite enjoyed our outing, Zagreus. So, I suppose I shall be seeing you around?" },
			},

			OrpheusGift06 =
			{
				PlayOnce = true,
				RequiredTextLines = { "OrpheusGift05", },
				{ Cue = "/VO/Orpheus_0196",
					Text = "I am indebted to you, Zagreus, both for your friendship and embarrassment of generosity." },
				{ Cue = "/VO/ZagreusHome_2319", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "And I, in turn, am indebted to you, Orpheus, for your calm demeanor and ability to accept life's greatest challenges. You inspire me, and I wish you the best." },
			},

			-- high relationship / locked gifts
			OrpheusGift07 =
			{
				PlayOnce = true,
				RequiredTextLines = { "OrpheusGift06", },
				-- You're too kind.
				EndCue = "/VO/ZagreusHome_0334",
				EndWait = 0.35,
				{ Cue = "/VO/ZagreusHome_2320", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Orpheus, mate! I have something good for you right here... Ambrosia! All the way from the surface. I want you to have it." },
				{ Cue = "/VO/Orpheus_0197",
					Text = "Ah, but this is splendid, Zagreus. And I must say, apart from my Eurydice, I've never felt as strong a bond of kinship as with you." },
			},
			OrpheusGift08 =
			{
				PlayOnce = true,
				RequiredTextLines = { "OrpheusGift07", },
				{ Cue = "/VO/ZagreusHome_2322", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Hey Orpheus, you know I once wrested this bottle of Ambrosia from the Champions of Elysium, defeating them two against one, just so I could bring it here to you personally?" },
				{ Cue = "/VO/Orpheus_0198",
					Text = "Please, my friend! I feel I am undeserving of such a relentless show of kindness on your part. I shall accept, but you'll relent, won't you? As well as forgive me?" },
				{ Cue = "/VO/ZagreusHome_2323", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Mate, if that is your wish, then it will be my pleasure to oblige. Though, may this gift symbolize the strength of our friendship for an eternity to come. I've learned so much from you." },
				{ Cue = "/VO/Orpheus_0179",
					PostLineThreadedFunctionName = "MaxedRelationshipPresentation",
					PostLineFunctionArgs = { Text = "NPC_Orpheus_01", Icon = "Keepsake_OrpheusSticker_Max" },
					Text = "Thank you, my friend. Would that I had capacity to take some joy from this." },
			},

