			MegaeraMirrorProgress01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraMeeting05", "FuryDarknessReaction01" },
				RequiredFalseTextLines = { "MegaeraGift10" },
				RequiredAccumulatedMetaPoints = 2000,
				RequiredActiveMetaPointsMin = 1000,
				RequiredKillsThisRun = { "Harpy" },
				-- Nothing wrong with having help.
				EndCue = "/VO/ZagreusHome_0749",
				EndWait = 0.45,
				{ Cue = "/VO/MegaeraHome_0105",
					Text = "Zagreus. Good, I wanted to tell you something. Your victories against me? They don't count for anything." },
				{ Cue = "/VO/ZagreusHome_0747", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Sounds like you've been giving this a lot of thought, there, Meg. How do you figure?" },
				{ Cue = "/VO/MegaeraHome_0106",
					Text = "Nyx's Mirror. You couldn't possibly beat me without its help, or hers." },
				{ Cue = "/VO/ZagreusHome_0748", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I'm not competing with you, Meg. I'm trying to get past you, to the surface." },
				{ Cue = "/VO/MegaeraHome_0107",
					Text = "Call it what you want. You're doing what you have to do. I understand. But you are not stronger than me. You just have more help." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			-- three variants below based on relationship
			MegaeraMirrorProgress02 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "FuryNoDarknessReaction01" },
				RequiredFalseTextLines = { "MegaeraMirrorProgress02B", "MegaeraMirrorProgress02C", "MegaeraGift06" },
				RequiredActiveMetaPointMax = 0,
				RequiredKillsThisRun = { "Harpy" },
				-- We'll see.
				-- EndCue = "/VO/ZagreusHome_0751",
				-- EndWait = 0.45,
				{ Cue = "/VO/MegaeraHome_0132",
					Text = "Beaten. By you. Without the help of Nyx's mirror. You humiliated me. And now you've come to rub it in, I guess? Well, go on, then. Not like you can shame me any more." },
				{ Cue = "/VO/ZagreusHome_1348", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Meg, what is it going to take for you to quit treating me like I'm incompetent? Have I not grown even a little since we met? You were supposed to teach me something; what does that make you?" },
				{ Cue = "/VO/MegaeraHome_0133",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "{#DialogueItalicFormat}Tsch{#PreviousFormat}... why don't you raise your voice a little louder so that all the House can hear. Is that what you want? For everyone to know that you're superior to me?" },
				{ Cue = "/VO/ZagreusHome_1349", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You're not hearing me, Meg. I know it sounds a little disingenuous considering we keep fighting to the death and all, but... I never meant to hurt you." },
				{ Cue = "/VO/MegaeraHome_0134",
					Text = "You give yourself far too much credit, Zagreus. Leave me alone." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			-- high relationship
			MegaeraMirrorProgress02B =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "FuryNoDarknessReaction01", "MegaeraGift06" },
				RequiredFalseTextLines = { "BecameCloseWithMegaera01Meg_GoToHer", "MegaeraGift10", "MegaeraMirrorProgress02C" },
				RequiredActiveMetaPointMax = 0,
				RequiredKillsThisRun = { "Harpy" },
				-- We'll see.
				-- EndCue = "/VO/ZagreusHome_0751",
				-- EndWait = 0.45,
				{ Cue = "/VO/MegaeraHome_0132",
					Text = "Beaten. By you. Without the help of Nyx's mirror. You humiliated me. And now you've come to rub it in, I guess? Well, go on, then. Not like you can shame me any more." },
				{ Cue = "/VO/ZagreusHome_1350", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Meg, that isn't why I'm here, you know that isn't how I am. Why can't you see it? All I've ever wanted was to prove myself to you." },
				{ Cue = "/VO/MegaeraHome_0135",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "{#DialogueItalicFormat}Tsch{#PreviousFormat}... you think that you can prove yourself to me by winning in a fight?" },
				{ Cue = "/VO/ZagreusHome_1351", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Sometimes I think that I can never really win with you, Meg. If I held back, what would you have thought?" },
				{ Cue = "/VO/MegaeraHome_0136",
					Text = "If you think you know me so well, Zagreus, then you ought to know better than to keep this conversation going with me, now." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			-- became close relationship
			MegaeraMirrorProgress02C =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "FuryNoDarknessReaction01", "MegaeraGift10" },
				RequiredFalseTextLines = { "MegaeraMirrorProgress02", "MegaeraMirrorProgress02B" },
				RequiredActiveMetaPointMax = 0,
				RequiredKillsThisRun = { "Harpy" },
				-- We'll see.
				-- EndCue = "/VO/ZagreusHome_0751",
				-- EndWait = 0.45,
				{ Cue = "/VO/MegaeraHome_0137",
					Text = "I can't believe it, Zag. I thought for sure that Nyx's mirror was the only reason you could beat me. I knew you'd changed to some extent, but... not this much." },
				{ Cue = "/VO/ZagreusHome_1352", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I had to see if I could do it, Meg. I've learned so much from you, I... thought maybe this was a way that I could prove myself to you. And to myself." },
				{ Cue = "/VO/MegaeraHome_0138", Portrait = "Portrait_FurySister01_Pleased_01",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "Prove yourself, to me...? You must not be as comfortable in your own skin as you let on. I can't decide if I like that about you. Now leave me alone while I give it some thought." },
			},

