				RequiredMinNPCInteractions = { NPC_FurySister_01 = 1 },
				RequiredMinCompletedRuns = 4,
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Yeah.
					{ Cue = "/VO/ZagreusField_1710" },
				},
				{ Cue = "/VO/MegaeraHome_0093",
					Text = "Well, how was it up there, Zagreus? Within Elysium. You'll have to tell us all about it every time the greatest strike you down." },
				{ Cue = "/VO/ZagreusHome_0762", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You've never been there, have you? It's... lush? Is that a word? It feels closer to the surface. Closer to the sky." },
				{ Cue = "/VO/MegaeraHome_0094",
					Text = "How poetic. You could have visited as part of your responsibilities. Instead, you trespass. Incurring wrath of fallen champions in service of your father." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
