			MegaeraRunProgress02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredRoomThisRun = "D_Intro",
				RequiredAnyTextLines = { "MegaeraBuildingTrust01", "MegaeraBuildingTrust01_B" },
				RequiredKills = { SatyrRanged = 8 },
