				{
					PreLineWait = 0.6,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- I suppose not...
					{ Cue = "/VO/ZagreusHome_1382" },
				},
				{ Cue = "/VO/MegaeraHome_0162",
					Text = "So you cleared out some of the Satyr infestation near the surface, then. How come the change of heart? Not like you to assist Lord Hades in such things." },
				{ Cue = "/VO/ZagreusHome_1381", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I wasn't trying to do my father any favors, Meg. Those Satyrs seem to hate me just as much as him. My one way out involves a detour straight into their dens." },
				{ Cue = "/VO/MegaeraHome_0163",
					Text = "An interesting coincidence. At any rate, that's one less problem for the rest of us to deal with. Until the Satyrs all come back, at least, which won't be long." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraRunProgress03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift01", "SisyphusGift03" },
				RequiredFalseTextLines = { "Ending01" },
				RequiredAccumulatedMetaPoints = 1200,
				RequiredMinCompletedRuns = 10,
				-- We'll see.
				EndCue = "/VO/ZagreusHome_0751",
				EndWait = 0.45,
				{ Cue = "/VO/MegaeraHome_0108",
					Text = "You've been through hell more times than you can count by now, haven't you, Zag. You getting used to it? No wonder that you're friends with Sisyphus." },
				{ Cue = "/VO/ZagreusHome_0750", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I'm getting used to it, all right. I'll keep going, for as many chances as it takes. I'll look forward to our next appointment out there." },
				{ Cue = "/VO/MegaeraHome_0109",
					Text = "{#DialogueItalicFormat}Appointment{#PreviousFormat}. Indeed. Don't keep me waiting. For all your toil, I'm sure you're not even close." },
