			MegaeraAboutPersephoneMeeting02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneMeeting03", "MegaeraGift03" },
				RequiredFalseTextLines = { "Ending01" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_FurySister_01",
					-- None of my business, I said.
					{ Cue = "/VO/MegaeraHome_0408" },
				},
				{ Cue = "/VO/ZagreusHome_3169", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Meg, did you... did you ever meet the Queen...? Remember anything about her, at all?" },
				{ Cue = "/VO/MegaeraHome_0407",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "It was not my place to address her. I barely ever saw her. Then, at some point... I never saw her again. And, unlike you, I know better than to get involved in things that are none of my business." },
				{ Cue = "/VO/ZagreusHome_3170", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "How is it that this place has this secret Queen for I don't know how long, then suddenly she just vanishes, and no one speaks a word of it?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			MegaeraAboutPersephoneMeeting03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraAboutPersephoneMeeting02", "MegaeraGift03", "PersephoneMeeting03" },
				RequiredFalseTextLines = { "Ending01", "PersephoneMeeting07" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- I think that she can be the judge of that.
					{ Cue = "/VO/ZagreusHome_3694" },
				},
				{ Cue = "/VO/MegaeraHome_0350",
					Text = "How is she doing, Zagreus? The Queen, I mean. She was... I had admired her." },
				{ Cue = "/VO/ZagreusHome_3693", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Mother... she's doing well, I think. She's in good spirits every time we meet. Happy to let her know you're thinking of her, by the way!" },
				{ Cue = "/VO/MegaeraHome_0351", PreLineWait = 0.5,
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "Don't, it's... it isn't necessary. She doesn't need you telling her about this place. Sounds to me like she is better off without." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraPostEnding01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01" },
				MaxRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 18 },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,	
					-- We'll make it official, all right.
					{ Cue = "/VO/ZagreusHome_3183" },
				},
				{ Cue = "/VO/MegaeraHome_0424",
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					PostLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",		
					Text = "Just so I understand. Breaking through everything in your path, taking resources from your father's realm, trying to get through me... all officially part of the job description?" },
				{ Cue = "/VO/ZagreusHome_3182", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I guess that's right... but, before you ask, I'm not doing it for him." },
				{ Cue = "/VO/MegaeraHome_0425",
					AngleTowardHero = true,
					Text = "I didn't think you'd have it in you. To leave this place for good. But I didn't think you'd take responsibility for anything, either. Anyway... we'll just have to kill each other more officially, I guess." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			-- skipped Ending02
			MegaeraPostEnding03 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraPostEnding01" },
				MaxRunsSinceAnyTextLines = { TextLines = { "MegaeraPostEnding01" }, Count = 22 },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Hope to find out.
					{ Cue = "/VO/ZagreusHome_3175" },
				},
				{ Cue = "/VO/MegaeraHome_0411",
					Text = "I never thought I'd see the Queen again. I still can't quite believe she's back... and staying? Is that really your doing?" },
				{ Cue = "/VO/ZagreusHome_3173", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's her doing, not mine. So you know her? Because... you could have told me. About her. Who she was to me." },
				{ Cue = "/VO/MegaeraHome_0412",
					AngleTowardHero = true,
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					PostLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					Text = "Don't know her all that well. But I do know Nyx, and I've been bound to secrecy as part of my oath to this House. I flay oathbreakers and liars, Zagreus. Wouldn't do for me to be no better than them, now, would it?" },
				{ Cue = "/VO/ZagreusHome_3174", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "No, I suppose not. So what does this mean for Nyx, now that she's back? Or for you, for that matter?" },
				{ Cue = "/VO/MegaeraHome_0413",
					AngleTowardTargetId = 423122,
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "The Queen always gave Nyx her space. Your father, though... I don't know what it'll be like for him, if she decides to stick around this time." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraPostEpilogue01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OlympianReunionQuestComplete" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,					
					-- Exactly!
					{ Cue = "/VO/ZagreusHome_3185" },
				},
				{ Cue = "/VO/MegaeraHome_0426",
					Text = "So the Olympians are backing you officially now? Doing their part to help the family business, finally. Lord Hades probably never thought he'd see the day. Night? You know what I mean." },
				{ Cue = "/VO/ZagreusHome_3184", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Seems only fair they should help in some capacity. They all work together on the surface, and, let's face it, they're responsible for a good number of shades pouring into this realm. Least they can do is help shore up the whole {#DialogueItalicFormat}'there is no escape' {#PreviousFormat}reputation we've got." },
				{ Cue = "/VO/MegaeraHome_0427",
					Text = "By making you unfairly stronger by the time you try to get through me?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutRelationship01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "BecameCloseWithMegaera01Meg_GoToHer", "BecameCloseWithMegaera01_BMeg_GoToHer" },
				EndVoiceLines =
				{
					{
						PreLineWait = 0.5,
						ObjectType = "NPC_FurySister_01",
						-- Heh. Guess you'll find out.
						{ Cue = "/VO/MegaeraHome_0395" },
					},
					{
						PreLineWait = 0.4,
						UsePlayerSource = true,
						-- Heh.
						{ Cue = "/VO/ZagreusHome_3190" },
					},
				},
				{ Cue = "/VO/MegaeraHome_0393", Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "Well, Zag? You like things nice and complicated, don't you. Apparently so do I." },
				{ Cue = "/VO/ZagreusHome_3153", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "If you're asking if I have any regrets about what happened between us, the answer is no. Being with you... any complications there are worth it." },
				{ Cue = "/VO/MegaeraHome_0394", Portrait = "Portrait_FurySister01_Pleased_01",
					AngleTowardHero = true,
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					PostLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					Text = "You don't need to tell me. Two rules, if you want to keep this up. First... we set the personal aside when we're out there. I better not hear a soul say I'm going soft on you. And second... keep up this slightly more mature thing you've had going for you recently." },
				{ Cue = "/VO/ZagreusHome_3154", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					AngleTowardTargetId = 423122,
					Text = "Wasn't planning on sliding back into the insensitive nonsense of youth, so I don't think the second thing will be a problem. But the first... can we really just keep killing each other out there like nothing happened?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			MegaeraAboutRelationship01B =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "BecameCloseWithMegaera01Meg_BackOff", "BecameCloseWithMegaera01_BMeg_BackOff" },
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,					
					-- Thanks, Meg.
					{ Cue = "/VO/ZagreusHome_3161" },
				},
				{ Cue = "/VO/MegaeraHome_0399", Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "{#DialogueItalicFormat}Tsk{#PreviousFormat}... Come off it, Zag. Are you really going to act all uncomfortable every time you see me now? More than usual I mean." },
				{ Cue = "/VO/ZagreusHome_3160", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "No, it's just... I felt terrible, turning you away. It's just... I've been so confused lately, and... I trust you. You're someone I can talk to. I guess I'm... afraid to lose that." },
				{ Cue = "/VO/MegaeraHome_0400", Portrait = "Portrait_FurySister01_Pleased_01",
					AngleTowardHero = true,
					Text = "Don't be an idiot, and you won't. We've been through a lot together. Grown, maybe. This particular relationship... it can't die so easily, I think." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutRelationship02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraAboutRelationship01", "AphroditeAboutMegRelationship01" },				
				RequiredAnyOtherTextLines = { "MegIntermissionChat01", "MegIntermissionChat02", "MegIntermissionChat03", "MegIntermissionChat04", "MegIntermissionChat05", "MegIntermissionChat06" },
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					-- I refuse.
					{ Cue = "/VO/ZagreusHome_3159" },
				},
				{ Cue = "/VO/ZagreusHome_3156", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I feel like everybody knows about us, Meg. Even Aphrodite, herself, seemed to know we were involved. It's not like I've been going around boasting. You wanted to keep things quiet, didn't you?" },
				{ Cue = "/VO/MegaeraHome_0396", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Pleased_01", AngleTowardHero = true,
					Text = "Zagreus. Are you happy with how things are between us now? Or do you have a problem with it?" },
				{ Cue = "/VO/ZagreusHome_3157", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well, I could do without the kill-or-be-killed stuff if we're being honest, that part is still a little weird, but otherwise... yes, I mean... I'm happy. You're {#DialogueItalicFormat}you{#PreviousFormat}." },
				{ Cue = "/VO/MegaeraHome_0397", Portrait = "Portrait_FurySister01_Pleased_01",
					AngleTowardTargetId = 423122,
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "Then, that's enough. Let them say what they want. What does it matter? We're gods. The things they say about us... even other gods... you pay too much attention to it, it'll drive you out of your mind." },
				{ Cue = "/VO/ZagreusHome_3158", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "But... I know how important your career is to you. This won't create a problem?" },
				{ Cue = "/VO/MegaeraHome_0398", Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "We've already crossed that bridge, I think. Stop worrying." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutRelationship03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "AphroditeAboutLove01", "MegaeraAboutRelationship02" },
				RequiredMinAnyTextLines = { TextLines = { "MegIntermissionChat01", "MegIntermissionChat02", "MegIntermissionChat03", "MegIntermissionChat04", "MegIntermissionChat05", "MegIntermissionChat06" }, Count = 2 },
				EndVoiceLines =
