					{ Cue = "/VO/ZagreusHome_3187" },
				},
				{ Cue = "/VO/MegaeraHome_0428", Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "Never in my wildest fantasies did I imagine you'd ever end up on the Featured Houseservant Board, Zagreus. I still can't entirely believe it." },
				{ Cue = "/VO/ZagreusHome_3186", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Whoa{#PreviousFormat}, back up! Can you elaborate a bit about these wildest fantasies, Meg?" },
				{ Cue = "/VO/MegaeraHome_0429", PreLineWait = 0.35, Portrait = "Portrait_FurySister01_Pleased_01",
					AngleTowardHero = true,
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "I'm serious. You should be proud. Not arrogantly proud to a fatal fault or anything, but a little bit is all right in a case like this." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutRepeatedLosses01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift03" },
				RequiredFalseTextLines = { "MegaeraGift10" },
				RequiredRoomThisRun = "A_Boss01",
				RequiredFalseDeathRoom = "A_Boss01",
				ConsecutiveClearsOfRoom =
				{
				  Name = "A_Boss01",
				  Count = 5,
				},
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- I'll keep putting up a fight if it's any consolation.
					{ Cue = "/VO/ZagreusHome_3168" },
				},
				{ Cue = "/VO/MegaeraHome_0405",
					AngleTowardHero = true,
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					Text = "Got to tell you something, Zagreus. I'm not getting any more used to losing to you like this. I mean... you don't even stop to rub it in. You know how humiliating that is?" },
				{ Cue = "/VO/ZagreusHome_3167", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Wait, you're saying you would feel {#DialogueItalicFormat}less {#PreviousFormat}ashamed about the outcomes of our fights if I were to gloat {#DialogueItalicFormat}more {#PreviousFormat}about beating you? How does that make any sense? " },
				{ Cue = "/VO/MegaeraHome_0406",
					AngleTowardTargetId = 423122,
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					Text = "You know what doesn't make any sense? That you keep beating me. I must be getting sloppy if I'm that predictable... too bad none of the other wretches I have to deal with put up much of a fight." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutSisters01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "FuryMetSisters01", "Fury2FirstAppearance", "Fury3FirstAppearance", "MegaeraGift02" },
				{ Cue = "/VO/ZagreusHome_0766", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusExpressiveEmpathy", PreLineAnimTarget = "Hero",
					Text = "You and your sisters really don't get along well, do you." },
				{ Cue = "/VO/MegaeraHome_0098",
					Text = "Alecto and Tisiphone and I each have a different outlook, I would say. We've learned mostly to stay out of one another's way." },
				{ Cue = "/VO/ZagreusHome_0767", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenial_Full", PreLineAnimTarget = "Hero",
					Text = "That doesn't sound like a healthy basis for a relationship between siblings." },
				{ Cue = "/VO/MegaeraHome_0099",
					AngleTowardHero = true,
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					Text = "Are you lecturing me about healthy relationships with family, Zagreus? Your family, your father's family, is the most broken and corrupted in the history of the entire concept." },
				{ Cue = "/VO/ZagreusHome_0768", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag", PreLineWait = 0.3,
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "...Fair point. I... didn't mean to sound like I had any meaningful advice, there, Meg. We're stuck with our bloodlines and our relatives, aren't we." },
				{ Cue = "/VO/MegaeraHome_0100",
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					Text = "Your family cut their Titan parents into pieces and then scattered them in Tartarus. Remember that when next you tell me about family." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutSisters02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "FurySistersUnion01" },
				RequiredMinActiveMetaUpgradeLevel = { Name = "BossDifficultyShrineUpgrade", Count = 1 },
				RequiredSupportAINames = { "Tisiphone", "Alecto" },
				RequiredRoomThisRun = "A_Boss01",
				-- RequiredFalseTextLines = { "FurySistersUnion01" },
				-- But it's a step.
				EndCue = "/VO/ZagreusHome_1385",
				EndWait = 0.5,
				{ Cue = "/VO/ZagreusHome_1383", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "You know, while it was brutal squaring off against the three of you, I have to say, Meg... I was happy you were with your sisters. Nothing like a common cause for bringing bitter rivals together, right?" },
				{ Cue = "/VO/MegaeraHome_0164",
					Text = "You have your Pact of Punishment to thank for our reunion, there. We have to answer to its terms. But my sisters and I, we got on better than I had expected, I will give you that." },
				{ Cue = "/VO/ZagreusHome_1384", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "So you've patched things up, then? Going to start spending more quality time together and all that?" },
				{ Cue = "/VO/MegaeraHome_0165", Portrait = "Portrait_FurySister01_Standoffish_01",
					Text = "{#DialogueItalicFormat}Hm{#PreviousFormat}, let's not get carried away. A work assignment forcing our cooperation is not exactly a big step in the quality of our relationship." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutAlecto01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				-- I see.
				EndCue = "/VO/ZagreusHome_0564",
				EndWait = 0.55,
				RequiredTextLines = { "Fury2FirstAppearance", "MegaeraGift02" },
				{ Cue = "/VO/ZagreusHome_0769", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Alecto's really something else. She must have been a real handful, huh?" },
				{ Cue = "/VO/MegaeraHome_0101",
					Text = "My youngest sister takes a lot of pleasure from her work. I've learned something of that from her. But that's as far as we relate, I think." },
				{ Cue = "/VO/ZagreusHome_0770", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "She could learn a thing or two from you, I'm sure. About having a little common decency, perhaps." },
				{ Cue = "/VO/MegaeraHome_0102",
					Text = "She won't be learning anything from me. The contempt you get from her is unconditional. I learned not to take it personally." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutTisiphone01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "Fury3FirstAppearance", "MegaeraGift02" },
				{ Cue = "/VO/ZagreusHome_0771", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Erm{#PreviousFormat}, so, about Tisiphone, is... {#DialogueItalicFormat}uh{#PreviousFormat}... everything all right with her? Talk of murder, all the time, and all." },
				{ Cue = "/VO/MegaeraHome_0103",
					Text = "Tisiphone is charged with punishing the worst that mortals have to offer. More than charged. Her work is everything to her." },
				{ Cue = "/VO/ZagreusHome_0772", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "No time for family or recreation, then, I take it? Pity what happens to those overly obsessed with work." },
				{ Cue = "/VO/MegaeraHome_0104",
					Text = "I get what you're insinuating, Zagreus. I'm nothing like her. Not even close. And don't you dare compare the two of us again." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			MegaeraAboutTisiphone02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "MegaeraGift06", "Fury3Encounter13" },
				UseableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_FurySister_01",
					-- You know, that's a good way to put it.
					{ Cue = "/VO/MegaeraHome_0403" },
				},
				{ Cue = "/VO/MegaeraHome_0401",
					Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "So you've been trying to give Tisiphone speech lessons between fights to the death. Are you bored, or just naive?" },
				{ Cue = "/VO/ZagreusHome_3162", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Come on, if her vocabulary consists of two different although admittedly almost identical words, there's got to be room for growth, there. Besides, I feel like... maybe she's lonely, or something. Has anyone ever tried to teach her anything?" },
				{ Cue = "/VO/MegaeraHome_0402",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "Maybe, just maybe, you're projecting your own insecurities onto her a little bit? Tis can take care of herself. Always has. Talking's not her strong suit like it's yours. But if you, in all your wisdom, think you've got something to teach her, you go right ahead." },
				{ Cue = "/VO/ZagreusHome_3163", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "You've tried already, haven't you? She's pretty dead-set on the murder thing, then?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutThanatos01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift07", "ThanatosGift07_A" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- You'd think!
					{ Cue = "/VO/ZagreusHome_3152" },
				},
				{ Cue = "/VO/ZagreusHome_3150", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You and Thanatos don't work together much anymore, do you, Meg?" },
				{ Cue = "/VO/MegaeraHome_0388",
					Text = "No. Though he still brings me my assignments every now and then." },
				{ Cue = "/VO/ZagreusHome_3151", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Oathbreakers, traitors, and the like? Sending them straight to Tartarus, huh. Must be quite an experience going from living and breathing, to... being dead, to being whisked off by Thanatos, straight to you and your whip." },
				{ Cue = "/VO/MegaeraHome_0389",
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					PostLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
				Text = "If that's not the experience mortals are looking for, they need only keep their oaths and not betray the ones they're closest to. It's not that hard." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutHypnos01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraWithHypnos03" },
				EndVoiceLines =
				{
					{
						PreLineWait = 0.4,
						UsePlayerSource = true,
						-- Have you tried sulking in the lounge?
						{ Cue = "/VO/ZagreusHome_3166" },
					},
					{
						PreLineWait = 0.3,
						ObjectType = "NPC_FurySister_01",
						-- Hmm.
						{ Cue = "/VO/MegaeraHome_0061" },
					},
				},
				{ Cue = "/VO/ZagreusHome_3164", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You're not too broken up about how things ended between you and Hypnos, are you, Meg?" },
				{ Cue = "/VO/MegaeraHome_0404",
					Text = "I'm still in a state of shock. I'll have to pick up the pieces somehow, and figure out how to get on with my existence." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutOrpheus01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift03" },
				RequiredAnyTextLines = { "OrpheusSingsAgain01", "OrpheusSingsAgain01_B", "OrpheusSingsAgain02", "OrpheusSingsAgain03", "OrpheusSingsAgain03_B" },
				RequiresAmbientMusicId = true,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- It's a change.
					{ Cue = "/VO/ZagreusHome_3177" },
				},
				{ Cue = "/VO/MegaeraHome_0417",
					Text = "You got that court musician to pipe up again. I know {#DialogueItalicFormat}you {#PreviousFormat}can't shut up, but didn't know it was contagious." },
				{ Cue = "/VO/ZagreusHome_3176", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, it was all Orpheus, not me. Can't sing like that without a lot of motivation, inner turmoil, who knows what else. Why, don't you like it? This lounge seems better with a little song." },
				{ Cue = "/VO/MegaeraHome_0418",
					Text = "This lounge is fine. And the singing... not used to it yet." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			MegaeraAboutSingersReunionQuestComplete01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraAboutOrpheus01", "OrpheusAboutSingersReunionQuest01" },
				AreIdsNotAlive = { 390000 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_FurySister_01",
					-- You always do.
					{ Cue = "/VO/MegaeraHome_0421" },
				},
				{ Cue = "/VO/MegaeraHome_0419",
					Text = "Not seen the court musician sulking over there quite as much lately. Your doing, I hear?" },
				{ Cue = "/VO/ZagreusHome_3178", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You'll just have to sulk for the both of you, I guess, then, Meg. But if you must know, on my authority I have released Orpheus from the terms of his old, rather unfair agreement with Father, so he can see his wife at least from time to time." },
				{ Cue = "/VO/MegaeraHome_0420",
					Text = "And Lord Hades is OK with you just picking out old binding contracts and nullifying them? You're exploiting your status." },
				{ Cue = "/VO/ZagreusHome_3179", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I'm doing no such thing. Father wanted me involved in all this business with his pacts, and presiding over the dead, then fine. But I'll do it my way." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraGhostAdminProgress01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift03" },
			 	RequiredFalseValues = { CurrentEmployeeOfTheMonth = "Zagreus" },
				RequiredMinAnyCosmetics =
				{
					Cosmetics = GameData.LoungeCosmetics,
					Count = 7,
					RequiredLifetimeResourcesSpentMin = { Gems = 800 },
				},
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Hoh now let's not get ahead of ourselves, Meg.
					{ Cue = "/VO/ZagreusHome_1380" },
				},
				{ Cue = "/VO/ZagreusHome_1378", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "What do you think of all the lounge decor, there, Meg? Looking a little better these days, wouldn't you say?" },
				{ Cue = "/VO/MegaeraHome_0160",
					Text = "You know, it isn't bad. Though I have heard the strangest thing, that you're responsible, for all of this? I couldn't understand why you would even bother, honestly." },
				{ Cue = "/VO/ZagreusHome_1379", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well, it's a popular locale for off-duty Underworld officers to take a load off between shifts, and so I figure... least I can do is pitch in and make the place a little better than I left it, right?" },
				{ Cue = "/VO/MegaeraHome_0161",
					Text = "How very selfless of you, Zagreus. Soon doubtless it'll be your portrait hanging on that wall back there." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			MegaeraGhostAdminProgress02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGhostAdminProgress01", "MegaeraGift07" },
			 	RequiredCosmetics = { "Cosmetic_Aquarium", "Cosmetic_BatCage", "Cosmetic_LoungeFireplace" },
			 	RequiredAnyCosmetics = { "Cosmetic_LoungeDiscoBall", "Cosmetic_LoungeDiscoBallA" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Good!
					{ Cue = "/VO/ZagreusHome_3181" },
				},
				{ Cue = "/VO/MegaeraHome_0422",
					Text = "The prism-sphere in the corner is maybe a little much. But the fireplace... the bat-cage... the aquarium... this lounge is practically comfortable. Lord Hades would strongly disapprove." },
				{ Cue = "/VO/ZagreusHome_3180", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Good thing he never sets foot in here, then, huh? Wait, you don't miss when things were all sad and dreary in here, do you?" },
				{ Cue = "/VO/MegaeraHome_0423",
					Text = "I'm still getting used to it. But, no." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutLegendary01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAssistKeepsake = "FuryAssistTrait",
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- That she is.
					{ Cue = "/VO/ZagreusHome_1397" },
				},
				{ Cue = "/VO/MegaeraHome_0174",
					Text = "How's my little Battie doing, Zag? The one I gave you. Was just a little pup. I figured she'd be better off with you. Please tell me I was right." },
				{ Cue = "/VO/ZagreusHome_1396", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Battie's doing great, Meg. Reminds me of you every time I think of her. Helps keep me company throughout my little jaunts outside. So you've a thing for bats, then?" },
				{ Cue = "/VO/MegaeraHome_0175", Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "That's good. And yes, the bats around here, they and I have always shared an understanding, you could say. I usually care for the pups myself. But Battie? She seemed more your style." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutDusaVsNyx01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "DusaWithNyx02", "ThanatosGift01", "CerberusGift01", "HypnosGift01", "AchillesGift01" },
				RequiredAnyTextLines = { "DusaWithMegaera01", "DusaWithMegaera02", "DusaWithMegaera03", "DusaWithMegaera04" },
				RequiredFalseFlags = { "DusaFiredFromJob" },
				RequiredFalseTextLines = { "DusaVsNyx04", "MegaeraAboutDusaVsNyx02" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- How is this my fault?
					{ Cue = "/VO/ZagreusHome_3142" },
				},
				{ Cue = "/VO/ZagreusHome_3140", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Meg, what's going on with Nyx and Dusa, do you know? I think they've been butting heads a bit lately." },
				{ Cue = "/VO/MegaeraHome_0373",
					AngleTowardHero = true,
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					Text = "I can't imagine why. Nyx has her under strict orders, yet you keep on stopping by for your little chats. You think she doesn't notice?" },
				{ Cue = "/VO/ZagreusHome_3141", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "What does she care? I talk to everybody around here at least a bit." },
				{ Cue = "/VO/MegaeraHome_0374",
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					AngleTowardTargetId = 423122,
					Text = "It's not my business, Zag. Not even for Dusa. But you watch yourself." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			MegaeraAboutDusaVsNyx02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredTextLines = { "DusaVsNyx04" },
				RequiredAnyTextLines = { "DusaWithMegaera01", "DusaWithMegaera02", "DusaWithMegaera03", "DusaWithMegaera04" },
