					{ Cue = "/VO/ZagreusHome_3144" },
				},
				{ Cue = "/VO/MegaeraHome_0375",
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					Text = "Damn you, Zagreus. She was my friend. And now she's gone." },
				{ Cue = "/VO/ZagreusHome_3143", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Who, you mean Dusa? Like I had anything to do with that. It's Nyx you should be cross with, not me!" },
				{ Cue = "/VO/MegaeraHome_0376",
					AngleTowardHero = true,
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					Text = "I don't think so. Nyx warned you. {#DialogueItalicFormat}Ungh{#PreviousFormat}, you know I can't go to her myself about this, so {#DialogueItalicFormat}fix it{#PreviousFormat}!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},
			MegaeraAboutDusaVsNyx03 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "DusaWithNyx03" },
				RequiredAnyTextLines = { "DusaWithMegaera01", "DusaWithMegaera02", "DusaWithMegaera03", "DusaWithMegaera04" },
				MaxRunsSinceAnyTextLines = { TextLines = { "DusaWithNyx03" }, Count = 15 },
				EndVoiceLines =
				{
					{
						PreLineWait = 0.4,
						ObjectType = "NPC_FurySister_01",
						-- This never should have happened, Zagreus.
						{ Cue = "/VO/MegaeraHome_0378", PreLineAnim = "FuryIdleInHouseFidgetGreeting" },
					},
				},
				{ Cue = "/VO/ZagreusHome_3145", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You saw Dusa's back, right, Meg? She's getting another chance. Nyx reconsidered!" },
				{ Cue = "/VO/MegaeraHome_0377",
					Text = "I saw. Nyx doesn't often change her mind when it comes to this House. But I won't second guess what happened or why." },
				{ Cue = "/VO/ZagreusHome_3146", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "You don't sound all that pleased..." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutDusaPromotion01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "DusaWithNyx04" },
				RequiredAnyTextLines = { "DusaWithMegaera01", "DusaWithMegaera02", "DusaWithMegaera03", "DusaWithMegaera04" },
				MaxRunsSinceAnyTextLines = { TextLines = { "DusaWithNyx04" }, Count = 20 },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Of course.
					{ Cue = "/VO/ZagreusHome_3149" },
				},
				{ Cue = "/VO/ZagreusHome_3147", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I'm so proud of Dusa lately, Meg! Chief Custodian of the House, was it? That's quite an honor coming from Nyx." },
				{ Cue = "/VO/MegaeraHome_0379",
					Text = "I thought all the added responsibility would stress Dusa out worse than usual. But she's taking it in stride." },
				{ Cue = "/VO/ZagreusHome_3148", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You're always looking out for her. Thanks for that." },
				{ Cue = "/VO/MegaeraHome_0380",
					Text = "Not doing it for you. Though... thanks for looking out for her, yourself." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutDusaLegendary01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredUsedAssistInRoomThisRun = "A_Boss01",
				RequiredAssistKeepsake = "DusaAssistTrait",
				RequiredAnyPlayedThisRun = { "/VO/ZagreusField_3006", "/VO/ZagreusField_3007", "/VO/ZagreusField_3008", "/VO/ZagreusField_3009", "/VO/ZagreusField_3010", "/VO/ZagreusField_3011" },
				RequiredFalseFlags = { "DusaFiredFromJob", "DusaNotYetReHired" },
				RequiredTextLines = { "MegaeraGift05" },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					UsePlayerSource = true,
					-- Appreciate your open-mindedness.
					{ Cue = "/VO/ZagreusHome_3139" },
				},
				{ Cue = "/VO/MegaeraHome_0371",
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					Text = "I did not appreciate you sending Dusa after me out there, Zag. You leave her out of this! Though... it was good to see her out of this damned House for a change." },
				{ Cue = "/VO/ZagreusHome_3138", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "See, I, too, thought she could use the fresh air. Or... however you'd describe the quality of the air on the edge of Tartarus. You know she'd never mean you any harm, right? In her mind... I think she's trying to keep it friendly between us." },
				{ Cue = "/VO/MegaeraHome_0372", Portrait = "Portrait_FurySister01_Pleased_01",
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					Text = "Eugh, she said the same thing to me. Look, you know what, fine. If that's the only way to get her to take a break from this place, I guess it's for the best." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutSisyphus01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift03", "SisyphusBackstory03", },
				RequiredFalseTextLines = { "MegaeraAboutSisyphusLiberationQuest01" },
				{ Cue = "/VO/ZagreusHome_0763", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Meg, I understand you're under orders to kill me on sight out there, but... do you really have to go so hard on Sisyphus?" },
				{ Cue = "/VO/MegaeraHome_0095",
					Text = "That wretched shade isn't who you think he is. Do you have any idea of the magnitude of his crimes from when he led a mortal life?" },
				{ Cue = "/VO/ZagreusHome_0764", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "He cheated death. Tricked Thanatos and maybe Father, too." },
				{ Cue = "/VO/MegaeraHome_0096",
					Text = "Before that, he did worse. He was an awful man. I think that is the real reason that your father punished him." },
				{ Cue = "/VO/ZagreusHome_0765", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag", PreLineWait = 0.3,
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "What if he's changed? Or... is that impossible. No coming back from certain crimes." },
				{ Cue = "/VO/MegaeraHome_0097",
					Text = "No coming back from certain crimes, indeed. Though you may have more mercy in your heart to spare than I in mine." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			MegaeraAboutSisyphusLiberationQuest01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift05", "SisyphusLiberationQuest_Beginning_01", },
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- It's a deal.
					{ Cue = "/VO/ZagreusHome_1391" },
				},
				{ Cue = "/VO/ZagreusHome_1389", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I have to ask you for a favor, Meg. And I'm willing to make it worth your while, if you'll hear me out." },
				{ Cue = "/VO/MegaeraHome_0169",
					Text = "A proposition, Zagreus? Let's hear it, then. While I'm in a charitable mood." },
				{ Cue = "/VO/ZagreusHome_1390", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It's about Sisyphus. If I can get my father to alleviate his sentence, would you just... ease off of him, a little bit? You and your sisters. I think he's suffered quite enough under your whips." },
				{ Cue = "/VO/MegaeraHome_0170",
					Text = "I don't know what you see in him, I swear. But you don't need to ask such things of me. If you can overturn his sentence with Lord Hades, I'd like nothing better than to never see that miserable shade again." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.Sulking,
			},

			-- alt below
			MegaeraBuildingTrust01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift06", "MegaeraBedroom01" },
				RequiredFalseTextLines = { "Ending01", "MegaeraPostEnding01", "MegaeraBuildingTrust01_B" },
