			MegaeraAboutBeingCloseWithThanatos01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "BecameCloseWithMegaera01Meg_GoToHer", "BecameCloseWithThanatos01_BThan_GoToHim" },
				RequiredFalseTextLines = { "MegaeraWithThanatosBedroom01" },
				EndVoiceLines =
				{
					PreLineWait = 0.5,
					UsePlayerSource = true,
					-- I will. Thanks, Meg.
					{ Cue = "/VO/ZagreusHome_1395" },
				},
				{ Cue = "/VO/ZagreusHome_1392", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Meg, you know what's going on with Than and me, don't you? I just... want things to be out in the open, between us." },
				{ Cue = "/VO/MegaeraHome_0171", Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "Oh I know everything about you, Zag. And Thanatos, maybe even better. Though I appreciate you telling me. It's fine." },
				{ Cue = "/VO/ZagreusHome_1393", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It's fine? I mean... upon Olympus, jealousy runs rampant, as I'm told. But here it's not even an issue, then?" },
				{ Cue = "/VO/MegaeraHome_0172", Portrait = "Portrait_FurySister01_Pleased_01",
					AngleTowardHero = true,
					Text = "If you prefer the way they do things on Olympus, then I think you know the way. As for me... dealing with jealousy is what I do. Tell me, should I feel threatened by Thanatos?" },
				{ Cue = "/VO/ZagreusHome_1394", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "No... he thinks so highly of you, he... he'd never wish to cause you any harm." },
				{ Cue = "/VO/MegaeraHome_0173", Portrait = "Portrait_FurySister01_Pleased_01",
					AngleTowardTargetId = 423122,
					Text = "And I feel much the same. He and I, we're not the same to you. I think we both see that, and understand. Now quit your second-guessing me, all right?" },
			},

