			 	RequiredFalseValues = { CurrentEmployeeOfTheMonth = "Megaera" },
				PlayOnce = true,
				MinRunsSinceAnyTextLines = { TextLines = GameData.HadesMiscPartnerTextLines, Count = 4 },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				-- Sorry, Meg.
				EndCue = "/VO/ZagreusHome_0774",
				EndWait = 0.6,
				{ Cue = "/VO/MegaeraHome_0085", PreLineWait = 0.35,
					Text = "...I cannot work with them, Lord Hades. Please. They don't respect my words, or even listen." },
				{ Cue = "/VO/Hades_0352", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "{#DialogueItalicFormat}Enough{#PreviousFormat}! Besides, Megaera, this is not your failing. It is mine. I must attend to these affairs more actively." },
				{ Cue = "/VO/MegaeraHome_0086", PreLineWait = 0.35,
					Text = "...I've often tried, but never to a satisfactory result. You're asking the impossible of me." },
				{ Cue = "/VO/Hades_0353", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "I am not asking, Megaera." },
			},

			MegaeraWithHades04 =
			{
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "MegaeraWithHades03", "Ending01" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 5 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = -485,
				TeleportOffsetY = 350,
				AngleTowardTargetId = 370006,
				InteractDistance = 400,
				{ Cue = "/VO/MegaeraHome_0355", PreLineWait = 0.35,
					Text = "...But, I don't understand, Lord Hades. I have fallen to him countless times. How can this be...?" },
				{ Cue = "/VO/Hades_1180", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "You and your sisters have protected Tartarus unerringly. The Prince has proven uncontainable. The fault is mine, not yours. Your duty to this realm must be commended." },
				{ Cue = "/VO/MegaeraHome_0356",
					Text = "You honor me... I have done nothing to deserve this. There must be something more that I can do?" },
				{ Cue = "/VO/Hades_1181", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "There is, and isn't, Megaera. I ask only that you remain committed to this realm. Bar passage in and out of Tartarus. And, if the Prince continues to show up... you give him hell." },
				{ Cue = "/VO/MegaeraHome_0357", PreLineWait = 0.35,
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "Lord Hades, it will be my pleasure, sir." },
			},

			MegaeraWithNyx01 =
			{
				Partner = "NPC_Nyx_01",
				PlayOnce = true,
				RequiredAnyTextLines = { "MegaeraMeeting01", "MegaeraMeeting01_B", "MegaeraMeeting01_Alt", "MegaeraMeeting01_Alt_B" },
				RequiredMinNPCInteractions = { NPC_Nyx_01 = 1 },
				RequiredFalseTextLines = { "Fury2FirstAppearance", "Fury3FirstAppearance" },
				MinRunsSinceAnyTextLines = { TextLines = { "NyxAboutDusa04" }, Count = 4 },
				RequiredRoomThisRun = "B_Intro",
				RequiredKillsThisRun = { "Harpy" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -50,
				TeleportOffsetY = 70,
				StatusAnimation = false,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				-- What was that about.
				EndCue = "/VO/ZagreusHome_0493",
				EndWait = 0.6,
				{ Cue = "/VO/Nyx_0105", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01", PreLineWait = 0.35,
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "...We each have our commitments to this House, Megaera. There is no shame in yours. And whilst your sisters are away, you bear their burden of responsibility, as well." },
				{ Cue = "/VO/MegaeraHome_0011",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "I understand, it's just... am I supposed to keep pretending nothing's changed? This House is cracking at the seams, and no one's going to admit it?" },
				{ Cue = "/VO/Nyx_0075",
					Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					Text = "The House shall stand eternally, although in what condition, it is ours to decide. Our obligation is to keep the House in order." },
				{ Cue = "/VO/MegaeraHome_0012",
					Text = "I know. You're right of course, you're right... I'll do my best. I'll do my best for both our sakes, Nyx." },
			},
			MegaeraWithNyx02 =
			{
				Partner = "NPC_Nyx_01",
				StatusAnimation = false,
				RequiredAnyTextLines = { "MegaeraMeeting01", "MegaeraMeeting01_B", "MegaeraMeeting01_Alt", "MegaeraMeeting01_Alt_B" },
				RequiredTextLines = { "NyxFirstMeeting", "MegaeraGift03", "NyxGift01" },
				MinRunsSinceAnyTextLines = { TextLines = { "NyxAboutDusa04" }, Count = 4 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -50,
				TeleportOffsetY = 70,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.4,
						ObjectType = "NPC_Nyx_01",
						-- ...We all do that which is within our power.
						{ Cue = "/VO/Nyx_0107" },
					},
					{
						PreLineWait = 0.45,
						UsePlayerSource = true,
						-- ...Not my business.
						{ Cue = "/VO/ZagreusHome_0494" },
					},
				},
				{ Cue = "/VO/Nyx_0106", PreLineWait = 0.35, Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "...and so, who else is there to be depended on...? You are a trusted overseer, Megaera. Your station is no accident and there is no one better fit for what you do. I urge you to look inwardly. How you approach your work is vital to the end result." },
				{ Cue = "/VO/MegaeraHome_0013", PreLineWait = 0.35,
					Text = "...Thank you for the advice, Nyx. I don't know what I'd do without you. Or what this House would be if not for you." },
			},
			MegaeraWithNyx03 =
			{
				Partner = "NPC_Nyx_01",
				RequiredTextLines = { "MegaeraMirrorProgress01" },
				RequiredFalseTextLines = { "MegaeraBedroom02", "MegaeraBedroom02B", "Ending01" },
				MinRunsSinceAnyTextLines = { TextLines = { "NyxAboutDusa04" }, Count = 4 },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				PlayOnce = true,
				UseableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -50,
				TeleportOffsetY = 70,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.4,
						ObjectType = "NPC_FurySister_01",
						-- <Scoff>
						{ Cue = "/VO/MegaeraHome_0325" },
					},
				},
				{ Cue = "/VO/MegaeraHome_0120", PreLineWait = 0.35,
					Text = "...But, how can you do this to me? Blast your Mirror. By taking sides with him, you're standing against me...!" },
				{ Cue = "/VO/Nyx_0130", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "Neutrality ceases to be an option when a crisis rises to a certain point, Megaera. You, yourself, shall have to choose allegiances. I have steadfastly told you this throughout our shared ordeal." },
				{ Cue = "/VO/MegaeraHome_0121",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "I thought that both of our allegiances were to this House, Nyx. It seems I was mistaken. Do you have any idea what sort of pressure I am under here...?" },
				{ Cue = "/VO/Nyx_0131",Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					PreLineThreadedFunctionName = "PowerWordPresentation", PreLineThreadedFunctionArgs = { WaitTime = 2.2 },
					Text = "Use caution with the tone you take with me, Overseer. I am the mother of the Fates, themselves. I do not act by whim or by mistake. Mind your own station, and perform your role. All shall unfold eventually as it must. I like the intervening time no more than you." },
			},
			MegaeraWithNyx04 =
			{
				Partner = "NPC_Nyx_01",
				RequiredTextLines = { "MegaeraGift09" },
				RequiredFalseTextLinesThisRun = { "Ending01" },
				MinRunsSinceAnyTextLines = { TextLines = { "NyxAboutDusa04" }, Count = 4 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -50,
				TeleportOffsetY = 70,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.35,
						ObjectType = "NPC_Nyx_01",
						-- Mmm.
						{ Cue = "/VO/Nyx_0255" },
					},
				},
				{ Cue = "/VO/MegaeraHome_0182", PreLineWait = 0.35,
					Text = "...Nyx, you'd tell me if I made a terrible mistake, or even just a bad one, wouldn't you? I need your guidance on this one, or maybe your approval." },
				{ Cue = "/VO/Nyx_0134", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "You shall always be the best determiner of what is right about such matters, Megaera. Though, if you seek my blessings or encouragement in this particular pursuit, please rest assured... you have them both. You know the way, from here." },
				{ Cue = "/VO/MegaeraHome_0183",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "I hope that you are right. You always have been, it's just that... I wish I had your confidence in this." },
			},
			MegaeraWithNyx05 =
			{
				Partner = "NPC_Nyx_01",
				RequiredTextLines = { "MegaeraWithNyx02", "Ending01" },
				RequiredFalseTextLinesThisRun = { "Ending01" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01", "NyxAboutDusa04" }, Count = 3 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -50,
				TeleportOffsetY = 70,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.35,
						ObjectType = "NPC_Nyx_01",
						-- I do know where to find you, Megaera.
						{ Cue = "/VO/Nyx_0133" },
					},
				},
				{ Cue = "/VO/MegaeraHome_0180", PreLineWait = 0.35,
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "...I think I understand, now, Nyx. You've been here longer than the rest of us combined. You've seen this House go through a lot. Even before Lord Hades took his place. I never should have doubted you." },
				{ Cue = "/VO/Nyx_0132", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "To doubt is an important instinct, Megaera. Without it, we could not conceive of better circumstances than the ones we know. Besides... I am the Night. Others shall always doubt me. You may doubt me again. I ask only that you heed well your intuitions, from now on." },
				{ Cue = "/VO/MegaeraHome_0181",
					Text = "I will. I promise, Nyx. And if there's anything you ever need that I can do, well... please, just ask of me." },
			},
			MegaeraWithNyx06 =
			{
				Partner = "NPC_Nyx_01",
				RequiredTextLines = { "MegaeraWithNyx02", "FurySistersUnion01" },
				RequiredFalseTextLinesThisRun = { "Ending01" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01", "NyxAboutDusa04" }, Count = 3 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -50,
				TeleportOffsetY = 70,
				AngleTowardTargetId = 370010,
				InteractDistance = 375,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.6,
						ObjectType = "NPC_FurySister_01",
						-- I understand.
						{ Cue = "/VO/MegaeraHome_0252" },
					},
				},
				{ Cue = "/VO/MegaeraHome_0081", PreLineWait = 0.35,
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "...They can't be reasoned with, Nyx. You know that. What use are they to us, trying to manage this? They'll make things worse." },
				{ Cue = "/VO/Nyx_0423", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					Text = "They are your sisters. They are part of your life, whether you like it or not. Your relationship with them is yours to mold." },
				{ Cue = "/VO/MegaeraHome_0082",
					Text = "I'm afraid there isn't much for me to work with. You have a lot more faith in them than I do." },
				{ Cue = "/VO/Nyx_0424", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01",
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "I have a lot more faith in {#DialogueItalicFormat}you{#PreviousFormat}, Megaera. There are many obstinate personalities with whom we regularly interact. Thus, we do what is possible." },						
			},

			MegaeraWithHypnos01 =
			{
				Partner = "NPC_Hypnos_01",
				RequiredTextLines = { "HypnosGift01", "MegaeraGift01", },
				RequiredAnyTextLines = { "MegaeraMeeting01", "MegaeraMeeting01_B", "MegaeraMeeting01_Alt", "MegaeraMeeting01_Alt_B" },
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredMinCompletedRuns = 13,
				RequiredKills = { Harpy = 8 },
				RequiredKillsThisRun = { "Harpy" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				TeleportToId = 370024,
				TeleportOffsetX = 100,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				-- Great.
				EndCue = "/VO/ZagreusField_0747",
				EndWait = 0.55,
				{ Cue = "/VO/MegaeraHome_0112", PreLineWait = 0.35,
					Text = "...Would you look at that, Hypnos. Care to repeat to me the unsolicited advice you just now offered, now that Zagreus is here?" },
				{ Cue = "/VO/Hypnos_0114", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024,
					Text = "Oh, {#DialogueItalicFormat}hahaha{#PreviousFormat}, well, it is my custom to give pointers to our visitors, who come involuntarily, because, I do pick up a lot of bits and pieces, talking here to everyone! About, you know... dying?" },
				{ Cue = "/VO/MegaeraHome_0113",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "Zagreus. He told me that I ought to evade more of your attacks next time. So you just watch your back out there, because I know your secrets thanks to Hypnos here." },
			},
			MegaeraWithHypnos02 =
			{
				Partner = "NPC_Hypnos_01",
				RequiredAnyTextLines = { "MegaeraWithHypnos01", "HypnosConsolation04", },
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				MinRunsSinceAnyTextLines = { TextLines = { "MegaeraWithHypnos01" }, Count = 10 },
				RequiredMinCompletedRuns = 25,
				RequiredKillsThisRun = { "Harpy" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				TeleportToId = 370024,
				TeleportOffsetX = 100,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				EndVoiceLines =
				{
					{
						PreLineWait = 0.35,
						ObjectType = "NPC_Hypnos_01",
						PreLineAnim = "HypnosIdleGreeting",
						-- I'll give it a shot!
						{ Cue = "/VO/Hypnos_0059" },
					},
					{
						PreLineWait = 0.45,
						UsePlayerSource = true,
						-- ...Alas.
						{ Cue = "/VO/ZagreusHome_0742" },
					},
				},
				{ Cue = "/VO/MegaeraHome_0114", PreLineWait = 0.35,
					Text = "...I think that I can find the lounge well on my own, Hypnos. Or... wait... what is this, did you just ask me out?" },
				{ Cue = "/VO/Hypnos_0115", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024,
					Text = "Me? No, {#DialogueItalicFormat}nonononono{#PreviousFormat}, no! I'd never even think to do that, Miss, I mean, you have your station, and I have mine. We're from two different worlds, you could say. Fated never to be seen together much." },
				{ Cue = "/VO/MegaeraHome_0115",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "Every time I talk to you, you somehow take it as a compliment, is that correct? Then, let me give {#DialogueItalicFormat}you {#PreviousFormat}some advice, for once. Stop being an idiot." },
			},
			MegaeraWithHypnos03 =
			{
				Partner = "NPC_Hypnos_01",
				RequiredTextLines = { "MegaeraWithHypnos02", "HypnosGift04", "MegaeraGift05" },
				MinRunsSinceAnyTextLines = { TextLines = { "MegaeraWithHypnos02" }, Count = 12 },
				PlayOnce = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				RequiredKillsThisRun = { "Harpy" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				TeleportToId = 370024,
				TeleportOffsetX = 100,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 370024,
				EndVoiceLines =
				{
					PreLineWait = 0.35,
					ObjectType = "NPC_Hypnos_01",
					-- ...Will do!
					{ Cue = "/VO/Hypnos_0183" },
				},
				{ Cue = "/VO/MegaeraHome_0176", PreLineWait = 0.35,
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					Text = "...You haven't greeted me as often lately, Hypnos. I wouldn't have noticed if it weren't half your job. If there's something you wish to say to me, why not just come right out." },
				{ Cue = "/VO/Hypnos_0180", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024,
					Text = "Oh, um, well, I, see, the thing is, Megaera, Miss Megaera, mind if I call you that? As I was saying, I just, thought maybe, you know, we had a thing, a real connection, lately, since you keep on dying more and more, and I keep standing here? But then... I always thought you were invincible..." },
				{ Cue = "/VO/MegaeraHome_0177",
					PostLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					Text = "I see. And so you're realizing now that your entire image of me came from your imagination, is that it?" },
				{ Cue = "/VO/Hypnos_0181", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteFiredUp",
					Text = "Well, it's just! I really thought with all my advice that you'd go back to killing Zagreus, rather than the other way around! I just don't think things are working out the way I hoped, you know?" },
				{ Cue = "/VO/MegaeraHome_0178",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",
					Text = "It almost sounds as though you've broken up with me, except... well, are you sure that you'll be all right?" },
				{ Cue = "/VO/Hypnos_0182", Portrait = "Portrait_ChildGhost_Default_01", Speaker = "NPC_Hypnos_01",
					Emote = "PortraitEmoteCheerful",
					PreLineAnim = "HypnosIdleGreeting", PreLineAnimTarget = 370024,
					Text = "Oh, yeah! Look, I'm just glad it's one of you two, sure beats having to greet shades nonstop! They never talk to me." },
				{ Cue = "/VO/MegaeraHome_0179", Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "All right, then... you greet me next I'm here." },
			},

			MegaeraWithPersephone01 =
			{
				Partner = "NPC_Persephone_Home_01",
				RequiredTextLines = { "Ending01", "PersephoneHomeMeeting01" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 5 },
				MaxRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 28 },
				PlayOnce = true,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				GiftableOffSource = true,
				TeleportToId = 555714,
				TeleportOffsetX = -40,
				TeleportOffsetY = 100,
				AngleTowardTargetId = 555714,
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					ObjectType = "NPC_FurySister_01",
					-- Heh. As you say.
					{ Cue = "/VO/MegaeraHome_0416" },
				},				
				{ Cue = "/VO/MegaeraHome_0414", PreLineWait = 0.35,
					Text = "...It was my duty to prevent him from getting farther. If I succeeded... he never would have found you. So this once, I'm glad I failed. I hope you know that, my Queen." },
				{ Cue = "/VO/Persephone_0193", Portrait = "Portrait_Persephone_Queen_01", Speaker = "NPC_Persephone_Home_01",
					PreLineAnim = "PersephoneUnderworld_Vulnerable", PreLineAnimTarget = 555714,
					Text = "I hoped, but did not know for certain till I heard from you just now, Overseer Megaera! And, if I may? I understand you have achieved an awful lot since I took my, {#DialogueItalicFormat}erm{#PreviousFormat}, leave of absence from this House. I see why Nyx and Hades put their trust in you!" },
				{ Cue = "/VO/MegaeraHome_0415",
					PreLineAnim = "FuryIdleInHouseFidgetGreeting",	
					Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "Thank you, my Queen. Then, if there's anything else that I can do to help you reacclimate yourself, I'm always at your service. You are most welcome, here." },
				{ Cue = "/VO/Persephone_0194", Portrait = "Portrait_Persephone_Queen_01", Speaker = "NPC_Persephone_Home_01",
					PreLineAnim = "PersephoneUnderworld_Dismiss", PreLineAnimTarget = 555714,
					Text = "Likewise, you be sure to let me know if my son there happens to make more trouble for you than you welcome in your life. All right?" },
			},

			DusaWithMegaera01 =
			{
				PlayOnce = true,
