		GiftTextLineSets =
		{
			-- grants a gift
			MegaeraGift01 =
			{
				PlayOnce = true,
				{ Cue = "/VO/ZagreusHome_0289", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, {#DialogueItalicFormat}uh{#PreviousFormat}, Meg? Look, I know you're just doing your job out there, and, anyway, found this, thought of you and all that, so... here." },
				{ Cue = "/VO/MegaeraHome_0051",
					PreLineWait = 0.4,
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					PostLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					Text = "I... I'll see to it this contraband is put away at once. Now, take your finder's fee and go about your business." },
			},
			MegaeraGift02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "MegaeraGift01", },
				{ Cue = "/VO/ZagreusHome_0117", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, Meg! I, {#DialogueItalicFormat}uh{#PreviousFormat}, thought I'd better hand this over." },
				{ Cue = "/VO/MegaeraHome_0045",
					PreLineAnim = "FuryIdleInHouseFidgetWhipTaunt_Start",
					Text = "I'll confiscate that if you really want. Although, I'm warning you, I'm not susceptible to bribes. Many have tried." },
				{ Cue = "/VO/ZagreusHome_0290", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PostLineAnim = "FuryIdleInHouseFidgetWhipTaunt_ReturnToIdle",
					Text = "Hey I'm just handing over contraband to the appropriate authorities, think nothing of it, Meg. Just doing our jobs, right?" },
			},
			MegaeraGift03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "MegaeraGift02", },
				{ Cue = "/VO/MegaeraHome_0046",
					Text = "{#DialogueItalicFormat}Tsch{#PreviousFormat}. You're serious. You're really giving that to me. I mean, I'll take it off your hands if that is really what you want." },
				{ Cue = "/VO/ZagreusHome_0291", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's really what I want. And, I sincerely hope we can move past our differences. Not that turning in this contraband has anything to do with that." },
			},
