			{
				PlayOnce = true,
				RequiredTextLines = { "MegaeraGift03", },
				EndVoiceLines =
				{
					PreLineWait = 0.45,
					ObjectType = "NPC_FurySister_01",
					-- ...I hope you do.
					{ Cue = "/VO/MegaeraHome_0368" },
				},
				{ Cue = "/VO/MegaeraHome_0047",
					Text = "Such gifts won't stay my whip when next we meet out there, you realize that, right?" },
				{ Cue = "/VO/ZagreusHome_0292", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "I realize it. And I hope you never change, Meg." },
			},
			MegaeraGift05 =
			{
				PlayOnce = true,
				RequiredTextLines = { "MegaeraGift04", },
				{ Cue = "/VO/MegaeraHome_0048",
					Text = "Flattery won't get you anywhere. We can't go back to how things used to be." },
				{ Cue = "/VO/ZagreusHome_0293", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Flattery never got me anywhere with you to begin with, Meg. Doesn't mean I won't keep trying. I am ever stubborn, aren't I." },
			},
			MegaeraGift06 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift05", },
				{ Cue = "/VO/ZagreusHome_0294", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Got you some more of that contraband you like. Don't suppose I could help you put it away or anything...?" },
