				{ Cue = "/VO/MegaeraHome_0141",
					AngleTowardHero = true,
					Text = "{#DialogueItalicFormat}Tsch{#PreviousFormat}... you still have no idea how to be up front with me, do you, Zag. Why don't you tell me why you're here, and what you want." },
				{ Cue = "/VO/ZagreusHome_1358", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Erm{#PreviousFormat}... so the whole contraband thing, you aren't buying it, I guess? Although I see you took the bottle there, now, anyway... I wanted just to talk to you. And patch things up, maybe." },
				{ Cue = "/VO/MegaeraHome_0142",
					Text = "{#DialogueItalicFormat}Patch things up{#PreviousFormat}. What is it, by your estimation, that could use repairs, between the two of us, {#DialogueItalicFormat}hm{#PreviousFormat}?" },
				{ Cue = "/VO/ZagreusHome_1359", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "I... I was naive. I tried not to get too close, but then I did, and said a lot of stupid things, and then... well, we've since tried to kill each other lots of times, there's that. Look, I am sorry, Meg." },
				{ Cue = "/VO/MegaeraHome_0143",
					AngleTowardTargetId = 423122,
					Text = "You said a lot of stupid things, all right. And we Furies, we're not much for forgiveness or apologies. But... I can see you're making an attempt. Now, thank you for the contraband. Good-bye." },
			},

			MegaeraGift09 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift08", },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- I see. Take all the time you need.
					{ Cue = "/VO/ZagreusHome_1364" },
				},
				{ Cue = "/VO/ZagreusHome_1361", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Here, Meg. Contraband. I wish I could do over how we started." },
				{ Cue = "/VO/MegaeraHome_0144",
					Text = "I, too, wish for a lot of things. Unfortunately there's no unraveling the Fates' patterns. We can learn from our mistakes or keep repeating them. That's it." },
				{ Cue = "/VO/ZagreusHome_1362", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "What if I've learned from my mistakes? Have I ever done anything to hurt you since we've been like this? Discounting the unavoidable and frequent fights to the death." },
				{ Cue = "/VO/MegaeraHome_0145",
					AngleTowardHero = true,
					Text = "There's nothing you can do to hurt me, Zag. It's just a question of whether you're worth my time." },
				{ Cue = "/VO/ZagreusHome_1363", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well... am I worth your time, then, Meg?" },
				{ Cue = "/VO/MegaeraHome_0146",
					AngleTowardTargetId = 423122,
					Text = "You know... let me get back to you on that. I need a little time to think it through." },
			},

			MegaeraGift10 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MegaeraGift09", },
				EndVoiceLines =
				{
					PreLineWait = 0.65,
					UsePlayerSource = true,
					-- Yeah. Thanks.
					{ Cue = "/VO/ZagreusHome_1369" },
				},
				{ Cue = "/VO/ZagreusHome_1365", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey Meg, you're off duty, aren't you? Look, I was just... hoping you might want to split this with me, best stuff I could find out there, and I would give it all to you, it's just... maybe you wouldn't mind some company. From me." },
				{ Cue = "/VO/MegaeraHome_0147",
					AngleTowardHero = true,
					Text = "You sound a little tongue-tied, Zagreus. Just like you always used to around me." },
				{ Cue = "/VO/ZagreusHome_1366", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "You're not exactly easy to approach, you know. So... how about it, then? Care for a drink with me?" },
				{ Cue = "/VO/MegaeraHome_0148",
					AngleTowardTargetId = 423122,
					PostLineThreadedFunctionName = "LoungeRevelryPresentation",
					PortraitExitAnimation = "Portrait_FurySister01_Standoffish_01_Exit",
					Text = "Open it up, and we can go from there." },
				-- intermission
				{ Cue = "/VO/MegaeraHome_0149",
					FadeOutTime = 0.5, FullFadeTime = 7.8, FadeInTime = 2.0, PreLineWait = 0.3,
					FadeInSound = "/SFX/Menu Sounds/PortraitEmoteAffectionSFX",
					InterSceneWaitTime = 0.5,
					Portrait = "Portrait_FurySister01_Pleased_01",
					Text = "...You should have seen the look on your face. You act a lot more confidently now. Though, I think, deep down, you are still that inexperienced little godling that you used to be." },
				{ Cue = "/VO/ZagreusHome_1367", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "And I think, deep down, you are not the heartless harbinger of retribution that you want for everyone to think you are. But I can keep your secret if you're willing to keep mine, what do you say?" },
				{ Cue = "/VO/MegaeraHome_0150",
					AngleTowardHero = true,
					Text = "Tsch... that sounds an awful lot like blackmail, Zagreus. You'd stoop to that, with me?" },
				{ Cue = "/VO/ZagreusHome_1368", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "No, I... I was just, that's not what I meant at all..." },
				{ Cue = "/VO/MegaeraHome_0151",
					AngleTowardTargetId = 423122,
					Portrait = "Portrait_FurySister01_Pleased_01",
					PostLineThreadedFunctionName = "MaxedRelationshipPresentation",
					PostLineFunctionArgs = { Text = "NPC_FurySister_01", Icon = "Keepsake_MegSticker_Max" },
					Text = "{#DialogueItalicFormat}Hahaha{#PreviousFormat}, I just want to see you squirm. Thanks for the company and for the drink. Guess I'll be seeing you around." },
			},

		},

