				RequiredAnyTextLines = { "ThanatosWithSisyphus02_FollowUp", "SisyphusLiberationQuestComplete" },
				MinRunsSinceAnyTextLines = { TextLines = { "ThanatosWithSisyphus02_FollowUp", "SisyphusLiberationQuestComplete" }, Count = 5 },
				{ Cue = "/VO/Sisyphus_0177",
					Text = "Prince Z.! Just missed your stern-faced colleague, feel that stillness hanging in the air? Sounds like he won't be checking up on me again anytime soon." },
				{ Cue = "/VO/ZagreusField_2833", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Thanatos really isn't such a bad sort once you get to know him, sir. Though, I suppose you'll have to take my word for that." },
