				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusFirstMeeting" },
				RequiredFalseTextLines = { "PatroclusAboutAchilles01B", "PatroclusMeeting02B" },
				RequiredCodexEntry =
				{
				  EntryName = "NPC_Patroclus_01",
				  EntryIndex = 1,
				},
				{ Cue = "/VO/ZagreusField_1705", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Is something wrong, there, sir? I mean, shouldn't you be up and about, competing for eternal pride and glory and all that?" },
				{ Cue = "/VO/Patroclus_0105",
					Text = "What's the use? Let's say we fought, if anybody asks. Now, go. I've no quarrel with you, besides. I'll just remain here, comfortably at rest, for some untold millennia, I guess. Have a nice... whatever time it is." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusMeeting02B =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusAboutBracer01C", "PatroclusAboutBracer01D" },
				RequiredFalseTextLines = { "PatroclusAboutAchilles01B", "PatroclusMeeting02" },
				{ Cue = "/VO/ZagreusField_1705", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Is something wrong, there, sir? I mean, shouldn't you be up and about, competing for eternal pride and glory and all that?" },
				{ Cue = "/VO/Patroclus_0105",
					Text = "What's the use? Let's say we fought, if anybody asks, now go. I've no quarrel with you, besides. I'll just remain here, comfortably at rest, for some untold millennia, I guess. Have a nice... whatever time it is." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusMeeting03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusMeeting02", "PatroclusMeeting02B" },
				RequiredFalseTextLines = { "PatroclusAboutAchilles01B", "PatroclusAboutMyrmidonReunionQuest01" },
				{ Cue = "/VO/Patroclus_0106",
					Text = "Still toiling around within Elysium, I see. Battling my brethren, in the name of glory, is that it?" },
				{ Cue = "/VO/ZagreusField_1706", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I'm heading for the surface. Though your brethren are slowing me down. Whether for glory or at my father's behest." },
				{ Cue = "/VO/Patroclus_0107",
					Text = "Your father...? {#DialogueItalicFormat}Ah{#PreviousFormat}, that's right, but you're the Prince, aren't you. You must see plainly, then, what your birthright amounts to: You're no better off than any of us here. Now, go." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusMeeting04 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusMeeting03", "PatroclusAboutAchilles01B", "PatroclusAboutAchilles01C_01", "PatroclusAboutAchilles01C_02" },
				RequiredFalseTextLines = { "PatroclusAboutAchilles01A", "PatroclusAboutAchilles06", "PatroclusMeeting06B" },
				{ Cue = "/VO/Patroclus_0108",
					Text = "You again. I told you to stay clear of me. Your father's quite the big shot around here, but that means nothing to me, understand? Go, and tell him he can cast me in the pits of Tartarus, for all I care. It'd be a welcome change of pace from being put to pasture here." },
				{
					Text = "Patroclus_OfferText05",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusGreetings,
			},
			PatroclusMeeting05 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusMeeting04" },
				RequiredFalseTextLines = { "PatroclusAboutAchilles06", "PatroclusMeeting06B" },
				{ Cue = "/VO/Patroclus_0109",
					Text = "I was unkind last time, stranger. Forgive my indiscretions there. Or don't. But I wished to apologize." },
				{ Cue = "/VO/ZagreusField_1713", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Think nothing of it, sir. Though if there's something I could do to help, you'd tell me, wouldn't you?" },
				{ Cue = "/VO/Patroclus_0110",
					Text = "You have no power to release me from this coil, I'm afraid. So just go on about your business, and leave me to mine." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			-- alt below
			PatroclusMeeting06 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusMeeting05" },
				RequiredFalseTextLines = { "PatroclusMeeting06B", "PatroclusGift08_A" },
				{ Cue = "/VO/Patroclus_0111",
					Text = "Stranger, I am warning you: If you keep showing up like this, I'm going to have to find a new nickname for you." },
				{ Cue = "/VO/ZagreusField_1714", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "No need to go out of your way, there, sir. We're just acquaintances. I don't know anything about you, either." },
				{ Cue = "/VO/Patroclus_0112",
					Text = "And you are better off. This place is for the greatest of the Greeks. Do I seem like the sort to fit in, here? Go on and chat with Theseus, or someone of that sort." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusMeeting06B =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusAboutAchilles03A", "PatroclusAboutAchilles03B" },
				RequiredFalseTextLines = { "PatroclusMeeting06", "PatroclusGift08_A" },
				{ Cue = "/VO/Patroclus_0111",
					Text = "Stranger, I am warning you: If you keep showing up like this, I'm going to have to find a new nickname for you." },
				{ Cue = "/VO/ZagreusField_1921", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "No need to go out of your way, there, sir. I rather feel like a stranger around here. I have no plans to extend my stay." },
				{ Cue = "/VO/Patroclus_0112",
					Text = "And you are better off. This place is for the greatest of the Greeks. Do I seem like the sort to fit in, here? Go on and chat with Theseus, or someone of that sort." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			-- alt below
			PatroclusMeeting07 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusMeeting06", "PatroclusMeeting06B" },
				RequiredFalseTextLines = { "MyrmidonReunionQuestComplete", "PatroclusMeeting07B" },
				{ Cue = "/VO/Patroclus_0113",
					Text = "Tell me, stranger, if it pleases you. How is it you keep running into me...? I thought this the remotest corner of Elysium." },
				{ Cue = "/VO/ZagreusField_1715", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "The ever-shifting chambers of the Underworld leave quite a bit to chance. I took our meetings merely for coincidence." },
				{ Cue = "/VO/Patroclus_0114",
					Text = "When chance is too far-fetched to be believed, then it's the Fates at play. Or something else. I don't know whether to believe you, but I know that I want you to leave me, now." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusGreetings,
			},
			PatroclusMeeting07B =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "MyrmidonReunionQuestComplete" },
				RequiredFalseTextLines = { "PatroclusMeeting07" },
				{ Cue = "/VO/Patroclus_0113",
					Text = "Tell me, stranger, if it pleases you. How is it you keep running into me...? I thought this the remotest corner of Elysium." },
				{ Cue = "/VO/ZagreusField_1715", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "The ever-shifting chambers of the Underworld leave quite a bit to chance. I took our meetings merely for coincidence." },
				{ Cue = "/VO/Patroclus_0422",
					Text = "When chance is too far-fetched to be believed, then it's the Fates at play. Or something else." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusGreetings,
			},
			PatroclusMeeting08 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusMeeting06", "PatroclusMeeting06B" },
				RequiredFalseTextLines = { "MyrmidonReunionQuestComplete" },
				{ Cue = "/VO/Patroclus_0131",
					Text = "What is it this time, stranger? You're quite effective at locating me, but not so good at leaving me in peace." },
				{ Cue = "/VO/ZagreusField_1729", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Just on one of my little jaunts through paradise is all, sir. Happened to be on the way toward my destination." },
				{ Cue = "/VO/Patroclus_0132",
					Text = "You think this place a paradise? At least down in the depths, there are so many other souls with whom to share one's misery. No offense... I know that you're just passing through." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusMeeting09 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusMeeting06", "PatroclusMeeting06B" },
				{ Cue = "/VO/Patroclus_0133",
					Text = "So, stranger. How goes willful disobedience of late? Would that I, too, had it within me to defy the gods." },
				{ Cue = "/VO/ZagreusField_1730", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Oh, you know, it's going. That I've been able to make it this far means I am getting somewhere, I suppose. No thanks to all your brethren in Elysium." },
				{ Cue = "/VO/Patroclus_0134",
					Text = "No use trying to reason with my brethren here. Many of the shades whom you've encountered, why, I watched them throw away their lives when they were men. Yet still they seek to prove to one another and themselves that they are great warriors." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusGreetings,
			},
			PatroclusMeeting10 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusAboutAchilles01A", "PatroclusAboutAchilles01B", "PatroclusAboutAchilles01C_01", "PatroclusAboutAchilles01C_02" },
				RequiredFalseTextLines = { "PatroclusAboutMyrmidonReunionQuest01" },
				{ Cue = "/VO/ZagreusField_3504", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Say, isn't that the Lethe right nearby? You've been through so much, don't you ever... you could just drink deeply of the River of Forgetfulness, and be done with, no?" },
				{ Cue = "/VO/Patroclus_0219",
					Text = "It has been rather tempting, yes. And I have taken sips, from time to time. But I've some memories I'm not quite ready to give up on, yet." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusMeeting11 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusGift04" },
				RequiredAnyTextLines = { "PatroclusMeeting06", "PatroclusMeeting06B", "PatroclusAboutMyrmidonReunionQuest01" },
				AreIdsNotAlive = { 563036 },
				{ Cue = "/VO/ZagreusField_3510", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You ever want to travel past this glade, sir? Elysium in all its splendors... there's a lot to see. Sure you're all right?" },
				{ Cue = "/VO/Patroclus_0409",
					Text = "I'm sure enough, stranger. I traveled far and wide whilst I drew breath. That got me rid of any wanderlust. This little corner suits me well, I think. I have the statue there, the river... it's quite nice." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},

