				RequiredAnyTextLines = { "PatroclusAboutAchilles01A", "PatroclusAboutAchilles01B", "PatroclusAboutAchilles01C_01", "PatroclusAboutAchilles01C_02" },
				RequiredFalseTextLines = { "PatroclusAboutBracer01A", "PatroclusAboutBracer01B", "PatroclusAboutBracer01C", "PatroclusAboutBracer01D", "MyrmidonReunionQuestComplete" },
				RequiredTrait = "DirectionalArmorTrait",
				{ Cue = "/VO/Patroclus_0171",
					Text = "What is that on your wrist, there, stranger? Let me see, is that... a bracer of the myrmidons. Achilles..." },
				{ Cue = "/VO/ZagreusField_3472", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "This is his bracer, yes. You recognize it, then. Were you a myrmidon, yourself? I heard they were unstoppable." },
				{ Cue = "/VO/Patroclus_0172",
					Text = "Well, now... if we were so unstoppable, we'd not be here, would we? May that bracer protect you more than its like protected our phalanxes." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusAboutAchillesAspect01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusAboutAchilles02" },
				RequiredTrait = "SpearTeleportTrait",
				AreIdsNotAlive = { 563036 },
				{ Cue = "/VO/Patroclus_0173",
					Text = "Must say that's an impressive spear you've got, there, stranger. Rather like the one Achilles held. He gave it to you, then? Followed him beyond the grave, only to be handed off." },
				{ Cue = "/VO/ZagreusField_3473", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's... a bit more complicated than that, sir, though yes. This is the very same as he once wielded. I am honored to carry it now." },
				{ Cue = "/VO/Patroclus_0174",
					Text = "I'll bet. Perhaps you'll even come to surpass the body count Achilles piled up with it back in his day, seeing as you seem to have limitless opportunity to use it." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusAboutAchillesAspect02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusAboutAchillesAspect01" },
				RequiredTrait = "SpearTeleportTrait",
				AreIdsNotAlive = { 563036 },
				{ Cue = "/VO/Patroclus_0175",
					Text = "You know, stranger, there was a time Achilles would have killed someone for taking up his spear. But there you are, like you own it." },
				{ Cue = "/VO/ZagreusField_3474", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I don't think anybody owns this spear, sir. I'm hanging onto it for the time being. Though I don't think I'll be the last to do so." },
				{ Cue = "/VO/Patroclus_0176",
					Text = "Oh, there's no need to get all philosophical about it. It's a killing weapon. Nothing more." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},

			-- if you don't know his connection to achilles already
			PatroclusAboutAchilles01A =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusMeeting06" },
				RequiredFalseTextLines = { "AchillesAboutPatroclus01A", "PatroclusAboutAchilles01B", "PatroclusAboutAchilles01C_01", "PatroclusAboutAchilles01C_02", "PatroclusAboutAchilles02" },
				{ Cue = "/VO/Patroclus_0115",
					Text = "You mentioned something of your parentage, stranger. I have a question on that subject, if you please." },
				{ Cue = "/VO/ZagreusField_1716", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I please, sir. What's your question? Ask away." },
				{ Cue = "/VO/Patroclus_0116",
					Text = "Your father must have records of the dead. So I was wondering if you might do for me a favor, and look up the whereabouts of someone I once knew." },
				{ Cue = "/VO/ZagreusField_1717", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenial_Full", PreLineAnimTarget = "Hero",
					Text = "You've done a lot for me. The least I can do is ask around. Who is it that you're looking for?" },
				{ Cue = "/VO/Patroclus_0117",
					Text = "I seek one called Achilles. Was rather famous once among the living, but among the dead, who knows. I thought he would be here, but he is not." },
				{ Cue = "/VO/ZagreusField_1718", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag", PreLineWait = 0.35,
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "...Achilles... I've heard the name before. And, {#DialogueItalicFormat}erm{#PreviousFormat}, can I ask why you wish to know of him? In case it happens to come up." },
				{ Cue = "/VO/Patroclus_0118",
					Text = "It's just... I simply miss him, stranger. That is all." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			-- if you do know his connection to achilles
			PatroclusAboutAchilles01B =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusFirstMeeting" },
				RequiredAnyTextLines = { "AchillesAboutPatroclus01A", "AchillesAboutPatroclus01B", "AchillesAboutPatroclus01C", "AchillesAboutPatroclus01D" },
				RequiredFalseTextLines = { "PatroclusAboutAchilles01C_01", "PatroclusAboutAchilles01C_02", "PatroclusAboutBracer01A", "PatroclusAboutBracer01B", "PatroclusAboutBracer01C", "PatroclusAboutBracer01D", "PatroclusAboutAchilles02" },
				{ Cue = "/VO/Patroclus_0119",
					Text = "Something the matter, there, stranger? Or have you come to torment me some more with idle chat?" },
				{ Cue = "/VO/ZagreusField_1720", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You know Achilles, don't you, sir. You're Patroclus. Achilles... serves us at my home. He trained me. He sends you his regards and love. He says to tell you he is thinking of you always." },
				{ Cue = "/VO/Patroclus_0120",
					Text = "...That name. So then you've seen him, have you. In your father's house. And want me to believe he said all that? {#DialogueItalicFormat}Tsk{#PreviousFormat}. You'll forgive me if I am a little skeptical. In life, I left Achilles. And in death, he left me. We're even. And we've nothing left to say to one another, now." },
				{ Cue = "/VO/ZagreusField_1721", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "But, that can't possibly be true. There must be some misunderstanding here. You haven't spoken since you died?" },
				{ Cue = "/VO/Patroclus_0121",
					Text = "We haven't, no. And I don't think we shall. But, you can humor him for me, I guess. Tell him I'm doing perfectly all right, won't you?" },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},

			-- if you do know his connection to achilles, specifically via the bracer conv with patroclus
			-- there is a near-copy of this below due to very similar but mutually exclusive requirements
			PatroclusAboutAchilles01C_01 =
