			PatroclusAboutHeroes01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredAnyTextLines = { "PatroclusAboutAchilles01A", "PatroclusAboutAchilles01B", "PatroclusAboutAchilles01C_01", "PatroclusAboutAchilles01C_02" },
				RequiredTextLines = { "PatroclusGift05" },
				AreIdsNotAlive = { 563036 },
				{ Cue = "/VO/ZagreusField_3505", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You must have met so many great heroes in your day, having fought alongside Achilles... do you still keep in touch with any of them, here?" },
				{ Cue = "/VO/Patroclus_0402",
					Text = "Great heroes... haven't you seen, by now, what they are like, stranger? Not all entirely what they're cracked up to be. But, there were good ones, yes. Suppose they're mostly keeping to themselves. Like I am here. Perhaps we'll run into each other, down the line." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusAboutGods01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusGift06" },
				AreIdsNotAlive = { 563036 },
				{ Cue = "/VO/Patroclus_0404",
					Text = "Never quite considered... you're related to the gods upon Olympus, aren't you, stranger...? I should be far more careful around you." },
				{ Cue = "/VO/ZagreusField_3507", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "What do you mean by that, sir? Though, yes, my father being who he is, it means I have a lot of uncles, cousins, and the like, up on the surface, there." },
				{ Cue = "/VO/Patroclus_0405",
					Text = "Well... you would be wise to watch yourself, with some of them, I think. Don't get on anyone's bad side, if you can help it. Which, when I yet lived, none of us could." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusGreetings,
			},
			-- alt below
			PatroclusAboutLove01_A =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusWithAchilles01" },
				RequiredAnyTextLines = { "BecameCloseWithThanatos01Than_GoToHim", "BecameCloseWithThanatos01_BThan_GoToHim", "BecameCloseWithMegaera01Meg_GoToHer", "BecameCloseWithMegaera01_BMeg_GoToHer" },
				RequiredAnyOtherTextLines = { "PatroclusGift08", "PatroclusGift08_A" },
				RequiredFalseTextLines = { "PatroclusAboutLove01_B" },
				AreIdsNotAlive = { 563036 },
				{ Cue = "/VO/Patroclus_0406",
					Text = "You ever been in love with someone, stranger? Might be a mortals-only thing, for all I know. You gods have strange relationships, I hear." },
				{ Cue = "/VO/ZagreusField_3508", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I have been, sir. Or, I should say, I am." },
				{ Cue = "/VO/Patroclus_0407",
					Text = "Ah, that's good. Cling to the feeling, then. And if you lose it... reach for it. Search for it, no matter what, all right? Because, without it... well, there isn't much of a comparison, I think." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusAboutLove01_B =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusWithAchilles01" },
				RequiredAnyOtherTextLines = { "PatroclusGift08", "PatroclusGift08_A" },
				RequiredFalseTextLines = { "PatroclusAboutLove01_A", "BecameCloseWithThanatos01Than_GoToHim", "BecameCloseWithThanatos01_BThan_GoToHim", "BecameCloseWithMegaera01Meg_GoToHer", "BecameCloseWithMegaera01_BMeg_GoToHer" },
				AreIdsNotAlive = { 563036 },
				{ Cue = "/VO/Patroclus_0406",
					Text = "You ever been in love with someone, stranger? Might be a mortals-only thing, for all I know. You gods have strange relationships, I hear." },
				{ Cue = "/VO/ZagreusField_3509", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Not sure that I have been, sir. I understand it's one of those things where, you'll know it when it happens." },
				{ Cue = "/VO/Patroclus_0408",
					Text = "Perhaps you shall, someday or night down here. The feeling comes with pain, and plenty of it, but... the pain is worth it, in my limited experience. And once you find that feeling, cling to it. As tightly as you can. If you don't mind my saying." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},

			PatroclusAboutTrinkets01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PatroclusGift05" },
				RequiredInactiveMetaUpgrade = "ExtraChanceReplenishMetaUpgrade",
				{ Cue = "/VO/Patroclus_0215",
					Text = "Well, what's it going to be this time, stranger? The HydraLite? Maybe the Kiss of Styx?" },
				{ Cue = "/VO/ZagreusField_3501", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You sure you don't need any of this stuff, sir? I really appreciate it, but... I don't want to take it for granted." },
				{ Cue = "/VO/Patroclus_0216",
					Text = "Oh, come, this is Elysium. Taking such things for granted is the point. We gloried shades receive our stipend of the finest goods, so we can battle till we've had our fill. But I've already had my fill, I think." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusAboutFishing01 =
