				RequiredTextLines = { "PatroclusFirstMeeting", "PatroclusGift04" },
				RequiredHasFish = true,
				RequiresFishingPointInRoom = true,
				{ Cue = "/VO/Patroclus_0203",
					Text = "You've ample time for fishing, have you, stranger? And here I thought you'd more important business to attend to, though it's not for me to judge, I guess." },
				{ Cue = "/VO/ZagreusField_3493", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I'll have you know some of us take matters of fishing very seriously, sir! Besides... helps take my mind off having to fight your Elysian brethren nonstop." },
				{ Cue = "/VO/Patroclus_0204",
					Text = "Oh I'd not take your mind off this fight if it's one that you hope to win, stranger. I once took my mind off a fight, and, well... I wound up here." },
				{
					Text = "Patroclus_OfferText02",
					Choices = PresetEventArgs.PatroclusBenefitChoices,
					PreLineFunctionName = "ShowUIForDecision",
				},
				OnQueuedThreadedFunctionName = "AmbientChatting",
				OnQueuedFunctionArgs = PresetEventArgs.PatroclusMuttering,
			},
			PatroclusAboutLegendary01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "AchillesGift07_A" },
				RequiredPlayed = { "/VO/ZagreusField_3584" },
				RequiredTrait = "AchillesPatroclusAssistTrait",
				AreIdsNotAlive = { 563036 },
