		GiftTextLineSets =
		{
			PatroclusGift01 =
			{
				PlayOnce = true,
				{ Cue = "/VO/ZagreusField_1731", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Hey, look, I can tell you're struggling right now, and... maybe this isn't the way to help you through it all, but... I want you to have this." },
				{ Cue = "/VO/Patroclus_0142",
					Text = "{#DialogueItalicFormat}Ahh{#PreviousFormat}, so you are taking pity on me, then? Well, I've no pride like many others here, so I shall take it. Though, let's make it a fair exchange, shall we?" },
			},
			PatroclusGift02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "PatroclusGift01" },
				{ Cue = "/VO/Patroclus_0135",
					Text = "Why would you waste a proper vintage on the likes of me? Questions that are better left unanswered, I would guess." },
				{ Cue = "/VO/ZagreusField_1733", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "If this eases your burden even for a little while, then it's in good hands, as far as I'm concerned." },
			},
			PatroclusGift03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "PatroclusGift02" },
				{ Cue = "/VO/Patroclus_0136",
					Text = "{#DialogueItalicFormat}Ah{#PreviousFormat}, a means to drown my sorrows temporarily. That's good. And rather kind of you, I have to say." },
				{ Cue = "/VO/ZagreusField_1734", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's nothing. Thank you for not trying to slay me where I stand and boast about it, like seemingly everyone else around here." },
			},
			PatroclusGift04 =
			{
				PlayOnce = true,
				RequiredTextLines = { "PatroclusGift03" },
				{ Cue = "/VO/Patroclus_0137",
					Text = "Oh. Here in Elysium you have to win this stuff, so I have rarely had occasion to enjoy it. Thanks." },
				{ Cue = "/VO/ZagreusField_1735", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I'd say you earned it more than anybody else I've seen around here, lately. Though I don't make the rules." },
			},
			PatroclusGift05 =
			{
				PlayOnce = true,
				RequiredTextLines = { "PatroclusGift04" },
				{ Cue = "/VO/Patroclus_0140",
					Text = "You owe me neither time nor generosity, and certainly not this. But if you truly have no better use for it..." },
				{ Cue = "/VO/ZagreusField_1738", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's not a matter of owe, sir. Please open your mind to the possibility, nay the fact, that there are those who care about your wellbeing." },
			},
			PatroclusGift06 =
			{
				PlayOnce = true,
				RequiredTextLines = { "PatroclusGift05" },
				{ Cue = "/VO/Patroclus_0139",
					Text = "Just the relief from misery that I was looking for. A fleeting feeling, really, but I'll take a fleeting feeling over none." },
				{ Cue = "/VO/ZagreusField_1737", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "That's both very dejected and very astute of you to say. Take care of yourself, will you?" },
			},
			PatroclusGift07_A =
			{
				PlayOnce = true,
				RequiredTextLines = { "PatroclusGift06" },
				{ Cue = "/VO/ZagreusField_3487", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Here, sir. The drink of the gods, themselves. Thought maybe you would like to try it! Just a small token of my appreciation for your selflessness with me, time after time." },
				{ Cue = "/VO/Patroclus_0197",
					Text = "You'd give the precious gift of Ambrosia to someone like me, stranger? A gloried thing like that. Why bother...? I should be just another shade to you." },
				{ Cue = "/VO/ZagreusField_3488", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well, you're not. You're someone I look forward to seeing here in Elysium, which is more than I can say for just about anybody else around here, so... please, enjoy it. Perhaps with Achilles." },
				{ Cue = "/VO/Patroclus_0199",
					Text = "Well... perhaps I'll try. You're not so bad, you know that, stranger? Careful with that, or you'll undermine the ruthless reputation that you have." },
			},
			PatroclusGift08_A =
			{
				PlayOnce = true,
				RequiredTextLines = { "PatroclusGift07_A" },
				{ Cue = "/VO/ZagreusField_3489", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I don't see that last bottle of Ambrosia anywhere, which I can only take to mean... you need another. Here, sir. Thanks for seeing me through all of this, time after time." },
				{ Cue = "/VO/Patroclus_0200",
					Text = "You're thanking {#DialogueItalicFormat}me{#PreviousFormat}, stranger? I've been here for some time, I think, and yet... some of your customs I'll admit remain quite strange to me. You've done more for me than I've any right to expect, from anyone." },
				{ Cue = "/VO/ZagreusField_3490", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It's just a bottle of Ambrosia, sir, it's really no big deal. OK, fine, it's rather difficult to come by, but still!" },
				{ Cue = "/VO/Patroclus_0201",
					Text = "You jest, but I do not. I'd offer you my lifelong gratitude, but... well, you know." },
				{ Cue = "/VO/ZagreusField_3492", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "You've got the rest of your life ahead of you, as far as I'm concerned. Here's to starting anew, right? You can call me Zagreus, or stranger, whatever suits you." },
				{ Cue = "/VO/Patroclus_0202",
