		GiftTextLineSets =
		{
			EurydiceGift01 =
			{
				PlayOnce = true,
				{ Cue = "/VO/ZagreusField_2498", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Eurydice, thank you for making me feel welcome in your pleasant home. Can't say the same for most places I've been lately. I've a small gift I wanted you to have." },
				{ Cue = "/VO/Eurydice_0049",
					Text = "{#DialogueItalicFormat}Aw{#PreviousFormat}, that's awful sweet of you, Your Royal Majesty. Bringing me fresh ingredients like this! Know what, here's something for you, too, help keep you safe out there!" },
			},
			EurydiceGift02 =
			{
				PlayOnce = true,
				RequiredTextLines = { "EurydiceGift01" },
				{ Cue = "/VO/Eurydice_0042",
					Text = "Just the ingredient I needed, hon, thank you so much! You know, you aren't near as bad as I'd heard!" },
				{ Cue = "/VO/ZagreusField_2499", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
						PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
						PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
						Text = "Oh, don't worry, we're still getting to know each other! Plenty of time for me to live up to my notorious reputation. Though I'm surprised you've heard of me at all." },
			},
			EurydiceGift03 =
			{
				PlayOnce = true,
				RequiredTextLines = { "EurydiceGift02" },
				{ Cue = "/VO/Eurydice_0043",
					Text = "{#DialogueItalicFormat}Aw{#PreviousFormat}, for me, really, Your Majesty? This will be perfect for my latest recipes." },
				{ Cue = "/VO/ZagreusField_2500", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
						PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
						PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
						Text = "You know, of all the souls I've ever met, I feel like you put this stuff to the best possible use." },
			},
			EurydiceGift04 =
			{
				PlayOnce = true,
				RequiredTextLines = { "EurydiceGift03" },
				{ Cue = "/VO/Eurydice_0044",
					Text = "You read my mind, hon! I go through this stuff awful fast sometimes." },
				{ Cue = "/VO/ZagreusField_2501", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
						PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
						PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
						Text = "And I, in turn, shall go through your incredible cooking awful fast, given the opportunity." },
			},
			EurydiceGift05 =
			{
				PlayOnce = true,
				RequiredTextLines = { "EurydiceGift04" },
				{ Cue = "/VO/Eurydice_0045",
					Text = "Hey, mighty generous of you to keep me stocked up on this stuff, Your Royal Majesty!" },
				{ Cue = "/VO/ZagreusField_2502", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
						PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
						PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
						Text = "My pleasure! I'm always pleased to see you during my otherwise generally unfortunate travels in Asphodel." },
			},

			EurydiceGift06 =
			{
				PlayOnce = true,
				RequiredTextLines = { "EurydiceGift05" },
				{ Cue = "/VO/Eurydice_0046",
					Text = "Thanks, Your Majesty! Though you're not going to get in trouble bringing me this stuff, are you?" },
				{ Cue = "/VO/ZagreusField_2503", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
						PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
						PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
						Text = "{#DialogueItalicFormat}Hah{#PreviousFormat}, no! I don't think I can get into more trouble at the moment. May this make your dishes that much more delectable!" },
			},
			EurydiceGift07 =
			{
				PlayOnce = true,
				RequiredTextLines = { "EurydiceGift06" },
				{ Cue = "/VO/ZagreusField_4289", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Eurydice, I was thinking maybe this would benefit your culinary practice. Here, please." },
				{ Cue = "/VO/Eurydice_0237",
					Emote = "PortraitEmoteSurprise",
					Text = "Whoa, this is Ambrosia! Hard to come by this stuff around here. You sure I can have this?" },
				{ Cue = "/VO/ZagreusField_4290", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
						PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
						Text = "Absolutely! Least I could do for all your friendship and support. Always happy to hear your voice when I'm out here." },
			},
			EurydiceGift08 =
			{
				PlayOnce = true,
				RequiredTextLines = { "EurydiceGift07" },
				{ Cue = "/VO/Eurydice_0048",
					Text = "I ever tell you you're a real sweetheart, hon? Because, if not, I'm telling you right now. Where would my culinary practice be if not for you?" },
				{ Cue = "/VO/ZagreusField_2505", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I think your culinary practice would be every bit as extraordinary, though I'm pleased to have done small service to you in this great pursuit." },
				{ Cue = "/VO/Eurydice_0208",
					PostLineThreadedFunctionName = "MaxedRelationshipPresentation",
					PostLineFunctionArgs = { Text = "NPC_Eurydice_01", Icon = "Keepsake_EurydiceSticker_Max" },
					Text = "Well, we both know you've done a lot more for me than that. I wasn't expecting to make any new friends here anytime soon, so all the more reason it's been a real treat to get to know you. If I may say? You're a hell of a guy, Your Royal Majesty." },						
			},

