	-- Persephone, Id = 559274
	-- Home version below
	-- Alt Spawn Points: { 555708 [garden], 391561 [with hades], 555710 [with cerberus] }
	NPC_Persephone_01 =
	{
		InheritFrom = { "NPC_Neutral" },

		UseText = "UseTalkToFemaleGhost",
		Portrait = "Portrait_Persephone_Default_01",
		AnimOffsetZ = 200,
		StartTextLinesAngleTowardHero = true,
		Groups = { "NPCs" },

		ActivateRequirements =
		{
			-- RequiredMinCompletedRuns = 0,
		},

		Binks =
		{
			"NPC_PersephoneGardenIdleHip_Bink",
			"NPC_PersephoneGardenGreeting_Bink",
			"NPC_PersephoneGardenWalk_Bink",
			"NPC_PersephoneGardenWalkStop_Bink",
			"NPC_PersephoneGardenDismiss_Bink",
			"NPC_PersephoneGardenVulnerable_Bink",
		},

		InteractTextLineSets =
		{
			PersephoneFirstMeeting =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredRoom = "E_Story01",
				EndVoiceLines =
				{
					PreLineWait = 1.8,
					Queue = "Interrupt",
					NoTarget = true,
					-- However long... it takes...
					{ Cue = "/VO/ZagreusField_3611" },
					-- Urgh... can't... hold... on...
					{ Cue = "/VO/ZagreusField_4646", PreLineWait = 2.5 },
				},
				{ Cue = "/VO/Persephone_0021",
					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { Mom = true },
					Speaker = "NPC_Persephone_Unnamed_01",
					SpeakerLabelOffsetY = 18,
					Portrait = "Portrait_Persephone_Apprehensive_01",
					Text = "Pray, who might you be, wandering all the way out here? You're trespassing on private property, you know. Speak up!" },
				{ Cue = "/VO/ZagreusField_3602",

					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Erm{#PreviousFormat}, you must be... Persephone. My name is Zagreus, and... I think I might be your son." },

				{ Cue = "/VO/Persephone_0022",

					MusicActiveStems = { "Percussion", "Room" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					PreLineWait = 0.15,
					Emote = "PortraitEmoteSurprise",
					Speaker = "NPC_Persephone_Unnamed_01", SpeakerLabelOffsetY = 18,
					Portrait = "Portrait_Persephone_FiredUp_01",
					PreLineAnim = "PersephoneGarden_Dismiss",
					Text = "{#DialogueItalicFormat}What{#PreviousFormat}?! How dare...! Get out of here! Get out, or I shall make you!! {#DialogueItalicFormat}Out {#PreviousFormat}with you!" },
				{ Cue = "/VO/ZagreusField_3603",

					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteFiredUp",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "No!! So then you {#DialogueItalicFormat}are {#PreviousFormat}her. I, I don't care if you hate me, but I'm not leaving until I get some answers! I never even knew you existed. Why did you leave me?" },
				{ Cue = "/VO/Persephone_0023",

					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Persephone_Joyful_01",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "PersephoneGarden_Shocked",
					Text = "You died! You, you {#DialogueItalicFormat}died{#PreviousFormat}...! Those burning feet, they... sputtered out, when you were born. But... now you're standing here before me. How...?" },
				{ Cue = "/VO/ZagreusField_3604",

					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I died? I died... what, you mean {#DialogueItalicFormat}permanently {#PreviousFormat}died, but, I'm alive! Oh, gods... you never even knew I existed, either." },
				{ Cue = "/VO/Persephone_0024",

					MusicActiveStems = { "Percussion", "Room" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Persephone_Joyful_01",
					PortraitExitAnimation = "Portrait_Persephone_Joyful_01_Exit",
					PostLineAnim = "ZagreusInteractionComeHither_Start", PostLineAnimTarget = "Hero",
					PostLineThreadedFunctionName = "TimePassesPresentation",
					PostLineFunctionArgs = { PreTextWait = 1.5, HeroAnim = "ZagreusTalkDenialStart", GlobalVoiceLines = "PersephoneFirstMeetingIntermissionVoiceLines", PersephoneMusicFullBlast = true },
					EndSound = "/Leftovers/Menu Sounds/EmoteThoughtful",
					Text = "Alive! Zagreus, he... even kept your name! You live, how can this be, you live? {#DialogueItalicFormat}You live{#PreviousFormat}...! Oh, the cruel Fates! My son!" },
				-- time passes
				{ Cue = "/VO/ZagreusField_3606",

					-- MusicActiveStems = { "WoodWinds" },
					-- MusicMutedStems = { "Percussion", "Room" },

					Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					FadeOutTime = 0.6, FullFadeTime = 6.5, FadeInTime = 1.8, PreLineWait = 0.2,
					FadeInSound = "/Leftovers/Menu Sounds/EmoteExcitement",
					InterSceneWaitTime = 0.2,
					MusicSection = 3,
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",

					TeleportToId = 571466,
					TeleportOffsetY = 80,
					TeleportOffsetX = 90,
					TeleportHeroToId = 571466,
					TeleportHeroOffsetX = -30,
					TeleportHeroOffsetY = 120,
					AngleHeroTowardTargetId = 571468,
					AngleTowardHero = true,

					Text = "...In short, I beat him, finally, though he did everything he could to prevent our meeting here like this. But there's something he wanted me to tell you, in the end. He said to tell you Cerberus is doing very well." },
				{ Cue = "/VO/Persephone_0025",

					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds", "Room", "Trombones" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					PreLineAnim = "PersephoneGarden_Vulnerable_ShockToThoughtful",
					Text = "{#DialogueItalicFormat}Ah{#PreviousFormat}. He told you that, did he. That's good. That's good! Though, tell me something, Zagreus. How... is it that you found me? To have journeyed all this way, I scarce believe it.... Zagreus?" },
				{ Cue = "/VO/ZagreusField_3607",

					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion", "Strings" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Zag_Unwell_01", Speaker = "CharProtag", Emote = "PortraitEmoteDepressed",
					PreLineAnim = "ZagreusInjured_IdleToSick", PreLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Ugh{#PreviousFormat}... Oh, it's... guess I'm just a little tired, that's all. Not accustomed to the weather, as I think you call it." },
				{ Cue = "/VO/Persephone_0026",

					MusicActiveStems = { "Percussion", "Strings" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Persephone_Apprehensive_01",
					PreLineAnim = "PersephoneGarden_Vulnerable",
					PreLineWait = 0.35,
					Text = "No... no, that isn't it, it's that... you cannot stay here. You can't stay here." },
				{ Cue = "/VO/ZagreusField_3608",

					MusicActiveStems = { "WoodWinds", "Room" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Zag_Unwell_01", Speaker = "CharProtag",
					Text = "Really, you're... {#DialogueItalicFormat}heh{#PreviousFormat}... kicking me out? Why?" },
				{ Cue = "/VO/Persephone_0027",

					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Persephone_Calculating_01",
					Text = "The Fates are cruel, Zagreus. You're bound to that place... same as your father. So they would have us say good-bye, for now. My heart soars, knowing you live. Then it breaks, that our time together was so brief." },
				{ Cue = "/VO/ZagreusField_3609",

					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Zag_Unwell_01", Speaker = "CharProtag",
					Text = "No... I can... come back... I can come back, now that I found you, he... maybe he'll just let me come back." },
				{ Cue = "/VO/Persephone_0028",

					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Persephone_FiredUp_01",
					PreLineAnim = "PersephoneGarden_Dismiss",
					Text = "You tell him I demand it. And I demand he also tell me how come he, or for that matter Nyx, never sent notice that you lived! Olympus is all rife with schemers, but I was so naive to think that they were different." },
				{ Cue = "/VO/ZagreusField_3610",

					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,

					Portrait = "Portrait_Zag_Unwell_01", Speaker = "CharProtag",
					Text = "I'll tell him... I'll tell him, I... feel awful. I... I have to go." },

				{ Cue = "/VO/Persephone_0029",

					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { FullBlast = true },
					Portrait = "Portrait_Persephone_Apprehensive_01",
					PostLineThreadedFunctionName = "SurfaceKillHero",
					PostLineFunctionArgs = { WaitTime = 0, MusicEndTime = 60 },
					Text = "The Styx shall take you, then. Oh, Zagreus. Farewell, my son. Won't you come back to me? When you are able, please. Come back. I shall be waiting here. However long it takes." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGardenFirstGreeting,
			},

			PersephoneMeeting02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneFirstMeeting" },
				RequiredRoom = "E_Story01",
				InitialGiftableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 1.8,
					Queue = "Interrupt",
					NoTarget = true,
					-- Until we... ungh.
					{ Cue = "/VO/ZagreusField_3628" },
					-- No... not... yet...
					-- { Cue = "/VO/ZagreusField_4649", PreLineWait = 2.5 },
				},
				{ Cue = "/VO/Persephone_0042",
					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { Mom = true },
					Portrait = "Portrait_Persephone_Joyful_01",
					PreLineAnim = "PersephoneGarden_Vulnerable",
					Text = "I begged the Fates that you'd return to me. Perhaps I ought to be more careful how I speak of them. Tell me, how was your journey? What happened?" },

				{ Cue = "/VO/ZagreusField_3622",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Father didn't make it any easier for me. Nor would he tell me why he never sent for you, after you left. So, I don't know, what's going on, still, but... I wanted to see you, again." },

				{ Cue = "/VO/Persephone_0043",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Text = "I wanted to see you, too. Come, speak quickly with me, that our time together here may be as full as possible, all right?" },

				{ Cue = "/VO/ZagreusField_3623",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "I just... I need to know what happened, between you and Father. Why are you here, and not with us?" },

				{ Cue = "/VO/Persephone_0044",
					MusicActiveStems = { "Percussion", "Room" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_Calculating_01",
					Text = "I couldn't live with myself anymore, down there. After I left Olympus, a long story in itself, I came to be with your father, in the Underworld. It was a shock, to say the least. The others in your father's house were welcoming enough, but... I never felt that I belonged." },

				{ Cue = "/VO/ZagreusField_3624",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "I know the feeling, I think. But you stayed. Long enough to have me. Those whom I've spoken to about you, it's as you said, they have fond memories of you. And then, you even had a child. Did Father... did he mistreat you?" },

				{ Cue = "/VO/Persephone_0045",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_Apprehensive_01",
					PreLineAnim = "PersephoneGarden_Greeting",
					Text = "No, no. Your father could be very difficult. Though, he was gentle, with me. Soon enough, we got on fine, better than fine, at times, but... when I was with child... why, I struggled terribly. Because... a child born of surface-dwellers could not live, down there." },

				{ Cue = "/VO/ZagreusField_3625",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Could not live down there, according to whom, exactly? The Fates?" },

				{ Cue = "/VO/Persephone_0046",
					MusicSection = 3,
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_Calculating_01",
					Text = "According to them, yes. According to your father, and to Nyx. Even having heard... I took my chances, anyway. But when you emerged stillborn... it was too much. I fled. I fled, and came to reside here. Above the notice of the Underworld. Beneath the notice of Olympus." },

				{ Cue = "/VO/ZagreusField_3626",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Unwell_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteDepressed",
					PreLineAnim = "ZagreusInjured_IdleToSick", PreLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Urgh{#PreviousFormat}... but... why would Father lie to you? If he never wanted you... or never wanted me... surely he had the means." },

				{ Cue = "/VO/Persephone_0047",
					MusicActiveStems = { "Percussion", "Room" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_Apprehensive_01",
					PreLineAnim = "PersephoneGarden_Vulnerable",
					Text = "I don't know. I'm wondering the same. But, oh, look at you, Zagreus! We're running out of time. It's happening again! Stay with me, please?" },

				{ Cue = "/VO/ZagreusField_3627",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Unwell_01", Speaker = "CharProtag",
					PreLineWait = 0.35,
					Text = "...I'm trying... but I can't... stay that much longer... I don't think." },

				{ Cue = "/VO/Persephone_0048",
					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { FullBlast = true },
					PostLineThreadedFunctionName = "SurfaceKillHero",
					PostLineFunctionArgs = { WaitTime = 0, MusicEndTime = 60 },
					Portrait = "Portrait_Persephone_Apprehensive_01",
					Text = "Oh, my dear son! Then please... ask Hades what you asked of me! Why he would lie? Tell him I must know, the same as you. And you farewell. Until we meet again!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGardenGreeting,
			},

			PersephoneMeeting03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneMeeting02" },
				RequiredRoom = "E_Story01",
				InitialGiftableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 1.8,
					Queue = "Interrupt",
					NoTarget = true,
					-- It's just... a hunch... of mine...
					{ Cue = "/VO/ZagreusField_3634" },
					-- I'll... be... back...
					{ Cue = "/VO/ZagreusField_4650", PreLineWait = 2.5 },
				},
				{ Cue = "/VO/ZagreusField_3629",
					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { Son = true },
					Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Mother, I... discovered how come Father led you to believe that I could not survive past birth. He didn't lie to you, exactly. So he says." },

				{ Cue = "/VO/Persephone_0049",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_FiredUp_01",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "PersephoneGarden_Dismiss",
					Text = "Wait, slow down, Zagreus, what do you mean? He told me you would not survive your birth, yet here you are. How wasn't that a lie?" },

				{ Cue = "/VO/ZagreusField_3630",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Because... he thought it was the truth. The Fates appointed him dominion over the entire Underworld. But he would never have an heir, so he was led to understand. He never expected me to be born, much less survive." },

				{ Cue = "/VO/Persephone_0050",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_FiredUp_01",
					Text = "He never expected you to survive? Then, forgive my asking, but... why did you? {#DialogueItalicFormat}How {#PreviousFormat}did you? The Fates are not known for making mistakes when it comes to such things." },

				{ Cue = "/VO/ZagreusField_3631",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Nyx saved my life. Or gave me life, I guess. Then raised me in your absence. The Fates are her daughters, right? Maybe she persuaded them to go easier on me. She cannot say more. In any case, apparently it took her quite a while to revive me, and... by then, you were gone." },

				{ Cue = "/VO/Persephone_0051",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_Calculating_01",
					Text = "Nyx... of course she was involved. I only managed to escape because of her. Then, she restored my stillborn son to life, and nurtured him? And, I never would have known, unless that child, fully grown and having learned the truth, defied his father, and then found me here?" },

				{ Cue = "/VO/ZagreusField_3632",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteDepressed", Anim = "ZagreusInjured_IdleToSick", AnimTarget = "Hero", DoShake = true, Portrait = "Portrait_Zag_Unwell_01", WaitTime = 5.2 },
					Text = "Yes. Although, we still don't know how come Father and Nyx never sent for you. Though, {#DialogueItalicFormat}ungh{#PreviousFormat}, let me ask you something, Mother, if you please. If you had found out... that I was born, and very much alive... would you have come back?" },

				{ Cue = "/VO/Persephone_0052",
					MusicActiveStems = { "Percussion", "Room" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_Joyful_01",
					PreLineAnim = "PersephoneGarden_Shocked",
					PostLineAnim = "PersephoneGarden_Vulnerable_ReturnToIdle",
					MusicSection = 3,
					Text = "Would I have come back... to see my only child? Had I but known you lived... I would have gladly gone through hell again to see you, Zagreus. Of course I would have." },

				{ Cue = "/VO/ZagreusField_3633",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					PreLineAnim = "ZagreusInjured_IdleToSick", PreLineAnimTarget = "Hero",
					Portrait = "Portrait_Zag_Unwell_01", Speaker = "CharProtag",
					Text = "Well then... what if... {#DialogueItalicFormat}urgh{#PreviousFormat}... what if Father... didn't want you... doing that?" },

				{ Cue = "/VO/Persephone_0053",
					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { FullBlast = true },
					Portrait = "Portrait_Persephone_Apprehensive_01",
					PreLineAnim = "PersephoneGarden_Greeting", 
					PostLineThreadedFunctionName = "SurfaceKillHero",
					PostLineFunctionArgs = { WaitTime = 0, MusicEndTime = 60 },
					Text = "What if he didn't want me coming back to him? Or seeing you? Oh no, you're fading again, Zagreus. You've pushed yourself too hard! But what is it you meant, by what you said?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGardenGreeting,
			},

			PersephoneMeeting04 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneMeeting03" },
				RequiredRoom = "E_Story01",
				InitialGiftableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 1.8,
					Queue = "Interrupt",
					NoTarget = true,
					-- I... I won't be long...
					{ Cue = "/VO/ZagreusField_3639" },
					-- Urgh... mmm... ungh...
					{ Cue = "/VO/ZagreusField_4651", PreLineWait = 2.5 },
				},
				{ Cue = "/VO/Persephone_0054",
					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { Mom = true },
					Text = "So good to see you, again, Zagreus. Since last you left, I couldn't help but think the worst, about what happened after I had left... or soon before. You mentioned Nyx... you don't suppose that she and Hades... wanted me out of the way?" },

				{ Cue = "/VO/ZagreusField_3635",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I just... don't think that can be. Though, let me ask you. How long did you know Father? And Nyx, for that matter? Did they seem trustworthy?" },

				{ Cue = "/VO/Persephone_0055",
					Portrait = "Portrait_Persephone_Calculating_01",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Text = "Yes. I knew them long enough to have decided yes, that they were trustworthy. As much as anyone I've ever met. But I also know never to be trusting to a fault. I have to consider the possibility I was deceived." },

				{ Cue = "/VO/ZagreusField_3636",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Especially since neither of them is being forthcoming with me even now, right? And yet, I have this feeling something else is going on, Mother. The way that Father speaks of you, the way Nyx speaks of you... I think... they care for you." },

				{ Cue = "/VO/Persephone_0056",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_Default_01",
					MusicSection = 3,
					Emote = "PortraitEmoteCheerful",
					PreLineAnim = "PersephoneGarden_Vulnerable",
					Text = "{#DialogueItalicFormat}Ah, hahaha{#PreviousFormat}! Well, they have a funny way of showing it, I think. Your father, so... you have to get through {#DialogueItalicFormat}him {#PreviousFormat}each time you visit {#DialogueItalicFormat}me{#PreviousFormat}?" },

				{ Cue = "/VO/ZagreusField_3637",
					MusicActiveStems = { "WoodWinds", "Room" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenial_Full", PreLineAnimTarget = "Hero",
					Text = "I do. Mother, what if... what if Father thinks that he's protecting you? Have you... considered that?" },

				{ Cue = "/VO/Persephone_0057",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
 					Portrait = "Portrait_Persephone_FiredUp_01",
					PreLineAnim = "PersephoneGarden_Greeting", 
					Text = "Protecting me? Why Zagreus, what do you mean by that? As you can see, I can fend for myself." },

				{ Cue = "/VO/ZagreusField_3638",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Unwell_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteDepressed",
					PreLineAnim = "ZagreusInjured_IdleToSick", PreLineAnimTarget = "Hero",
					Text = "Well... {#DialogueItalicFormat}urgh{#PreviousFormat}... you're from Olympus, aren't you? But... the gods, none of them seem to know... you're here... so..." },

				{ Cue = "/VO/Persephone_0058",
					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { FullBlast = true },
 					Portrait = "Portrait_Persephone_FiredUp_01",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "PersephoneGarden_Vulnerable", 
					PostLineThreadedFunctionName = "SurfaceKillHero",
					PostLineFunctionArgs = { WaitTime = 0, MusicEndTime = 60 },
					Text = "What, how would you know something like that? Zagreus? Ah, curse the Fates, you're leaving me again..." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGardenGreeting,
			},

			-- assuming you've met the gods a bunch, alt below
			PersephoneMeeting05_A =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneMeeting04" },
				RequiredFalseTextLines = { "PersephoneMeeting05_B" },
				RequiredMinCompletedRuns = 15,
				RequiredRoom = "E_Story01",
				InitialGiftableOffSource = true,
				EndVoiceLines =
				{
					PreLineWait = 1.8,
					Queue = "Interrupt",
					NoTarget = true,
					-- When I... return...
					{ Cue = "/VO/ZagreusField_3646" },
					-- Khh... uhhh... urgh...
					{ Cue = "/VO/ZagreusField_4652", PreLineWait = 2.5 },
				},
				{ Cue = "/VO/Persephone_0059",
					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { Mom = true },
					Portrait = "Portrait_Persephone_Apprehensive_01",
					Text = "Son, I have to ask you, how much do you know about your relatives upon Olympus?" },

				{ Cue = "/VO/ZagreusField_3640",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I think I know quite a bit by now. I've been in steady contact with Athena, Zeus, and other members of the family ever since I first left Father's House... they've been assisting me in getting here." },

				{ Cue = "/VO/Persephone_0060",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
 					Portrait = "Portrait_Persephone_FiredUp_01",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "PersephoneGarden_Vulnerable",
					Text = "No... Zagreus, what have you done? You've led them {#DialogueItalicFormat}here{#PreviousFormat}?" },

				{ Cue = "/VO/ZagreusField_3642",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "No, no, they think... they think I'm trying to escape from Father so that I can join them on their mountaintop. At least I {#DialogueItalicFormat}think {#PreviousFormat}that's what they think. Most of them, anyway." },

				{ Cue = "/VO/Persephone_0061",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_Apprehensive_01",
					Text = "But then... does Hades know of this? That Olympus is involved?" },

				{ Cue = "/VO/ZagreusField_3643",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "He does. Both he and Nyx. In fact, it's Nyx who put me and Olympus into contact. I could never have broken free of the Underworld if not for their help." },

				{ Cue = "/VO/Persephone_0062",
					MusicActiveStems = { "Percussion" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Persephone_Apprehensive_01",
					Text = "So Nyx and Hades are against each other, then. And Olympus is helping you... but they don't know I'm here, as far as you're aware?" },

				{ Cue = "/VO/ZagreusField_3644",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					MusicSection = 3,
					Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "That's right. More or less. Nyx and Father seem to put up with each other. Don't seem to have much of a choice. Though, ever since I learned of you, Nyx has been helping me to find you... while Father's tried to stop me." },

				{ Cue = "/VO/Persephone_0063",
					MusicActiveStems = { "Percussion", "Room" },
					MusicMutedStems = { "WoodWinds" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
 					Portrait = "Portrait_Persephone_FiredUp_01",
					PreLineAnim = "PersephoneGarden_Dismiss", 
					Text = "That fool... what was he thinking? What {#DialogueItalicFormat}is {#PreviousFormat}he thinking...? Do you suppose... he thinks that he's protecting me from {#DialogueItalicFormat}them{#PreviousFormat}? Oh, no..." },

				{ Cue = "/VO/ZagreusField_3645",
					MusicActiveStems = { "WoodWinds" },
					MusicMutedStems = { "Percussion" },
					MusicActiveStemsDuration = 2,
					MusicMutedStemsDuration = 3,
					Portrait = "Portrait_Zag_Unwell_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInjured_IdleToSick", PreLineAnimTarget = "Hero",
					Emote = "PortraitEmoteDepressed",
					Text = "I... {#DialogueItalicFormat}urgh{#PreviousFormat}... I think maybe he does think so, indeed... because, now having met my relatives upon Olympus, I... can definitely see that they can be a bit... vindictive about things." },

				{ Cue = "/VO/Persephone_0064",
					PreLineThreadedFunctionName = "SetupPersephoneMusic",
					PreLineThreadedFunctionArgs = { FullBlast = true },
 					Portrait = "Portrait_Persephone_Calculating_01",
					PostLineThreadedFunctionName = "SurfaceKillHero",
					PostLineFunctionArgs = { WaitTime = 0, MusicEndTime = 60 },
					Text = "Vindictiveness runs in the family, my son. Then, if Olympus is involved, that would explain a lot. I'm here, not there with them, as you can see. But, oh, it seems we'll have to speak some more, when next you return..." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGardenGreeting,
