		InteractTextLineSets =
		{
			-- ending
			PersephoneHomeMeeting01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "Ending01" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Persephone_Home_01",
					-- Indeed.
					{ Cue = "/VO/Persephone_0266" },
				},
				{ Cue = "/VO/ZagreusHome_2686", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "How has it been for you so far, Mother? Reacclimating to all this. You said before it was a struggle. Must be very strange, being back. Being in darkness all the time." },
				{ Cue = "/VO/Persephone_0245",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "You know, I think it's gone all right so far! Thank you for asking, Son. Everyone has been quite welcoming. The darkness is a pleasant change of pace, and while this dress can be a little difficult to move in... I quite like it." },
				{ Cue = "/VO/ZagreusHome_2687", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "That's good to hear. I have to admit I was a little worried you'd be desperate to return to your cottage by now. This place can be a little much." },
				{ Cue = "/VO/Persephone_0246",
					Text = "I'm sure it can. And I do miss my little cottage here and there, but... I think I'm through running away. There may be times when it is necessary, of course. But no matter how far you run, it doesn't make your problems go away. You taught me that, you know." },
				{ Cue = "/VO/ZagreusHome_2688", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteSurprise",
					Text = "I did? But... I tried desperately to run away, myself.... Oh." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGuaranteedGreeting,
			},
			PersephoneHomeMeeting02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01" },
				RequiredFalseFlags = { "PersephoneGoingAway" },

				EndVoiceLines =
				{
					PreLineWait = 0.35,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Never! Kidding. Of course.
					{ Cue = "/VO/ZagreusHome_3340" },
				},
				{ Cue = "/VO/Persephone_0275",
					Text = "Turns out there's still an awful lot to do around here, isn't there, Zagreus? I'm still getting caught up on day-to-day affairs." },
				{ Cue = "/VO/ZagreusHome_3339", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Day-to-day, how can you even tell it's day around here, anyway, Mother?" },
				{ Cue = "/VO/Persephone_0276",
					Text = "Well that was just a guess, now that you mention it. In any case, forgive me if I don't get swooped up in every household detail, all right?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},
			PersephoneHomeMeeting03 =
			{
