					-- I like them, too.
					{ Cue = "/VO/ZagreusHome_3343" },
				},
				{ Cue = "/VO/ZagreusHome_3341", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "So, Mother, all the pomegranates around here... those are your doing?" },
				{ Cue = "/VO/Persephone_0277",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "Not a lot of fruit trees take to this environment, my son. Though, there's something about pomegranates! Maybe it's that their seeds so resemble the gemstones your father likes so much. Or that they don't need much water." },
				{ Cue = "/VO/ZagreusHome_3342", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "...Fruit trees, huh. I grew up around all these poms and never thought much of it. Are there many other types of fruit trees on the surface, then?" },
				{ Cue = "/VO/Persephone_0278",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					PreLineWait = 0.35,
					Text = "Well... I suppose there are a few. But I like pomegranates most of all." },
