				AngleTowardTargetId = 555304,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Sounds good.
					{ Cue = "/VO/ZagreusHome_3346" },
				},
				{ Cue = "/VO/ZagreusHome_3344", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "This garden isn't quite the sight yours was back on the surface, is it, Mother." },
				{ Cue = "/VO/Persephone_0279",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "On the contrary, Zagreus! It has its own unique charm. As each garden does. I was impressed it wasn't left entirely to rot after my absence." },
				{ Cue = "/VO/ZagreusHome_3345", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "It was totally off limits to me. Though I guess Father must have taken care to maintain it as best he could." },
				{ Cue = "/VO/Persephone_0280",
					Text = "He did, all right. Minus the part where you weren't allowed. Though, let's look after it from here, shall we?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
