				},
				{ Cue = "/VO/ZagreusHome_3347", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You know, that painting in Father's... I mean your bedchambers, it turned out rather well, didn't it? Some very talented shades around here." },
				{ Cue = "/VO/Persephone_0281",
					Emote = "PortraitEmoteCheerful",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, it's beautiful! I tried to persuade your father to hang it out in the hall but he's too coy about such things. Though, I like it where it is... a reminder to us, every day and night." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},
			PersephoneHomeMeeting06 =
			{
