				-- RequiredTextLines = {  "DemeterAboutPersephone03", "HadesAboutPersephone01" },
				-- RequiredFalseTextLines = { "HadesAboutPersephone01" },

				EndVoiceLines =
				{
					{
						PreLineWait = 0.4,
						UsePlayerSource = true,
						RequiredMinElapsedTime = 3,
						-- I will. Thank you, Mother.
						{ Cue = "/VO/ZagreusHome_3309" },
