				},
				{ Cue = "/VO/Persephone_0242",
					Text = "Were you able to make it to my cottage on the surface, Zagreus? I hope my lavender patches are doing all right." },
				{ Cue = "/VO/ZagreusHome_3308",
					Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "I, {#DialogueItalicFormat}erm{#PreviousFormat}... I couldn't make it all the way there, Mother. It's like... without your presence on the surface, I'm unable to hang on for very long at all." },
				{ Cue = "/VO/Persephone_0244",
					Text = "{#DialogueItalicFormat}Ah{#PreviousFormat}, I was afraid of that. Well, don't despair. I placed a charm upon my seedlings there that ought to keep them flourishing well on their own. Your duty now is simply to escape. You keep it up!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
