			PersephoneAboutHades01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01" },
				RequiredFalseTextLinesLastRun = { "PersephoneHomeMeeting01" },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				-- [location: garden]
				RequiredFalseTextLinesThisRun = { "NyxAboutDusa04" },
				TeleportToId = 556841,
				TeleportOffsetX = -50,
				TeleportOffsetY = 0,
				AngleTowardTargetId = 555304,
				EndVoiceLines =
				{
					PreLineWait = 0.6,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Sometimes I think he tried.
					{ Cue = "/VO/ZagreusHome_3311" },
				},
				{ Cue = "/VO/Persephone_0247",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					Text = "Tell me something, Zagreus... sometimes I fear... do you hate him? I mean your father. Regardless of his intentions... it sounds as though he made your life extraordinarily difficult. All his pent-up fear and anger... you bore the brunt of it." },
				{ Cue = "/VO/ZagreusHome_3310", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Do I hate him...? Mother, I don't know that I hate anybody, really. The tales of him and the Olympians rending their immortal Titan parents to bits... I can't relate to how they must have felt. But, I resented him, for sure. And... I don't know if I'll ever get over that, entirely." },
				{ Cue = "/VO/Persephone_0248",
					Portrait = "Portrait_Persephone_Queen_Apprehensive_01",
					Text = "I understand. I think that when Hades told you he asked no forgiveness, he understood as well, and meant it. Well, for all his failings, I'm thankful that he did not teach you how to hate. For your own sake, if not for his." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},
			PersephoneAboutHades02 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneAboutHades01" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneAboutHades01" }, Count = 3 },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				-- [location: garden]
				RequiredFalseTextLinesThisRun = { "NyxAboutDusa04" },
				TeleportToId = 556841,
				TeleportOffsetX = -50,
				TeleportOffsetY = 0,
				AngleTowardTargetId = 555304,
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Yes. I'm glad for you. 
					{ Cue = "/VO/ZagreusHome_3313" },
				},
				{ Cue = "/VO/Persephone_0249",
					Portrait = "Portrait_Persephone_Queen_Apprehensive_01",
					Text = "So, how has it been between you and your father lately, Zagreus? I know the two of you have lots of work ahead of you. Especially him." },
				{ Cue = "/VO/ZagreusHome_3312", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Well, it's been all right, I guess. I think he's set in some of his ways. But at least we have some sort of understanding now. Even if he acts like everything is still the same when I'm out there on the job." },
				{ Cue = "/VO/Persephone_0250",
					Text = "He says it's so you put forth your fullest effort every time. All this is a big adjustment for him as well. Not that it's any excuse for how he's treated you. Are you... all right, seeing us together?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},
			PersephoneAboutHades03 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneAboutHades02", "PersephoneGift02", "HadesGift03" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneAboutHades02" }, Count = 3 },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				AreIdsNotAlive = { 370006 },

				EndVoiceLines =
				{
					PreLineWait = 0.3,
					ObjectType = "NPC_Persephone_Home_01",
					-- Hahaha, no. You're plenty.
					{ Cue = "/VO/Persephone_0253" },
				},
				{ Cue = "/VO/ZagreusHome_3314", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "{#DialogueItalicFormat}Erm{#PreviousFormat}, so are things with you and Father... everything all right, so far?" },
				{ Cue = "/VO/Persephone_0251",
					Text = "Things are good, Zagreus. I'm from Olympus... all the relationships I've ever known have been the complicated sort. But with your father... despite our different dispositions and interests... beneath it all, we share a simple understanding." },
				{ Cue = "/VO/ZagreusHome_3315", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "And... what understanding is that? If you can share." },
				{ Cue = "/VO/Persephone_0252",
					PreLineWait = 0.35,
					Text = "That we're all we've got. And that we love each other. Through thick and thin." },
				{ Cue = "/VO/ZagreusHome_3316", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineWait = 0.35,
					Emote = "PortraitEmoteSurprise",
					Text = "Wait, you're not... you're not thinking about having more children, are you...?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			PersephoneAboutNyx01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01", "NyxWithPersephone01" },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				RequiredFalseTextLinesLastRun = { "NyxWithPersephone01" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- I'm sure she did.
					{ Cue = "/VO/ZagreusHome_3323" },
				},
				{ Cue = "/VO/Persephone_0258",
					Text = "Nyx has a very steady presence, doesn't she? I want to say she hasn't changed a bit since before you were born, but... I know that she's been through a lot." },
				{ Cue = "/VO/ZagreusHome_3322", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "She's always managed, and quite gracefully at that. Never seen her break a sweat over anything. Even when I confronted her about you. Though, you two seem closer than I realized, I think." },
				{ Cue = "/VO/Persephone_0259",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					Text = "Well, your father isn't always the conversationalist, and Nyx... she was the first chthonic god I ever met. I suppose she welcomed having another lady about the House." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},
			PersephoneAboutNyx02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneAboutNyx01", "PersephoneGift06" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneGift06", "PersephoneAboutNyx01" }, Count = 2 },
				RequiredFalseFlags = { "PersephoneGoingAway" },

				EndVoiceLines =
				{
					PreLineWait = 0.3,
					UsePlayerSource = true,
					-- Hahaha, it's a deal.
					{ Cue = "/VO/ZagreusHome_3325" },
				},
				{ Cue = "/VO/Persephone_0260",
					Portrait = "Portrait_Persephone_Queen_Apprehensive_01",
					Text = "Zagreus... how am I doing? As a mother. Tell me honestly. Nyx has had so much to share about raising you. Foods you hated or enjoyed, your natural abilities, your friendships... she knows everything! Sometimes I wonder how I can ever compete?" },
				{ Cue = "/VO/ZagreusHome_3324", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Mother, I think you're doing great. And I'm sure you know it's not a competition between you and Nyx. I just get the benefit of having two great mothers rather than one. Or zero." },
				{ Cue = "/VO/Persephone_0261",
					Text = "Thank you, my son. I know I wasn't there for most of the hard part, but... I'm here, now. Though, when I inevitably, inadvertently trample all over your feelings at some point, please tell me, all right?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			PersephoneAboutCharon01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01" },
				RequiredFalseFlags = { "PersephoneGoingAway" },

				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- No idea what a calling card is, but, OK!
					{ Cue = "/VO/ZagreusHome_3338" },
				},
				{ Cue = "/VO/ZagreusHome_3337", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Mother, one thing I was wondering about was, how did you summon Charon to your surface residence? Especially without, you know, being dead." },
				{ Cue = "/VO/Persephone_0274",
					Portrait = "Portrait_Persephone_Queen_Apprehensive_01",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, why... he gave me his calling card after he graciously helped get me out of here the first time. No doubt at Nyx's request, but still. Never thought I'd use it, but... I still kept it. Just in case." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},			

			PersephoneBackstory01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01", "DemeterAboutPersephone03" },
				-- RequiredTextLines = {  "DemeterAboutPersephone03", "HadesAboutPersephone01" },
				-- RequiredFalseTextLines = { "HadesAboutPersephone01" },
				RequiredFalseFlags = { "PersephoneGoingAway" },

				EndVoiceLines =
				{
					{
						PreLineWait = 0.35,
						UsePlayerSource = true,
						-- At least we've some of that in us.
						{ Cue = "/VO/ZagreusHome_3305" },
					},
					{
						PreLineWait = 0.35,
						ObjectType = "NPC_Persephone_Home_01",
						-- Indeed, my son.
						{ Cue = "/VO/Persephone_0241" },
					},
				},
				{ Cue = "/VO/ZagreusHome_3487", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Mother, you'll forgive my asking, but... your mother used to call you by a different name, didn't she?" },
				{ Cue = "/VO/Persephone_0238",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					Emote = "PortraitEmoteDepressed",
					Text = "{#DialogueItalicFormat}Ah{#PreviousFormat}, don't remind me. Imagine if I called you {#DialogueItalicFormat}Zaggie-poo {#PreviousFormat}from when you were born to this very moment, that's how that name felt to me. I much preferred the name my father gave to me." },
				{ Cue = "/VO/ZagreusHome_3303", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Your mortal father, right...? So he called you Persephone. What was he like?" },
				{ Cue = "/VO/Persephone_0239",
					Text = "He was kind to me. And funny! Little bringer of destruction, I was to him. I liked that. He was right, I was a terror. But then he passed. I was very little still. My mother never spoke of him again." },
				{ Cue = "/VO/ZagreusHome_3304", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Demeter mentioned him a bit to me. She seems... I don't know, but I don't sense that she regrets the relationship or anything. Just sad about the outcome I think." },
				{ Cue = "/VO/Persephone_0240",
					Text = "I suppose I can see that. I thought I might end up like Father... growing old, wasting away. Then the time flew by, and I realized I was closer to Mother's side. Mortals certainly don't have it easy. But their limited days, it means... they live their lives to the fullest, I think." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			PersephoneAboutEternalWinter01 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 3 },
				RequiredFalseTextLines = { "PersephoneReturnsFromOlympus01" },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Persephone_Home_01",
					-- We can, and shall.
					{ Cue = "/VO/Persephone_0255" },
				},
				{ Cue = "/VO/ZagreusHome_3317", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Mother, just how long has it been snowing on the surface, do you know? Because, it seems to me that Demeter... Grandmother, I should say, she... well, it's been very cold up there each time I've been." },
				{ Cue = "/VO/Persephone_0254",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					Text = "I was afraid of that... I wanted to believe the snow about my cottage, it was native to the climate there, but... seems that Mother took my absence harder than I thought. Oh, what have I done. All those poor people." },
				{ Cue = "/VO/ZagreusHome_3318", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "It's not your fault. You couldn't have known. And we can fix this, right?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			PersephoneAboutNyxChaosReunionQuest01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01", "NyxChaosReunionQuestComplete" },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- Least I could do.
					{ Cue = "/VO/ZagreusHome_3332" },
				},
				{ Cue = "/VO/Persephone_0268",
					Text = "Nyx told me that you helped her reconnect with her parent... after a long, long time. That was very kind of you. She never asks for anything. But we all owe her a lot, don't we?" },
				{ Cue = "/VO/ZagreusHome_3331", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I think so. If not for her... I can't even imagine, really. Did Nyx ever speak to you of Chaos, before I was born?" },
				{ Cue = "/VO/Persephone_0269",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					Text = "Not really, no. Nyx is very private when it comes to personal matters. But she has feelings and desires same as anybody. Well, mostly, but the point is... I know this meant a lot to her." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			PersephoneAboutMyrmidonReunionQuest01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01", "MyrmidonReunionQuestComplete" },
				RequiredCosmetics = { "AchillesPatroclusQuestItem" },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- Thank you. He more than deserved it.
					{ Cue = "/VO/ZagreusHome_3333" },
				},
				{ Cue = "/VO/Persephone_0270",
					Text = "It seems Achilles had a complicated arrangement with your father. I understand you simplified things for the better, there, and he was able to visit Elysium at last. Well done." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			PersephoneAboutSingersReunionQuest01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01", "OrpheusAboutSingersReunionQuestComplete01" },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- I'd no idea.
					{ Cue = "/VO/ZagreusHome_3336" },
				},
				{ Cue = "/VO/Persephone_0271",
					Text = "So Orpheus was finally able to reunite with Eurydice because of you, my son? {#DialogueItalicFormat}Ah{#PreviousFormat}, I'm so glad! Your father's arrangement with him was quite severe. Though he did ask for a lot, for a mortal soul to return to the land of the living..." },
				{ Cue = "/VO/ZagreusHome_3334", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Is it even possible, for Father to return a mortal back to life if he so chose? Just by letting their shade escape, or something?" },
				{ Cue = "/VO/Persephone_0272",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					Text = "Oh, I've no idea, probably not? But we were so moved by Orpheus' plea and song, that, well... I think your father took pity on him." },
				{ Cue = "/VO/ZagreusHome_3335", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Wait, {#DialogueItalicFormat}we {#PreviousFormat}were so moved? You were there when Orpheus came to try and rescue Eurydice?" },
				{ Cue = "/VO/Persephone_0273",
					PreLineAnim = "PersephoneUnderworld_Vulnerable",
					Text = "I was. Your father knew I loved their music. Imagine my surprise to see him as our court musician, here!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			PersephoneAboutThanatosRelationship01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01", "PersephoneGift04" },
				MinRunsSinceAnyTextLines = { TextLines = { "BecameCloseWithThanatos01Than_GoToHim", "BecameCloseWithThanatos01_BThan_GoToHim" }, Count = 10 },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- They are. Thank you, Mother.
					{ Cue = "/VO/ZagreusHome_3327" },
				},
				{ Cue = "/VO/Persephone_0263",
					Text = "I always fancied Thanatos, you know. So put together and everything! I'm pleased to see you've grown so close. Things good between you two?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			PersephoneAboutDusaRelationship01 =
			{
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01", "BecameCloseWithDusa01" },
				RequiredFalseFlags = { "DusaFiredFromJob", "DusaNotYetReHired", "PersephoneGoingAway" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 10 },
				AreIdsAlive = { 390080 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- She does. She's great, Mother.
					{ Cue = "/VO/ZagreusHome_3328" },
				},
				{ Cue = "/VO/Persephone_0264",
					Text = "That Dusa sure has quite the uplifting presence around here, doesn't she, Zagreus? Sometimes I see you chatting and it warms my heart." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			PersephoneAboutDusaFiring01 =
			{
				PlayOnce = true,
				Priority = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneHomeMeeting01", "DusaVsNyx04" },
				RequiredFalseTextLines = { "DusaWithNyx03" },
				RequiredTrueFlags = { "DusaFiredFromJob" },
				RequiredFalseFlags = { "PersephoneGoingAway" },
				AreIdsNotAlive = { 390080 },

				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Urgh... yeah....
					{ Cue = "/VO/ZagreusHome_3329" },
				},
				{ Cue = "/VO/Persephone_0265",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					Text = "Zagreus, I know you're upset about what happened between Nyx and Dusa. Before you ask... I need to respect Nyx's space and authority on this one, all right?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},
			PersephoneAboutDusaFiring02 =
			{
				PlayOnce = true,
				Priority = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneAboutDusaFiring01", "DusaWithNyx03" },
				AreIdsAlive = { 390080 },
				RequiredFalseFlags = { "PersephoneGoingAway" },

				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- Most definitely, yes.
					{ Cue = "/VO/ZagreusHome_3330" },
				},
				{ Cue = "/VO/Persephone_0267",
					Text = "I saw that Dusa had returned. I trust Nyx's judgment, though I must admit I was pleased to see she decided to give her another chance." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			-- ending / -- epilogue
			PersephoneAboutOlympianReunionQuest01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				StatusAnimation = "StatusIconWantsToTalkImportant",
				RequiredTextLines = {
					"PersephoneHomeMeeting01",
					"HadesAboutOlympianReunionQuest01A",
					"HadesPostEnding01",
					"DemeterAboutPersephone01",
					"DemeterAboutPersephone02",
					"DemeterAboutPersephone03",
					"DemeterAboutPersephone04",
					"ZeusAboutPersephone01",
					"ZeusAboutPersephone02"
				},
				RequiredMinAnyTextLines =
				{ 	
					TextLines = 
					{
						"ZeusGift07",
						"PoseidonGift07",
						"AthenaGift07",
						"AphroditeGift07",
						"AresGift07",
						"ArtemisGift07",
						"DionysusGift07",
						"HermesGift08",
						"DemeterGift06"
					},
					Count = 6
				},
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneHomeMeeting01" }, Count = 10 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					RequiredMinElapsedTime = 3,
					-- I know what you mean. I'll let you know when it's done.
					{ Cue = "/VO/ZagreusHome_2705" },
				},
				{ Cue = "/VO/Persephone_0115",
					Text = "All right. We've matters with Olympus to resolve. They don't know I'm here, most of them, besides Zeus, maybe Athena. They think you're Nyx's son rather than mine, and that your goal is still to join them on their mountaintop." },
				{ Cue = "/VO/ZagreusHome_2697", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "What a mess. What can we do? The gods of Olympus have all been very good to me, for the most part. I don't feel great about deceiving them. They keep trying to help me escape!" },
				{ Cue = "/VO/Persephone_0116",
					PreLineAnim = "PersephoneUnderworld_Dismiss",
					Text = "What can we do? We can make peace with them. And let them know the truth. So much of it as they would tolerate. Diplomacy is not your father's strong suit. But I am going to need you two to do exactly as I say. Do you understand?" },
				{ Cue = "/VO/ZagreusHome_2698", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Yes, Mother. So what do you intend to do, and how do I fit in?" },
				{ Cue = "/VO/Persephone_0117",
					Text = "We're going to invite all of them to a little family gathering! Here. And you shall personally deliver them their invitations, Zagreus." },
				{ Cue = "/VO/ZagreusHome_2699", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Mother, are you serious? So, we're just going to have a bash with them, and they'll just see you after all this time, and be fine with everything?" },
				{ Cue = "/VO/Persephone_0118", Portrait = "Portrait_Persephone_Queen_Calculating_01",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "They certainly shall not be fine with everything. But, Hades and I, we'll be prepared to tell them what they need to hear. Don't worry, I shall do the talking." },
				{ Cue = "/VO/ZagreusHome_2700", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					Text = "What are you going to say?" },
				{ Cue = "/VO/Persephone_0120",
					Emote = "PortraitEmoteCheerful",
					PreLineAnim = "PersephoneUnderworld_Vulnerable",
					Text = "Oh, you know! That we met, fell in love, eloped. Had a child. A child who, once grown, sought desperately to reconnect with his extended family. To the chagrin of his grim lord father..." },
				{ Cue = "/VO/ZagreusHome_2701", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathy_Return", PreLineAnimTarget = "Hero",
					Text = "That's... that's all true. As individual statements, at least." },
				{ Cue = "/VO/Persephone_0121",
					PreLineWait = 0.35,
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "...Fortunately, our loving and dutiful son at last persuaded his mother and father to reveal their secretive relationship, and make an effort to rejoin their great family." },
				{ Cue = "/VO/ZagreusHome_2702", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "Your mother won't be happy that you left." },
				{ Cue = "/VO/Persephone_0122", Portrait = "Portrait_Persephone_Queen_Calculating_01",
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteSparkly", WaitTime = 11.0, Portrait = "Portrait_Persephone_Queen_01" },
					Text = "I'll deal with her. Perhaps I'll have to visit her and the rest of them from time to time, if that's what it shall take. But I'll be back. I've no choice! Did you know that if you eat the seeds of an Underworld pomegranate, then you cannot leave for one month out of the year, per seed?" },
				{ Cue = "/VO/ZagreusHome_2703", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					Emote = "PortraitEmoteSurprise",
					PreLineAnim = "ZagreusTalkDenial_Full", PreLineAnimTarget = "Hero",
					Text = "What? No, I never knew that." },
				{ Cue = "/VO/Persephone_0123",
					Text = "Neither do they." },
				{ Cue = "/VO/ZagreusHome_2704", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "How soon is this family gathering you have in mind going to take place?" },
				{ Cue = "/VO/Persephone_0124",
					Text = "Soon after you invite them all, I hope. Now, go. And trust in me, all right? We have a lot to learn of trust, I think." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGuaranteedGreeting,
			},
			PersephoneAboutOlympianReunionQuest02 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneAboutOlympianReunionQuest01" },
				RequiredFalseTextLines = { "OlympianReunionQuestComplete" },
				RequiredMinAnyTextLines = { TextLines = 
					{
						"ZeusAboutOlympianReunionQuest01",
						"PoseidonAboutOlympianReunionQuest01",
						"AthenaAboutOlympianReunionQuest01",
						"AresAboutOlympianReunionQuest01",
						"AphroditeAboutOlympianReunionQuest01",
						"ArtemisAboutOlympianReunionQuest01",
						"DionysusAboutOlympianReunionQuest01",
						"DemeterAboutOlympianReunionQuest01",
						"HermesAboutOlympianReunionQuest01",
						"ChaosAboutOlympianReunionQuest01"
					}, Count = 3 },
				RequiredMaxAnyTextLines = { TextLines = 
					{
						"ZeusAboutOlympianReunionQuest01",
						"PoseidonAboutOlympianReunionQuest01",
						"AthenaAboutOlympianReunionQuest01",
						"AresAboutOlympianReunionQuest01",
						"AphroditeAboutOlympianReunionQuest01",
						"ArtemisAboutOlympianReunionQuest01",
						"DionysusAboutOlympianReunionQuest01",
						"DemeterAboutOlympianReunionQuest01",
						"HermesAboutOlympianReunionQuest01",
						"ChaosAboutOlympianReunionQuest01"
					}, Count = 9 },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					RequiredMinElapsedTime = 3,
					UsePlayerSource = true,
					-- You got it, Mother.
					{ Cue = "/VO/ZagreusHome_2707" },
				},
				{ Cue = "/VO/Persephone_0125",
					Text = "How goes the doling out of invitations, Zagreus? Have the Olympians been amenable to the idea?" },
				{ Cue = "/VO/ZagreusHome_2706", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Not just amenable, Mother. Positively intrigued. I can't believe you scrambled the invitations in such a way that, only with the lot of them combined will they get all of the details!" },
				{ Cue = "/VO/Persephone_0126",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "A bit of collaboration seemed in the spirit of things. We're a great, big family, Zagreus. Past time we started acting like one, no? Now, keep it up. And thank you for your help." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},
			PersephoneAboutOlympianReunionQuest03 =
			{
				Priority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneAboutOlympianReunionQuest01", "PersephoneAboutOlympianReunionQuest02", "ChaosGift06" },
				RequiredFalseTextLines = { "OlympianReunionQuestComplete", "ChaosAboutOlympianReunionQuest01" },
				EndVoiceLines =
				{
					PreLineWait = 0.3,
					RequiredMinElapsedTime = 3,
					ObjectType = "NPC_Persephone_Home_01",
					-- Mm-hm!
					{ Cue = "/VO/Persephone_0096" },
				},
				{ Cue = "/VO/ZagreusHome_2708", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Mother, this family gathering you're planning... are all our relatives welcome? Because I was wondering if I could extend the invitation to a distant ancestor... you know... Chaos." },
				{ Cue = "/VO/Persephone_0127",
					Text = "Nyx's parent... you've been in contact, then? Look, if you feel it's the right thing to do, I trust your judgment. The spirit of this event is to bring the family together." },
				{ Cue = "/VO/ZagreusHome_2709", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "Thanks. I doubt Chaos would be able to attend, though would still appreciate the thought." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			-- epilogue
			OlympianReunionQuestComplete =
			{
				Partner = "NPC_Hades_01",
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				StatusAnimation = "StatusIconWantsToTalkImportant",
				RequiredTextLines = { "PersephoneAboutOlympianReunionQuest01", "ZeusAboutOlympianReunionQuest01", "PoseidonAboutOlympianReunionQuest01", "AthenaAboutOlympianReunionQuest01", "AresAboutOlympianReunionQuest01", "AphroditeAboutOlympianReunionQuest01", "ArtemisAboutOlympianReunionQuest01", "DionysusAboutOlympianReunionQuest01", "DemeterAboutOlympianReunionQuest01", "HermesAboutOlympianReunionQuest01" },
				TeleportToId = 370006,
				TeleportOffsetX = 195,
				TeleportOffsetY = 55,
				AngleTowardTargetId = 391553,
				-- BlockDistanceTriggers = true,

				EndVoiceLines =
				{
					PreLineWait = 0.6,
					ObjectType = "NPC_Persephone_Home_01",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					-- Sounds good. I'll see you then.
					{ Cue = "/VO/Persephone_0337" },
				},
				{ Cue = "/VO/ZagreusHome_2710", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "It's done, Mother! Father. The invitations. Personally sent to everyone with whom I've been in contact. So, what do we do now?" },

				{ Cue = "/VO/Persephone_0128",
					Emote = "PortraitEmoteCheerful",
					Text = "Excellent, Zagreus! Congratulate him, Hades! On a job well done." },

				{ Cue = "/VO/Hades_0923", Speaker = "NPC_Hades_01", Portrait = "Portrait_Hades_Default_01",
					Text = "{#DialogueItalicFormat}Grr. {#PreviousFormat}Persephone, how can you be so certain your request was executed to your satisfaction?" },

				{ Cue = "/VO/Persephone_0129",
					PreLineAnim = "PersephoneUnderworld_Dismiss",
					Text = "It's known as trust, Hades. Or faith? They're similar. Try one, sometime! It'll be good for you. As for what we do next... why, we've a great, big feast we must prepare!" },

				{ Cue = "/VO/Hades_0924", Speaker = "NPC_Hades_01", Portrait = "Portrait_Hades_Default_01",
					PreLineAnim = "Hades_HouseFistSlam", PreLineAnimTarget = 370006,
					PreLineThreadedFunctionName = "PlayEmoteAnimFromSource", PreLineThreadedFunctionArgs = { Emote = "PortraitEmoteDepressed", Portrait = "Portrait_Hades_Averted_01", WaitTime = 7.5 },
					Text = "This House is not a suitable location for such guests! Dionysus, alone, shall bring ruin to this place! Or... {#DialogueItalicFormat}urgh{#PreviousFormat}... the faith thing that you said." },

				{ Cue = "/VO/Persephone_0130",
					PortraitExitAnimation = "Portrait_Persephone_Queen_01_Exit",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "Yes, good! Now then... we have a lot of work to go around, so why don't we call everyone on staff, and let them in on this, and dole out suitable responsibilities, all right?" },

				{ Cue = "/VO/Hades_0925", Speaker = "NPC_Hades_01", Portrait = "Portrait_Hades_Averted_01",
					PreLineWait = 0.6,
					Text = "All right...." },

				{ Cue = "/VO/ZagreusHome_2711", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					Text = "All right." },

				{ Cue = "/VO/Persephone_0131",
					EndSound = "/Leftovers/Menu Sounds/EmoteThoughtful",
					PostLineFunctionName = "EpilogueScenePresentation",
					PostLineFunctionArgs = {
						EpilogueStartDelay = 2.0, StartDelay = 2.5, FadeInTime = 1.5,
						FadeOutWait = 45, PanDuration = 35,
						SecretMusic = "/Music/MusicExploration1_MC",
						PortraitGlobalVoiceLines = "StorytellerEpilogueVoiceLines", },
					-- SpawnOnId = 422138, AngleTowardHero = true,
					-- TeleportHeroToId = 555690, AngleHeroTowardTargetId = 421926,
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "All right!" },

				{ Cue = "/VO/Persephone_0332",
					FadeOutTime = 0.5, FullFadeTime = 0.1, TeleportToId = 391472, AngleTowardTargetId = 423390, TeleportOffsetX = -100, TeleportOffsetY = -20, TeleportHeroToId = 391472, AngleHeroTowardTargetId = 391545, FadeInTime = 0.5,
					PreLineWait = 1.0,
					PreLineAnim = "PersephoneUnderworld_Vulnerable",
					Emote = "PortraitEmoteCheerful",
					Text = "...{#DialogueItalicFormat}Whew{#PreviousFormat}! I'd say that went about as well as could have been expected! And the cleanup wasn't all that bad. For the most part." },

				{ Cue = "/VO/ZagreusHome_3709", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "We're fortunate Uncle Zeus seemed to catch on right away, and went along with it. But, this whole elaborate tale you spun... I remember how hurt I felt when I found out Father lied to me about you. Now we're lying to Olympus. What if they find out? Even if they don't... I don't like it." },

				{ Cue = "/VO/Persephone_0333",
					Portrait = "Portrait_Persephone_Queen_Apprehensive_01",
					PreLineAnim = "PersephoneUnderworld_Vulnerable",
					Text = "Zagreus... I may not be an expert... but here's what I've come to understand about how families work. At least our own. The thing is... how we speak to one another is even more important than the words we use. I think, deep down, everyone in our family knows this." },

				{ Cue = "/VO/ZagreusHome_3710", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "You're saying they all knew you weren't being perfectly honest, and went along with it anyway? I guess the pomegranate thing in particular was a bit of a stretch." },

				{ Cue = "/VO/Persephone_0334",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					Text = "The common ground in our family is that we want peace. Besides Ares, though even he would rather the conflict occur elsewhere. That elaborate tale... it's less a deception, and more a concession. A way of saying let's move on from the past, and start anew. No hard feelings." },

				{ Cue = "/VO/ZagreusHome_3711", Portrait = "Portrait_Zag_Defiant_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenial_Full", PreLineAnimTarget = "Hero",
					Text = "You don't have hard feelings? What about Father? What about Demeter?" },

				{ Cue = "/VO/Persephone_0335",
					Portrait = "Portrait_Persephone_Queen_01",
					PreLineAnim = "PersephoneUnderworld_Vulnerable",
					Text = "Well, let's see. My hard feelings are nothing next to the joy of having my son in my life. Your father never imagined his relationship with me could be out in the open. And my mother... she's just glad I'm alive." },

				{ Cue = "/VO/ZagreusHome_3712", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "And Uncle Zeus is glad things didn't turn out worse for {#DialogueItalicFormat}him{#PreviousFormat}. I get the sense he acts a bit impulsively at times, and deals with the consequences later." },

				{ Cue = "/VO/Persephone_0336",
					Portrait = "Portrait_Persephone_Queen_01",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "What can I tell you, Zagreus? We're family. You said it yourself to me before: We're stuck with one another. That any of us like each other in the slightest... it's a wonder and a blessing, I think." },

				{ Cue = "/VO/ZagreusHome_3713", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					PostLineThreadedFunctionName = "ActivateOlympusSculpture",
					PostLineFunctionName = "ConcludeEpilogue",
					Text = "Yeah. I know what you mean. Thanks, Mother. Think I'll be heading out again soon. See you when I get back?" },
			},

			PersephoneLeavesToOlympus01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "OlympianReunionQuestComplete" },

				EndVoiceLines =
				{
					PreLineWait = 0.4,
					ObjectType = "NPC_Persephone_Home_01",
					-- I'm going to miss you too, Son.
					{ Cue = "/VO/Persephone_0139" },
				},
				{ Cue = "/VO/ZagreusHome_3269", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I have to say, Mother, that was a clever plan. Convincing Father to finally let somebody have some fun around here." },
				{ Cue = "/VO/Persephone_0133",
					Emote = "PortraitEmoteCheerful",
					PreLineFunctionName = "SetPersephoneAway",
					Text = "{#DialogueItalicFormat}Haha{#PreviousFormat}, well! I'm glad it worked out for the best. There's just one thing that happened that I ought to tell you about, Son. No secrets between us, and all. This... is quite difficult for me to say." },
				{ Cue = "/VO/ZagreusHome_3270", Portrait = "Portrait_Zag_Serious_01", Speaker = "CharProtag",
					Text = "It's about Demeter, isn't it. You two were talking half the night. She wants you to come home with her, doesn't she? You'll always be her little one, something like that?" },
				{ Cue = "/VO/Persephone_0134",
					Portrait = "Portrait_Persephone_Queen_Apprehensive_01",
					Text = "Indeed... something like that. You're quite perceptive, have I ever told you, Zagreus? But yes, my mother, she was quite conflicted. Didn't know whether to smite me or to hold me, maybe both. And, yes. She asked that I come home... to my responsibilities." },
				{ Cue = "/VO/ZagreusHome_3271", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag", PreLineWait = 0.35,
					PreLineAnim = "ZagreusTalkDenial_Full", PreLineAnimTarget = "Hero",
					Text = "So... you're leaving...? No, Mother, you can't be leaving, you just got here!" },
				{ Cue = "/VO/Persephone_0135",
					Text = "Oh, it's not as bad as that, it's just... if we are to keep the peace down here, then, I must spend some time up there. Every so often! But, I shall always return, in no time at all. I promised Hades, and I promise you." },
				{ Cue = "/VO/ZagreusHome_3272", Portrait = "Portrait_Zag_Empathetic_01", Speaker = "CharProtag",
					Text = "Well then... when are you going to go?" },
				{ Cue = "/VO/Persephone_0136",
					PreLineWait = 0.35,
					Text = "Soon, Zagreus. Perhaps by the time you're back." },
				{ Cue = "/VO/ZagreusHome_3273", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusInteractionThoughtful", PreLineAnimTarget = "Hero",
					Text = "I don't suppose... I can come with you, can I?" },
				{ Cue = "/VO/Persephone_0138",
					Text = "{#DialogueItalicFormat}Tsk{#PreviousFormat}, you know the surface isn't good for you! Besides, you're needed here. But, if you really want to see it for yourself, someday, who knows? Perhaps eventually we'll figure out a way." },
				{ Cue = "/VO/ZagreusHome_3274", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "I'm going to miss you, Mother." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
			},

			-- first return from Olympus & eternal winter resolved
			PersephoneReturnsFromOlympus01 =
			{
				SuperPriority = true,
				PlayOnce = true,
				UseableOffSource = true,
				RequiredTextLines = { "PersephoneLeavesToOlympus01" },
				EndVoiceLines =
				{
					PreLineWait = 0.4,
					UsePlayerSource = true,
					-- At least she forgave.
					{ Cue = "/VO/ZagreusHome_3321" },
				},
				{ Cue = "/VO/ZagreusHome_3486", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkDenialStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkDenialReturnToIdle", PostLineAnimTarget = "Hero",
					Text = "Welcome home, Mother! How was your trip, things go all right with you and Demeter? With... Grandmother. Still getting used to thinking of her that way." },
				{ Cue = "/VO/Persephone_0256",
					Portrait = "Portrait_Persephone_Queen_Calculating_01",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "You know, it's funny... I think she was so overwhelmed to see me again that maybe something finally changed in her. I always thought she was utterly unyielding. She asked me if I truly came here by choice, and I put her at ease." },
				{ Cue = "/VO/ZagreusHome_3320", Portrait = "Portrait_Zag_Default_01", Speaker = "CharProtag",
					PreLineAnim = "ZagreusTalkEmpathyStart", PreLineAnimTarget = "Hero",
					PostLineAnim = "ZagreusTalkEmpathy_Return", PostLineAnimTarget = "Hero",
					Text = "Ah, that's a relief. Grandmother can be rather intense, but... she's been kind to me, and very helpful, too. Do you suppose she'll ease up on the whole eternal winter thing there on the surface, now that she knows you're safe?" },
				{ Cue = "/VO/Persephone_0257",
					Text = "I asked. She told me that she would. Warmth shall return to the surface. Some of the time, at least. Likely on a rigid schedule, knowing her. And perhaps not in the vicinity of our domain. She's not the sort to forgive and simply forget." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGuaranteedGreeting,
			},

			PersephoneWithHades01 =
			{
				Priority = true,
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "PersephoneHomeMeeting01" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 6 },
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "PersephoneGoingAway" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				-- In Garden
				RequiredFalseTextLinesThisRun = { "NyxAboutDusa04" },
				TeleportWithId = 370006,
				TeleportToId = 555689,
				TeleportOffsetX = 195,
				TeleportOffsetY = 55,
				AngleTowardTargetId = 391553,
				InteractDistance = 850,
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines =
				{
					PreLineWait = 0.6,
					ObjectType = "NPC_Persephone_Home_01",
					-- Well, we need them.
					{ Cue = "/VO/Persephone_0202" },
				},
				{ Cue = "/VO/Persephone_0199", PreLineWait = 0.35,
					PreLineAnim = "PersephoneUnderworld_Dismiss",
					Text = "...{#DialogueItalicFormat}Oh{#PreviousFormat}, come on, Hades! Mortals are much more resilient than that! What, you think they'll show up at your gates here, all at once? They have our family looking out for them." },

				{ Cue = "/VO/Hades_0928", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineFunctionName = "HadesArmsCross",
					Text = "That is what I'm afraid of. Do you truly believe my brothers and the rest shall take the sort of care of them while they draw breath, as we do here after they have expired?" },

				{ Cue = "/VO/Persephone_0200",
					PreLineFunctionName = "HadesArmsUnCross",
					Text = "It's true we have to look out for them indefinitely, while Olympus need only look out for them for a little while. But our relatives are always watching from their mountain! And if they do turn a blind eye once in a while... mortals aren't bad at fending for themselves." },

				{ Cue = "/VO/Hades_0929", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineFunctionName = "HadesArmsCross",
					Text = "But they are never satisfied! Always begging us to provide more, and more." },

				{ Cue = "/VO/Persephone_0201",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "They {#DialogueItalicFormat}like {#PreviousFormat}us, Hades! They praise us, tell one another about us. What do you expect? Would you rather they forgot about us?" },

				{ Cue = "/VO/Hades_0930", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineFunctionName = "HadesArmsUnCross",
					Text = "...No. No, I suppose not. But they {#DialogueItalicFormat}need {#PreviousFormat}us!" },
			},
			PersephoneWithHades02 =
			{
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "PersephoneWithHades01", "PersephoneGift02", "HadesGift02" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneWithHades01", "PersephoneWithHades03", "PersephoneWithHades04" }, Count = 5 },
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "PersephoneGoingAway" },
				StatusAnimation = false,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportToId = 370006,
				TeleportOffsetX = 195,
				TeleportOffsetY = 55,
				AngleTowardTargetId = 391553,
				InteractDistance = 400,
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines =
				{
					PreLineWait = 1.0,
					ObjectType = "NPC_Hades_01",
					-- ...He has.
					{ Cue = "/VO/Hades_0933" },
				},
				{ Cue = "/VO/Hades_0931",
					Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineWait = 0.35,
					Text = "...And what of the complaints from Elysium? They wanted the lawns thoroughly manicured for their athletic contests." },

				{ Cue = "/VO/Persephone_0203",
					PreLineAnim = "PersephoneUnderworld_Dismiss",
					Text = "Already taken care of. Though, the flooding in Asphodel, {#DialogueItalicFormat}hm{#PreviousFormat}, that doesn't look like something we can clear up for quite some time. Fortunately, the residents seem to be getting accustomed to it." },

				{ Cue = "/VO/Hades_0932", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "There have been fewer reports about it as of late. And what of the Satyr problem? Blasted things are rampant up there, still." },

				{ Cue = "/VO/Persephone_0204",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					PostLineAnim = "Hades_HouseWritingLoop_IdleLoop_Start", PostLineAnimTarget = 370006,
					Text = "Well, they're probably contained up there, at least. And Zagreus has been keeping them in check, hasn't he?" },
			},
			PersephoneWithHades03 =
			{
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "PersephoneHomeMeeting01" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneWithHades01", "PersephoneWithHades02", "Ending01" }, Count = 5 },
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "PersephoneGoingAway" },
				UseText = "UseListenNPC",
				StatusAnimation = false,
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				-- In Garden
				RequiredFalseTextLinesThisRun = { "NyxAboutDusa04" },
				TeleportWithId = 370006,
				TeleportToId = 555689,
				TeleportOffsetX = 195,
				TeleportOffsetY = 55,
				AngleTowardTargetId = 391553,
				InteractDistance = 850,
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines =
				{
					PreLineWait = 0.9,
					ObjectType = "NPC_Hades_Story_01",
					-- Our best... that I can promise you.
					{ Cue = "/VO/Hades_0937" },
				},
				{ Cue = "/VO/Hades_0934",
					PreLineThreadedFunctionName = "StopHadesInGardenStatusSpeaking",
					Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					PreLineWait = 0.35,
					Text = "...Well, be that as it may... if they pester you at all, do let me know, and I shall have them dealt with quickly." },

				{ Cue = "/VO/Persephone_0205",
					Portrait = "Portrait_Persephone_Queen_Apprehensive_01",
					PreLineAnim = "PersephoneUnderworld_Vulnerable",
					Text = "{#DialogueItalicFormat}Oh{#PreviousFormat}, Hades... I think the shades around here have been through enough as it is! I don't mind them. After all the time I spent on my own, having some company has been a welcome change." },

				{ Cue = "/VO/Hades_0935", Portrait = "Portrait_Hades_Default_01", Speaker = "NPC_Hades_01",
					Text = "You are certain all your needs have been provided for? Because if there is anything that you require..." },

				{ Cue = "/VO/Persephone_0206",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "{#DialogueItalicFormat}Require{#PreviousFormat}? Look at this place, Hades. Look at {#DialogueItalicFormat}me{#PreviousFormat}! I have everything! And so do you. Try not to worry quite so much. And be patient with your son. That's all I ask." },

				{ Cue = "/VO/Hades_0936", Portrait = "Portrait_Hades_Averted_01", Speaker = "NPC_Hades_01",
					PreLineFunctionName = "HadesArmsCross",
					Text = "Persephone, I cannot promise you such things, they're... not my strengths. Besides, I have a reputation to uphold! {#DialogueItalicFormat}God of the dead{#PreviousFormat}! If the wretches do not fear me, or {#DialogueItalicFormat}you {#PreviousFormat}for that matter... what, then?" },

				{ Cue = "/VO/Persephone_0207",
					PreLineFunctionName = "HadesArmsUnCross",
					Portrait = "Portrait_Persephone_Queen_Apprehensive_01",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					PostLineAnim = "Hades_HouseWritingLoop_IdleLoop_Start", PostLineAnimTarget = 370006,
					Text = "I'm not going to tell you how to balance your work, your marriage, and your parental responsibilities. I don't think anybody on Olympus or below knows quite how best to manage that. So long as we do our best." },
			},
			PersephoneWithHades04 =
			{
				Partner = "NPC_Hades_01",
				RequiredTextLines = { "PersephoneWithHades03", "PersephoneGift04", "HadesGift04" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneWithHades01", "PersephoneWithHades02", "PersephoneWithHades03" }, Count = 5 },
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "PersephoneGoingAway" },
				StatusAnimation = false,
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				-- In Garden
				RequiredFalseTextLinesThisRun = { "NyxAboutDusa04" },
				TeleportWithId = 370006,
				TeleportToId = 555689,
				TeleportOffsetX = 195,
				TeleportOffsetY = 55,
				AngleTowardTargetId = 391553,
				InteractDistance = 850,
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines =
				{
					PreLineWait = 0.75,
					ObjectType = "NPC_Hades_Story_01",
					-- Indeed.
					{ Cue = "/VO/Hades_1174" },
				},
				{ Cue = "/VO/Hades_0938",
					PreLineThreadedFunctionName = "StopHadesInGardenStatusSpeaking",
					Portrait = "Portrait_Hades_Averted_01", Speaker = "NPC_Hades_01",
					PreLineWait = 0.35,
					PreLineFunctionName = "HadesArmsCross",
					Text = "...Sometimes, I... still cannot believe that you are here. Returned to us. Your radiance, in all this darkness... you must miss seeing the coast." },

				{ Cue = "/VO/Persephone_0208",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "The coast was nice enough, Hades. But nothing is ever perfect, right? No matter how hard you try. Still... I think we get quite close around here, every once in a while." },

				{ Cue = "/VO/Hades_0939", Portrait = "Portrait_Hades_Averted_01", Speaker = "NPC_Hades_01",
					PreLineFunctionName = "HadesArmsUnCross",
					Text = "You have taken much less time adjusting to this place than when first you joined us. Part of my surprise. I, myself, was forced to be here. I never thought anyone would come from the surface here by choice." },

				{ Cue = "/VO/Persephone_0209",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "The heart can make us do the strangest things, can't it?" },
			},

			PersephoneWithNyx01 =
			{
				Partner = "NPC_Nyx_01",
				RequiredTextLines = { "NyxWithPersephone01" },
				MinRunsSinceAnyTextLines = { TextLines = { "NyxWithPersephone01" }, Count = 3 },
				MaxRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 50 },
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "PersephoneGoingAway" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportToId = 370010,
				TeleportOffsetX = -160,
				TeleportOffsetY = -20,
				AngleTowardTargetId = 370010,
				IgnoreStartTextLinesAngleTowardHero = true,
				InteractDistance = 325,
				EndVoiceLines =
				{
					PreLineWait = 0.6,
					ObjectType = "NPC_Nyx_01",
					-- Mmm.
					{ Cue = "/VO/Nyx_0255" },
				},
				{ Cue = "/VO/Persephone_0186", PreLineWait = 0.35,
					Text = "...So if there's anything that I can do for you, Nyx, you need but ask, all right? I plan to pull my weight." },
				{ Cue = "/VO/Nyx_0341", Portrait = "Portrait_Nyx_Default_01", Speaker = "NPC_Nyx_01", PreLineWait = 0.35,
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "Your services were always welcome, here. Please acclimate yourself at your own rate. I am available to answer any questions you may have at any time." },
				{ Cue = "/VO/Persephone_0187",
					PreLineAnim = "PersephoneUnderworld_Vulnerable",
					Text = "Thank you, my friend. I have to tell you... it's good to be back." },
			},
			PersephoneWithNyx02 =
			{
				Partner = "NPC_Nyx_01",
				RequiredTextLines = { "PersephoneWithNyx01", "NyxGift07", "PersephoneGift07", "OrpheusGift05", "AchillesGift06_A" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneWithNyx01" }, Count = 10 },
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "PersephoneGoingAway" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				UseableOffSource = true,
				TeleportWithId = 370010,
				TeleportToId = 556834,
				TeleportOffsetX = -150,
				TeleportOffsetY = 150,
				AngleTowardTargetId = 370010,
				InteractDistance = 325,
				IgnoreStartTextLinesAngleTowardHero = true,
				EndVoiceLines = 
				{
					PreLineWait = 0.5,
					ObjectType = "NPC_Nyx_01",
					PreLineAnim = "NyxIdleGreeting",
					-- Precisely, yes.
					{ Cue = "/VO/Nyx_0343" },
				},
				{ Cue = "/VO/Persephone_0330", Portrait = "Portrait_Persephone_Queen_Apprehensive_01", PreLineWait = 0.35,
					Text = "...I just don't know how I can ever repay you. For raising him. For {#DialogueItalicFormat}everything{#PreviousFormat}. After I left... besides old Cerberus, I think maybe I missed you most of all." },
				{ Cue = "/VO/Nyx_0342", Portrait = "Portrait_Nyx_Averted_01",
					Speaker = "NPC_Nyx_01", PreLineWait = 0.35,
					PreLineAnim = "NyxIdleGreeting", PreLineAnimTarget = 370010,
					Text = "I can remember feeling similar emotions at the time. However, it is quite unnecessary to give thanks to me this way. You are the Queen, safely returned where you belong. I did what anybody would have in my place." },
				{ Cue = "/VO/Persephone_0331",
					PreLineAnim = "PersephoneUnderworld_Greeting",
					Text = "Anybody would have lovingly raised my estranged son, while keeping safe my secret, eventually orchestrating an elaborate plot to reunite him with me, so that I might someday return, to have a Nectar with them in the lounge?" },
			},

			PersephoneWithCerberus01 =
			{
				Partner = "NPC_Cerberus_01",
				RequiredTextLines = { "Ending01", "PersephoneHomeMeeting01" },
				MinRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 5 },
				MaxRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 50 },
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "PersephoneGoingAway" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				TeleportToId = 370007,
				TeleportOffsetX = -325,
				TeleportOffsetY = 150,
				AngleTowardTargetId = 370007,
				IgnoreStartTextLinesAngleTowardHero = true,
				{ Cue = "/VO/Persephone_0188", PreLineWait = 0.35,
					PreLineAnim = "PersephoneUnderworld_Vulnerable",
					Text = "Oh, Cerberus! Doubtless you deserve the biggest thanks that this House still stands, don't you? Did you enjoy the treat I brought you from the surface?" },
				{ Cue = "/VO/Storyteller_0349", PreLineWait = 1.0, StartSound = "/VO/CerberusWhine_4", IsNarration = true,
					Portrait = "Portrait_Cerberus_Default_01",
					PortraitExitAnimation = "Portrait_Cerberus_Default_01_Exit",
					Text = "{#DialogueItalicFormat}The surface-treat-devouring hound of hell provides the Queen a most decidedly affirmative response." },
				{ Cue = "/VO/Persephone_0189",
					Text = "I thought you might. You wouldn't know it wasn't made of meat, I'll bet! I'll see if we can't grow you plenty more." },
			},
			PersephoneWithCerberus02 =
			{
				Partner = "NPC_Cerberus_01",
				RequiredTextLines = { "PersephoneWithCerberus01" },
				MinRunsSinceAnyTextLines = { TextLines = { "PersephoneWithCerberus01" }, Count = 15 },
				MaxRunsSinceAnyTextLines = { TextLines = { "Ending01" }, Count = 80 },
				PlayOnce = true,
				UseableOffSource = true,
				RequiredFalseFlags = { "PersephoneGoingAway" },
				UseText = "UseListenNPC",
				BlockDistanceTriggers = true,
				TeleportToId = 370007,
				TeleportOffsetX = -325,
				TeleportOffsetY = 150,
				AngleTowardTargetId = 370007,
				IgnoreStartTextLinesAngleTowardHero = true,
				{ Cue = "/VO/Persephone_0190", PreLineWait = 0.35,
					PreLineAnim = "PersephoneUnderworld_Vulnerable",
					Text = "You're such a good boy, aren't you, Cerberus? Yes you are! Guarded the place while I was out. And even let my son come visit me!" },
				{ Cue = "/VO/Storyteller_0406", PreLineWait = 1.0, StartSound = "/VO/CerberusWhine_4", IsNarration = true,
					Portrait = "Portrait_Cerberus_Default_01",
					PortraitExitAnimation = "Portrait_Cerberus_Default_01_Exit",
					Text = "{#DialogueItalicFormat}The ever-vigilant and terrifying Cerberus merely accepts the praise." },
			},

