				RequiredFalseFlags = { "PersephoneJustReturned", "PersephoneGoingAway" },
				{ Cue = "/VO/Persephone_0166",
					Text = "Keeping the realm secure as ever, right?" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
				StatusAnimation = false,
			},
			PersephoneChat06 =
			{
				UseableOffSource = true,
