				RequiredFalseFlags = { "PersephoneJustReturned", "PersephoneGoingAway" },
				{ Cue = "/VO/Persephone_0167",
					Text = "It's always nice to have a chat with you." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
				StatusAnimation = false,
			},
			PersephoneChat07 =
			{
				UseableOffSource = true,
				EndGlobalVoiceLines = "MiscEndVoiceLines_Persephone",
				RequiredFalseFlags = { "PersephoneJustReturned", "PersephoneGoingAway" },
				{ Cue = "/VO/Persephone_0168",
					Text = "I think the House is well enough in order as of late." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
				StatusAnimation = false,
			},
			PersephoneChat08 =
			{
				UseableOffSource = true,
				EndGlobalVoiceLines = "MiscEndVoiceLines_Persephone",
				RequiredFalseFlags = { "PersephoneJustReturned", "PersephoneGoingAway" },
				{ Cue = "/VO/Persephone_0169",
					Text = "I hope your last assignment turned out well." },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
				StatusAnimation = false,
			},
			PersephoneChat09 =
			{
