				RequiredFalseFlags = { "PersephoneJustReturned", "PersephoneGoingAway" },
				RequiredFalseQueuedTextLines = { "HadesAboutOlympianReunionQuest01A" },
				-- [location: garden]
				RequiredFalseTextLinesThisRun = { "NyxAboutDusa04" },
				TeleportToId = 556841,
				TeleportOffsetX = -50,
				TeleportOffsetY = 0,
				AngleTowardTargetId = 555304,
				{ Cue = "/VO/Persephone_0172",
					Text = "Hope my little cottage on the coast is holding up!" },
				OnQueuedFunctionName = "CheckDistanceTriggerThread",
				OnQueuedFunctionArgs = PresetEventArgs.PersephoneGreeting,
				StatusAnimation = false,
			},
			PersephoneChat12 =
			{
				Skip = true,
				UseableOffSource = true,
				EndGlobalVoiceLines = "MiscEndVoiceLines_Persephone",
				RequiredFalseFlags = { "PersephoneJustReturned", "PersephoneGoingAway" },
				{ Cue = "/VO/Persephone_0173",
